INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1899, 'Rooftop Sundowners at Linea / Closer to the Moon', 'A photogenic terrace perched on Lipscani 17, Linea is sunset central—golden light, skyline views, and a soundtrack cruising from nu-disco to easy house. Seating clusters around planters and a sleek bar; heaters and canopies stretch the season, but on clear nights the terrace sparkles. The crowd is mixed but style-conscious: couples shooting golden-hour selfies, small groups clinking spritzes, and birthday tables leaning into celebratory energy. It''s lively without being chaotic. Cocktails are polished and modern; reservations recommended for prime times. Perfect for a chic pregame that often becomes the main event.', 'nightlife', NULL, NULL, NULL, 120, 210, ARRAY['category:nightlife','energy:medium','mood:romantic','mood:sophisticated','mood:social','context:date','context:friends','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Lipscani', 44.4327, 26.0993, '2025-10-23T14:02:11.821Z', '2025-11-19T14:40:28.406Z', 'bucharest-rooftop-linea-closer-moon', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://booklinea.me', 'Răsărit pe Acoperiș la Linea / Closer to the Moon', 'O terasă fotogenică amplasată pe Lipscani 17, Linea este locul perfect pentru apus - lumină de aur, vedere spre skyline și o selecție muzicală care merge de la nu-disco la house ușor. Locurile sunt grupate în jurul ghivecelor și al unui bar elegant; încălzitoare și copertine prelungesc sezonul, dar în serile senine terasa strălucește. Publicul este variat, dar conștient de stil: cupluri care fac selfie-uri în lumina de aur, grupuri mici care ciocnesc spritz-uri și mese de zi de naștere care intră în energie de sărbătoare. Este animat fără a fi haotic. Cocktailurile sunt rafinate și moderne; rezervările sunt recomandate pentru orele de vârf. Perfect pentru un pregătit șic care adesea devine evenimentul principal.', ARRAY['categorie:viata_noapte','energie:mediu','stare:romantic','stare:sophisticated','stare:social','context:intalnire','context:friends','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (802, 'Cluj Tandem Paragliding at Feleacu', 'A few steps down a grassy slope and the wing fills—suddenly your feet are air. Tandem paragliding is soaring without the stress: your pilot manages takeoff, thermals, and landing while you settle into the seat and watch Cluj and the Transylvanian hills glide by. Calm air means mellow floating; lively air means playful wingovers that tickle the stomach. Either way, it''s the purest form of silent flight you can buy for an afternoon.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 60, 120, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:explorer','terrain:hill','terrain:forest','terrain:valley','equipment:provided','context:solo','context:date','context:small-group','requirement:booking-required','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','time_of_day:sunset','travel_time_band:nearby','skills:none_required','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Feleacu', 'Cluj', 46.721711, 23.620992, '2025-10-22T21:54:01.820Z', '2025-11-18T13:26:03.951Z', 'cluj-feleacu-tandem-paragliding', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.721711,23.620992', 'medium', 'mixed', 'any', 'moderate', 'https://www.aspar.ro', 'Zbor Tandem cu Parașuta la Feleacu, Cluj', 'Câțiva pași pe o pantă înerbată și aripa prinde formă - deodată picioarele îți sunt în aer. Paragliding-ul tandem înseamnă planare fără stres: pilotul tău gestionează decolarea, curenții termici și aterizarea, în timp ce tu stai comod în scaun și privești cum Cluj și dealurile transilvănene alunecă pe lângă tine. Aerul calm înseamnă plutire liniștită; aerul vioi înseamnă manevre pline de energie care îți gâdilă stomacul. Indiferent de situație, este cea mai pură formă de zbor tăcut pe care poți să o experimentezi într-o după-amiază.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:explorator','teren:hill','teren:padure','teren:valley','echipament:furnizat','context:solo','context:intalnire','context:grup_mic','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','moment_zi:sunset','timp_calatorie:aproape','abilitati:none_required','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (859, 'Bâlea Ski Mountaineering Clinic (Făgăraș)', 'Skin uphill from the cable car top station and learn efficient kick turns, avalanche companion rescue, and safe line choice on 30–35° slopes. You''ll feel your legs burn on steady climbs, then switch to downhill mode for controlled, off-piste turns in glacial bowls. A UIAGM/IFMGA guide sets pace, reads the snowpack, and drills beacon–shovel–probe routines so you leave with real mountain judgment, not just a fun ride. Expect brisk transitions, wind in your face near the saddles, and the buzz of stringing clean turns in untracked snow.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 360, 420, ARRAY['category:winter','energy:high','experience_level:intermediate','mood:adrenaline','mood:adventurous','mood:focused','mood:explorer','terrain:mountain','terrain:ridge','equipment:rental-gear','context:small-group','context:friends','requirement:guide-required','risk_level:high','weather_fit:cold_sensitive','time_of_day:daytime','time_of_day:sunrise','travel_time_band:day-trip','skills:technique','skills:endurance','skills:navigation','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Cârțișoara', 'Sibiu', 45.603, 24.617, '2025-10-22T21:59:34.455Z', '2025-11-19T14:40:28.406Z', 'cartisoara-balea-ski-mountaineering-clinic-alpine-tours', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.603,24.617', 'medium', 'mixed', 'any', 'moderate', 'https://mountainguide.ro', 'Clinică de Schi Montan la Bâlea (Făgăraș)', 'Urcă cu schiurile de la stația superioară a telecabinei și învață tehnici eficiente de schimbare a direcției, salvare în caz de avalanșă și alegerea liniei sigure pe pantele de 30-35°. Vei simți arsura în picioare în urcări constante, apoi vei trece în modul coborâre pentru viraje controlate, în afara pistei, în bazinele glaciare. Un ghid UIAGM/IFMGA stabilește ritmul, citește stratul de zăpadă și exersează rutinele de utilizare a balizei, lopetei și sondei, astfel încât să pleci cu adevărat judecată montană, nu doar cu o plimbare plăcută. Pregătește-te pentru tranziții rapide, vânt în față lângă trecători și satisfacția unor viraje curate pe zăpadă netulburată.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:intermediar','stare:adrenalina','stare:adventurous','stare:focused','stare:explorator','teren:munte','teren:ridge','echipament:inchiriere_echipament','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:ridicat','vreme:cold_sensitive','moment_zi:zi','moment_zi:sunrise','timp_calatorie:excursie_zi','abilitati:tehnica','abilitati:endurance','abilitati:navigation','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
