INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1940, 'Sunset Splash at Magic Place Aqua Park', 'When the heat slips off the concrete and Lacul Morii glows, Magic Place turns into Bucharest''s open-air swim playground. Big blue pools catch the last gold light, slides thrum with whoops, and the DJ line keeps a light, pop-leaning pulse. Families claim cabanas by day; after work, groups of friends drift between loungers, bars, and the water for an easy, social vibe. It''s less training, more unwinding: float, snack, rinse, repeat. Expect queues on scorching weekends, and a lively, mixed crowd from across the city. Staff manage capacity well, lifeguards are visible, and there''s plenty of deck space to sprawl. Ideal for a half-day of lazy laps and low-key fun.', 'social', NULL, NULL, NULL, 180, 360, ARRAY['category:social','energy:medium','mood:social','mood:party','mood:chill','context:friends','context:small-group','context:large-group','time_of_day:evening','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Crângași', 44.45631, 26.04456, '2025-10-23T14:10:25.802Z', '2025-11-18T13:35:30.673Z', 'bucharest-magic-place-sunset-splash', ARRAY[]::text[], ARRAY[]::text[], NULL, 'medium', 'mixed', 'any', 'moderate', 'https://www.facebook.com/magicplaceaquapark/', 'Stropire la Apus la Magic Place Aqua Park', 'Când căldura se ridică de pe beton și Lacul Morii strălucește, Magic Place devine locul de înjumătățit al Bucureștiului. Bazinele albastre mari prind ultima lumină aurie, toboganele vuiesc de strigăte, iar DJ-ul menține un ritm ușor, pop. Familiile ocupă cabane ziua; după program, grupuri de prieteni se plimbă între șezlonguri, baruri și apă într-o atmosferă ușoară și sociabilă.', ARRAY['categorie:social','energie:mediu','stare:social','stare:party','stare:relaxat','context:friends','context:grup_mic','context:large-group','moment_zi:seara','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1942, 'After-Work Dip at Daimon Wellness (Tineretului)', 'Set just off the park''s leafy paths, Daimon''s pool area draws a friendly neighborhood crowd for post-work swims and summer lounging. Expect a clean rectangular basin for easy laps earlier in the evening and a chill social atmosphere as the sun drops—low music, café service, and clusters of friends rotating between water, deck chairs, and chats. It''s casual and accessible, with lifeguards on deck and straightforward facilities. Warm nights carry a soft breeze off Tineretului''s trees, and the vibe stays relaxed rather than raucous. Great for a light workout followed by a terrace drink.', 'social', NULL, NULL, NULL, 90, 180, ARRAY['category:social','energy:medium','mood:social','mood:chill','context:friends','context:small-group','context:large-group','time_of_day:evening','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Tineretului', 44.413227, 26.107828, '2025-10-23T14:10:25.804Z', '2025-11-18T13:35:30.674Z', 'bucharest-daimon-wellness-dip', ARRAY[]::text[], ARRAY[]::text[], NULL, 'medium', 'mixed', 'any', 'moderate', 'https://www.daimonclub.ro/', 'Scufundare după Program la Daimon Wellness (Tineretului)', 'Situat lângă potecile verzi ale parcului, zona de bazin a lui Daimon atrage o mulțime de vecini prietenoși pentru înot după program și plajă de vară. Te poți aștepta la un bazin dreptunghiular curat pentru lungimi ușoare în prima parte a serii și o atmosferă socială relaxată pe măsură ce soarele coboară — muzică ușoară, servicii de cafenea și grupuri de prieteni care se rotesc între apă, șezlonguri și conversații.', ARRAY['categorie:social','energie:mediu','stare:social','stare:relaxat','context:friends','context:grup_mic','context:large-group','moment_zi:seara','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1937, 'Pura Vida Sky Bar (Old Town)', 'Backpacker-friendly rooftop perched above Smârdan''s bustle with beanbags, LEDs, and easygoing playlists. Sunset brings camera phones; later it''s beers, spritzes, and new friends from five countries at the next table. Come for views, stay for people-watching.', 'nightlife', NULL, NULL, NULL, 60, 120, ARRAY['category:nightlife','energy:medium','mood:social','mood:romantic','mood:party','context:friends','context:solo','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Old Town', 44.4321, 26.1005, '2025-10-23T14:07:36.440Z', '2025-11-19T14:40:28.406Z', 'bucharest-pura-vida-sky-bar-old-town', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://puravidahostels.ro', 'Pura Vida Sky Bar (Centrul Vechi)', 'Terasă pe acoperiș prietenoasă cu turiștii, situată deasupra animației de pe Smârdan, cu saci de șezut, LED-uri și playlist-uri relaxate. La apusul soarelui, telefoanele foto încep să scapere; mai târziu sunt beri, spritz-uri și prieteni noi de la mesele vecine din cinci țări. Vino pentru priveliște, rămâi pentru observat oamenii.', ARRAY['categorie:viata_noapte','energie:mediu','stare:social','stare:romantic','stare:party','context:friends','context:solo','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$']::text[]) ON CONFLICT DO NOTHING;
