INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1945, 'Campus Swim at Carol Davila Pool (Cotroceni)', 'In the academic enclave near Eroilor, the medical university''s pool hosts structured classes and adult sessions in a no-nonsense, athletic setting. The vibe is studious: coaches timing sets, steady whistles, and lane discipline that keeps splits honest. Lighting is bright, water temp geared to training, and lanes rotate between lessons and free swim—check schedules. Expect students, club swimmers, and neighborhood regulars; facilities are functional more than fancy, but the value is excellent.', 'social', NULL, NULL, NULL, 60, 120, ARRAY['category:social','energy:medium','mood:energetic','mood:social','context:solo','context:friends','context:small-group','time_of_day:evening','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Cotroceni / Eroilor', 44.4353197, 26.0705778, '2025-10-23T14:10:25.807Z', '2025-11-18T13:36:43.773Z', 'bucharest-carol-davila-campus-swim', ARRAY[]::text[], ARRAY[]::text[], NULL, 'medium', 'mixed', 'any', 'moderate', 'https://umfcd.ro/studenti/servicii-sociale/activitati-de-recreere/', 'Înot pe Campus la Piscina Carol Davila (Cotroceni)', 'În incinta academică de lângă Eroilor, piscina universității medicale găzduiește clase structurate și sesiuni pentru adulți într-un cadru sportiv fără compromisuri. Atmosfera este una studiată: antrenori care cronometrează seriile, fluiere constante și disciplină pe culoare care mențin corectitudinea. Iluminatul este puternic, temperatura apei este adaptată antrenamentului, iar culoarul se rotește între lecții și înot liber - verifică programul. Te poți aștepta să întâlnești studenți, înotători de club și rezidenți locali; facilitățile sunt funcționale mai degrabă decât luxoase, dar raportul calitate-preț este excelent.', ARRAY['categorie:social','energie:mediu','stare:energetic','stare:social','context:solo','context:friends','context:grup_mic','moment_zi:seara','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1948, 'Grand Lanes at World Class The Grand (JW Marriott)', 'Inside the JW Marriott complex, The Grand''s World Class pool delivers a polished, hotel-club experience: warm lighting, calm water, and the hush of soft-touch spa design. Lanes suit steady, recreational sets; afterwards, slide into sauna/steam or the weight floor. The crowd mixes hotel guests in robes with members in training mode, but the tone remains refined and unhurried. Service is professional, towels are ready, and you can wrap with a tea in the lobby''s marble glow. A classy central-west option.', 'social', NULL, NULL, NULL, 60, 150, ARRAY['category:social','energy:low','mood:chill','mood:sophisticated','mood:luxe','context:date','context:solo','context:friends','time_of_day:evening','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Cotroceni / 13 Septembrie', 44.4254, 26.0773, '2025-10-23T14:10:25.809Z', '2025-11-18T13:36:43.785Z', 'bucharest-jwmarriott-grand-lanes', ARRAY[]::text[], ARRAY[]::text[], NULL, 'medium', 'mixed', 'any', 'moderate', 'https://www.worldclass.ro/club/worldclass-bucuresti-at-the-grand/', 'Culoare Grandioase la World Class The Grand (JW Marriott)', 'În interiorul complexului JW Marriott, piscina World Class The Grand oferă o experiență de club-hotel rafinată: lumină caldă, apă liniștită și liniștea designului spa cu atingere ușoară. Culoarul se potrivește pentru serii regulate de înot recreativ; afterwards, poți aluneca în saună/hammam sau la zona de greutăți. Publicul amestecă turiști în halate cu membrii în mod de antrenament, dar tonul rămâne rafinat și negrăbit. Serviciul este profesionist, prosoapele sunt gata, iar tu poți încheia cu un ceai în strălucirea holului de marmură. O opțiune elegantă în zona centrală-vest.', ARRAY['categorie:social','energie:scazut','stare:relaxat','stare:sophisticated','stare:luxe','context:intalnire','context:solo','context:friends','moment_zi:seara','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1946, 'Affordable Lengths at Olimpia (Iancului)', 'A staple of Sector 2, Olimpia''s covered pool is about straightforward access to water time—no frills, steady lane turnover, and friendly desk staff. The crowd blends families, adult learners, and budget-minded lap swimmers. Evenings see a gentle buzz as lessons wrap and lanes free up. Locker rooms are simple and serviceable; bring your own lock and cap. It''s a neighborhood classic for routine fitness without the fee shock.', 'social', NULL, NULL, NULL, 60, 120, ARRAY['category:social','energy:medium','mood:social','mood:chill','context:solo','context:friends','context:small-group','time_of_day:evening','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Iancului / Muncii', 44.442738, 26.148557, '2025-10-23T14:10:25.807Z', '2025-11-19T14:40:28.406Z', 'bucharest-olimpia-iancului-lengths', ARRAY[]::text[], ARRAY[]::text[], NULL, 'medium', 'mixed', 'any', 'moderate', 'https://inot-sport.ro', 'Lungimi Accesibile la Olimpia (Iancului)', 'Un punct fix în Sectorul 2, piscina acoperită Olimpia se concentrează pe accesul direct la înot - fără artificii, schimbare constantă de culoare și personal amabil la recepție. Publicul este un amestec de familii, adulți care înață și înotători care fac lungimi cu buget limitat. Serile capătă o atmosferă ușoară pe măsură ce lecțiile se încheie și culoarul devine liber. Vestiare sunt simple și funcționale; adu-ți propriul lacăt și șapcă. Este un clasic al cartierului pentru fitness de rutină, fără șocul prețurilor.', ARRAY['categorie:social','energie:mediu','stare:social','stare:relaxat','context:solo','context:friends','context:grup_mic','moment_zi:seara','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1735, 'Control Club Electronic Night', 'Bass thumps through two rooms while Bucharest''s underground scene dances shoulder-to-shoulder. You move from the garden bar to a dark floor where international and local DJs build long, hypnotic sets. It''s casual, sweaty, social—come to lose track of time, discover new sounds, and leave hoarse from cheering when the lights come up.', 'nightlife', ARRAY['all']::text[], 'indoor', 'high', 180, 360, ARRAY['category:nightlife','energy:high','experience_level:beginner','mood:social','mood:adrenaline','mood:adventurous','terrain:urban','equipment:none','context:friends','context:group','context:solo','requirement:booking-optional','risk_level:low','weather_fit:all_weather','time_of_day:evening','time_of_day:night','travel_time_band:in-city','skills:none_required','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'București', 44.4356, 26.097, '2025-10-22T23:31:56.833Z', '2025-11-19T14:40:28.406Z', 'bucharest-control-club-electronic-night', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.4356,26.0970', 'large', 'mixed', 'any', 'moderate', 'https://www.control-club.ro', 'Seară Electronică Control Club', 'Bassul răzbate prin două camere în timp ce scena underground București dansează umăr lângă umăr. Te muți de la bar în grădină la un dans întunecat unde DJ internaționali și locali construiesc seturi lungi și hipnotice. E casual, transpirat, social - vino să pierzi noțiunea timpului, să descoperi sunete noi și să pleci răgușit de la scandat când se aprind luminile.', ARRAY['categorie:viata_noapte','energie:ridicat','nivel_experienta:incepator','stare:social','stare:adrenalina','stare:adventurous','teren:urban','echipament:none','context:friends','context:grup','context:solo','cerinta:booking-optional','nivel_risc:scazut','vreme:orice_vreme','moment_zi:seara','moment_zi:noapte','timp_calatorie:in_oras','abilitati:none_required','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1952, 'Scârț Loc Lejer Living-Room Lounge', 'Part living-room, part time capsule, this beloved house-turned-hangout wraps you in retro couches, kitschy lamps, and the adjoining Museum of the Communist Consumer. The soundtrack is warm—soul, funk, old Romanian pop at talk-friendly volume—and the bar pours simple cocktails, local craft beers, and herbal teas until late. Board games slide across the coffee tables; conversations spark between artists, students, and curious travelers. There''s no rush culture here: you settle in, sip slowly, and watch the room glow soft as evening deepens. Perfect for dates that start shy and end giggly, or for decompressing after a high-energy night elsewhere. It''s a safe, inclusive crowd; staff are sweet and prices kind. Expect full tables on weekends and a wholesome, creative Timișoara vibe all week long.', 'social', NULL, NULL, NULL, 120, 210, ARRAY['category:social','energy:low','mood:chill','mood:social','mood:romantic','context:date','context:small-group','context:solo','time_of_day:evening','time_of_day:night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Timișoara', 'Elisabetin', 45.743234, 21.224252, '2025-10-23T14:13:34.674Z', '2025-11-18T13:36:43.789Z', 'timisoara-scart-loc-lejer-lounge', ARRAY[]::text[], ARRAY[]::text[], NULL, 'medium', 'mixed', 'any', 'moderate', 'https://www.facebook.com/scartloclejer/', 'Scârț Loc Lejer - Salonul de Living', 'Parte living, parte capsulă temporală, acest loc drag inimii transformat din casă te înfășoară în canapele retro, lămpi kitsch și Muzeul Consumatorului Comunist alăturat. Fundalul sonor este cald - soul, funk, pop românesc vechi la un volum prietenos conversației - iar barul servește cocktailuri simple, beri artizanale locale și ceaiuri cu plante până târziu. Jocuri de societate alunecă pe mesele de cafea; conversațiile prind viață între artiști, studenți și călători curioși. Nu există cultură a grabei: te stabilești, bei încet și privești cum camera strălucește pe măsură ce seara se adâncește. Perfect pentru întâlniri care încep timid și se termină chicotind, sau pentru a te relaxa după o noapte cu energie înaltă. Este un public sigur și incluziv; personalul este drăguț, iar prețurile sunt blânde. Aștează-te la mese pline în weekend și la o atmosferă creativă și plăcută în Timișoara pe tot parcursul săptămânii.', ARRAY['categorie:social','energie:scazut','stare:relaxat','stare:social','stare:romantic','context:intalnire','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','pret:$']::text[]) ON CONFLICT DO NOTHING;
