INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (17, 'Timisoara Club Night', 'Dress sharp(ish) and dive into Timișoara''s sleek club scene for chart, electronic and hip-hop nights. Bottle service available but not required; most venues run late with guest DJs most Fridays and Saturdays.', 'nightlife', ARRAY['spring','summer','fall','winter','year-round']::text[], 'indoor', 'high', 180, 300, ARRAY['category:nightlife','context:friends','context:group','cost_band:$$','energy:high','equipment:none','experience_level:beginner','indoor_outdoor:indoor','mood:adrenaline','mood:social','requirement:booking-optional','risk_level:low','seasonality:all','skills:none_required','subtype:club','subtype:dj','terrain:urban','time_of_day:evening','time_of_day:night','travel_time_band:in-city','weather_fit:all_weather']::text[], NULL, ARRAY[]::text[], 'Timișoara', 'Timiș', 45.7489, 21.2087, '2025-10-20T20:49:36.001Z', '2025-11-18T15:21:55.642Z', 'timisoara-club-night', ARRAY['dj','club']::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.7489,21.2087', 'large', 'mixed', 'any', 'moderate', 'https://retezat.ro/', 'Seară de Club în Timișoara', 'Îmbrăcați-vă elegant și intrați în scena de club modernă a Timișoarei pentru seri de muzică la zi, electronică și hip-hop. Serviciu la masă disponibil, dar nu obligatoriu; cele mai multe locații merg târziu, cu DJ invitați în majoritatea vinerilor și sâmbetelor.', ARRAY['categorie:viata_noapte','context:friends','context:grup','pret:$$','energie:ridicat','echipament:none','nivel_experienta:incepator','interior_exterior:interior','stare:adrenalina','stare:social','cerinta:booking-optional','nivel_risc:scazut','sezonalitat