INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1955, 'Lokal Oradea Rooftop Sundowners', 'High above Oradea''s ornate facades, Lokal''s roof deck catches the last gold of sunset and a breeze off the Crișul Repede. The bar team leans bright and spritzy—seasonal highballs, citrusy Collins, fresh-herb garnishes—while a DJ teases nu-disco and deep house at a conversational thump. Seating mixes banquettes and skyline-facing rail stools; heaters and blankets stretch the season well into autumn. Guests skew well-dressed but relaxed: locals on date night, travelers orbiting the nearby Black Eagle arcade, small groups celebrating. The vibe nudges from chill to lively come 22:00, and weekends can feel downright celebratory. Expect waitlists in midsummer; elevate the look (smart casual) and book if you can. Perfect as a glide path into a bigger night or as a soft-landing finale with lights twinkling below.', 'nightlife', NULL, NULL, NULL, 120, 210, ARRAY['category:nightlife','energy:medium','mood:sophisticated','mood:romantic','mood:luxe','mood:social','context:date','context:small-group','context:friends','time_of_day:evening','time_of_day:night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Oradea', 'Centru', 47.056243, 21.929569, '2025-10-23T14:13:34.675Z', '2025-11-18T13:38:07.929Z', 'oradea-lokal-rooftop-sundowners', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://www.norbucharest.ro', 'Lokal Oradea - Apusuri pe Terasă', 'Deasupra fațadelor ornate ale Oradei, terasa Lokal prinde ultimele raze aurii ale apusului și o briză de pe Crișul Repede. Echipa de bar este strălucitoare și proaspătă - cocktailuri sezoniere, Collins-uri citrice, garnisiri din plante proaspete - în timp ce un DJ mixează nu-disco și deep house într-un ritm conversațional. Spațiul de ședere combină canapele și scaune cu vedere la orizont; încălzitoare și pături prelungesc sezonul bine în toamnă. Oaspeții sunt bine îmbrăcați, dar relaxați: localnici în seară de întâlnire, călători care gravitează în jurul arcade-ului Vulturul Negru, grupuri mici care sărbătoresc. Atmosfera trece de la calm la animat pe la ora 22:00, iar weekendurile pot fi cu adevărat sărbătorești. Așteptați liste de așteptare în mijlocul verii; ridicați ținuta (smart casual) și rezervați dacă puteți. Perfect ca o pregătire pentru o noapte mai intensă sau ca un final liniștit cu luminile sclipind dedesubt.', ARRAY['categorie:viata_noapte','energie:mediu','stare:sophisticated','stare:romantic','stare:luxe','stare:social','context:intalnire','context:grup_mic','context:friends','moment_zi:seara','moment_zi:noapte','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1957, 'Underground The Pub: Iași Rock Cellar', 'Beneath the city center, this low-ceilinged basement thrums with guitars and grunge nostalgia. Posters peel artfully from brick; the bar slings cold lagers and no-nonsense shots, and the stage hosts everything from local rockers to tribute nights that pack the room. It''s sweaty, friendly, and gloriously unpretentious—students and nine-to-fivers elbow-to-elbow, yelling choruses and swapping set-list predictions. Sound is punchy for the size; lights are moody reds and blues; the dance floor dissolves into a mass of head-nods when the drummer drops the fill. A dependable after-exam blow-off or weekend catharsis. Wear sneakers, budget for a small cover, and expect to leave with a raspy voice and two new friends.', 'nightlife', NULL, NULL, NULL, 150, 300, ARRAY['category:nightlife','energy:high','mood:party','mood:underground','mood:rebellious','context:friends','context:small-group','context:solo','time_of_day:evening','time_of_day:night','time_of_day:late-night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Iași', 'Centru', 47.163177, 27.581203, '2025-10-23T14:13:34.677Z', '2025-11-18T13:38:07.946Z', 'iasi-underground-the-pub-rock', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://www.facebook.com/UndergroundIasi/', 'Underground The Pub - Pivnița Rock Iași', 'Sub centrul urbei, această pivniță cu tavanul jos vibrează de chitări și nostalgie grunge. Posterele atârnă artistic de zidărie; barul servește lager rece și shots fără pretenții, iar scena găzduiește totul - de la formații locale la seri de tribut care umplu încăperea. E transpirație, prietenie și glorios de nepretențios - studenți și angajați de la 9 la 5 umăr la umăr, strigând refrenuri și schimbând predicții de playlist. Sunetul este puternic pentru spațiul mic; luminile sunt roșii și albastre; pista de dans se topește într-o masă de dați din cap când toboșarul lansează o secvență. Un loc sigur pentru destinderea de după examene sau catharsis de weekend. Purtați adidași, bugetați pentru o taxă mică și așteptați-vă să plecați cu vocea răgușită și doi prieteni noi.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:underground','stare:rebellious','context:friends','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','moment_zi:late-night','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1959, 'EGO Mamaia Beach Club Nights', 'When the coast heats up, EGO is a midnight carnival—open-air terraces, lasers slicing the sea mist, and a crowd that arrives perfumed and camera-ready. The soundtrack is big-room house, Romanian chart crossovers, and summer guest DJs; bottle service glows under LED ice buckets while dancers toss confetti from the balcony. Expect a high-energy, dress-to-impress scene—Bucharest weekender crews, VIP tables, and holiday-mode travelers. Drinks run pricey, but service is swift; security is visible and the production is slick. Come late (after 23:30), book if you want a table, and bring light layers for the sea breeze. On peak weekends, sunrise departures are common and cab queues long—plan your exit. If you''re chasing the Romanian Riviera''s maximum wattage, this is your bullseye.', 'nightlife', NULL, NULL, NULL, 180, 360, ARRAY['category:nightlife','energy:high','mood:party','mood:luxe','mood:energetic','context:friends','context:large-group','context:small-group','time_of_day:night','time_of_day:late-night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Constanța (Mamaia)', 'Mamaia', 44.233786, 28.625567, '2025-10-23T14:13:34.678Z', '2025-11-18T13:38:07.949Z', 'mamaia-ego-beach-club-nights', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://baracca.ro', 'EGO Mamaia - Nopți de Club pe Plajă', 'Când zona de coastă se încinge, EGO este un carnaval de miezul nopții - terase în aer liber, lasere care taie ceața mării, și o mulțime sosită parfumată și pregătită pentru poze. Fundalul muzical este house de big room, hituri românești și DJ invitați de vară; serviciul de sticle strălucește sub găleți de gheață LED, iar dansatorii aruncă confetti de pe balcon. Așteptați-vă la o scenă de energie înaltă, unde toți sunt îmbrăcați să impresioneze - echipe de weekend din București, mese VIP și călători în mod de vacanță. Băuturile sunt scumpe, dar serviciul este rapid; securitatea este vizibilă, iar producția este profesionistă. Veniți târziu (după 23:30), rezervați dacă doriți o masă și luați câteva straturi ușoare pentru briza mării. În weekendurile de vârf, plecările la răsărit sunt obișnuite, iar cozile de taxiuri sunt lungi - planificați-vă ieșirea. Dacă urmăriți maximul de intensitate al Riverei Românești, aceasta este ținta.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:luxe','stare:energetic','context:friends','context:large-group','context:grup_mic','moment_zi:noapte','moment_zi:late-night','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1119, 'Woodworking Basics at NOD Makerspace', 'Get a safe, guided intro to table saws, planers and sanders, then build a small object (shelf, tray) from raw stock. You''ll read grain, square edges, and learn clamps are your best friend. Expect a calm, focused flow with tangible results—and the quiet pride of something sturdy you built with your own hands.', 'creative', ARRAY['all']::text[], 'indoor', 'medium', 180, 240, ARRAY['category:creative','energy:medium','experience_level:beginner','mood:creative','mood:focused','mood:explorer','terrain:urban','equipment:provided','context:solo','context:small-group','requirement:booking-required','requirement:lesson-recommended','risk_level:moderate','weather_fit:all_weather','time_of_day:daytime','travel_time_band:in-city','skills:technique','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'București', 44.4202, 26.1159, '2025-10-22T22:16:28.418Z', '2025-11-19T14:40:28.406Z', 'bucharest-woodworking-intro-nod', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.4202,26.1159', 'small', 'locals', 'small-group', 'moderate', 'https://nodmakerspace.ro', 'Noțiuni de Tâmplărie la NOD Makerspace', 'Primiți o introducere sigură și ghidată în ferăstraie de masă, rindele și șlefuitoare, apoi construiți un mic obiect (raft, tavă) din material brut. Veți citi textura lemnului, veți îndrepta marginile și veți învăța că menghina vă este cel mai bun prieten. Așteptați-vă la o cursivitate calmă și concentrată, cu rezultate tangibile - și mândria liniștită a ceva solid pe care l-ați construit cu propriile mâini.', ARRAY['categorie:creativ','energie: