INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1971, 'Fratelli Iași – Luxe Mixed Music Night', 'Palas-side glamour with a Romanian-meets-international playlist: commercial dance, pop edits, hip-hop interludes, and occasional live features. The room''s lighting is architectural—beams sketch across the ceiling, making even the back bar feel in the show. It''s a dress-up crowd (smart casual into sharp), where birthdays and reunions collide with conference after-parties. Drinks lean premium with competent classics, and staff keep tables humming. If you want a capital-style VIP night in Moldavia, this is the move.', 'nightlife', NULL, NULL, NULL, 240, 420, ARRAY['category:nightlife','energy:high','mood:luxe','mood:party','mood:social','context:date','context:friends','context:small-group','context:large-group','time_of_day:night','time_of_day:late-night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Iași', 'Palas / Morilor', 47.16, 27.595, '2025-10-23T14:16:35.298Z', '2025-11-18T13:39:10.202Z', 'romania-iasi-fratelli', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://fabrica-club.ro', 'Fratelli Iași – Seară de Muzică Mixtă de Lux', 'Lux în stil Palas cu un playlist româno-internațional: dans comercial, prelucrări pop, intermezze hip-hop și ocazional caracteristici live. Luminile încăperii sunt arhitecturale—raze care schițează tavanul, făcând chiar și bara din spate să pară parte din spectacol. Este un public care se îmbracă elegant (casual selectiv spre impecabil), unde zilele de naștere și reuniunile se intersectează cu after-party-uri de conferință. Băuturile sunt premium cu cocktail-uri clasice bine executate, iar personalul menține mesele animate. Dacă vrei o seară VIP de stil de capitală în Moldova, aceasta este mișcarea.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:luxe','stare:party','stare:social','context:intalnire','context:friends','context:grup_mic','context:large-group','moment_zi:noapte','moment_zi:late-night','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1973, 'Olympic Lanes at Complex Sportiv Otopeni', 'Just north of Bucharest, Otopeni''s Olympic complex delivers serious lap swimming in a clean, well-maintained 50-meter basin. Eight lanes with electronic timing, bright overhead lighting, and a no-nonsense training atmosphere attract club swimmers, triathletes, and fitness enthusiasts. Expect regimented lane etiquette, cool water temps ideal for sustained effort, and a crowd that respects the pace. Changing facilities are functional, showers are hot, and the staff keeps things running smoothly. Peak hours (18:00–20:30) see full lanes; arrive slightly earlier or later for clearer water. Day passes available; perfect for structured interval work or long endurance sets.', 'fitness', ARRAY['year-round']::text[], 'indoor', 'high', 60, 120, ARRAY['category:fitness','energy:high','mood:focused','context:solo','context:friends','time_of_day:evening','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Otopeni', 'Ilfov', 44.5486, 26.085, '2025-11-13T09:38:30.227Z', '2025-11-19T14:40:28.406Z', 'otopeni-olympic-swimming', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://complexnatatieotopeni.ro', 'Bazine Olimpice la Complexul Sportiv Otopeni', 'Chiar la nord de București, complexul olimpic din Otopeni oferă înot serios într-un bazin curat și întreținut de 50 de metri. Opt culoare cu cronometraj electronic, iluminare puternică și o atmosferă de antrenament fără compromisuri atrag înotători de club, triatleți și entuziaști de fitness. Așteptați-vă la o etichetă strictă a culoarelor, temperaturi ale apei reci ideale pentru efort susținut și o mulțime care respectă ritmul. Facilitățile de schimbare sunt funcționale, dușurile sunt calde, iar personalul ține totul sub control. Orele de vârf (18:00–20:30) au toate culoarele ocupate; sosiți puțin mai devreme sau mai târziu pentru apă mai liberă. Bilete zilnice disponibile; perfect pentru interval de lucru structurat sau seturi de rezistență lungi.', ARRAY['categorie:fitness','energie:ridicat','stare:focused','context:solo','context:friends','moment_zi:seara','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1974, 'Training Session at Bazinul Olimpic Dinamo', 'Dinamo''s legendary 50-meter Olympic pool sits in the heart of Bucharest''s sports district, offering a professional training environment for serious swimmers. Eight crisp lanes, electronic timing boards, and attentive lifeguards maintain a focused atmosphere. The gallery above hums with club energy while the water stays cool and clear. Expect a mixed crowd of competitive swimmers, triathletes, and after-work lap enthusiasts—serious but welcoming. Fluorescent lighting keeps visibility high, and the old-school Bucharest sport culture means practical changing rooms and efficient service. Lane discipline is strong, making it ideal for interval training or long sets.', 'fitness', ARRAY['year-round']::text[], 'indoor', 'high', 60, 120, ARRAY['category:fitness','energy:high','mood:focused','context:solo','context:friends','time_of_day:evening','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'București', 'Floreasca', 44.4551, 26.1024, '2025-11-13T09:38:30.234Z', '2025-11-19T14:40:28.406Z', 'bucharest-dinamo-olympic', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://www.csdinamo.eu', 'Sesiune de Antrenament la Bazinul Olimpic Dinamo', 'Bazinul olimpic de 50 de metri al lui Dinamo, situat în inima cartierului sportiv bucureștean, oferă un mediu de antrenament profesionist pentru înotătorii serioși. Opt culoare curate, panouri electronice de cronometrare și salvamari atenți mențin o atmosferă concentrată. Galeria de deasupra vibrează cu energia clubului, în timp ce apa rămâne răcoroasă și limpede. Așteptați-vă la o mulțime mixtă de înotători competitivi, triatleți și entuziaști ai înotului de after-work—serioși, dar primitori. Iluminarea fluorescentă menține vizibilitatea înaltă, iar cultura sportivă bucureșteană veche înseamnă vestiare practice și servicii eficiente. Disciplina culoarelor este strictă, ceea ce îl face ideal pentru antrenamente de interval sau seturi lungi.', ARRAY['categorie:fitness','energie:ridicat','stare:focused','context:solo','context:friends','moment_zi:seara','pret:$$']::text[]) ON CONFLICT DO NOTHING;
