INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1914, 'Foraged Cocktails at FIX Me a Drink', 'Bucharest''s cult cocktail address lives in a mid-century lobby bar, dimly lit and heavy on botanicals. Drinks lean Nordic-meets-Carpathian: spruce tips, sea buckthorn, pine cones, and hyper-seasonal syrups underpin elegant, low-waste recipes served in beautiful glassware. The room is hushed without being stiff; bartenders guide you gently into the interesting stuff. The crowd is design-savvy—chefs, creatives, and travelers who plan itineraries around bars. Expect a seat-only experience at peak hours and a playlist that keeps conversation centered. It''s a masterclass in Romanian terroir, poured.', 'nightlife', NULL, NULL, NULL, 90, 150, ARRAY['category:nightlife','energy:low','mood:sophisticated','mood:romantic','mood:chill','context:date','context:small-group','context:solo','time_of_day:evening','time_of_day:night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Obor & Romana', 44.4429, 26.0968, '2025-10-23T14:05:26.506Z', '2025-11-19T14:40:28.406Z', 'bucharest-fix-me-a-drink-foraged-cocktails', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://fixmad.com', 'Cocktailuri Culese la FIX Me a Drink', 'Adresa de cult a cocktailurilor din București locuiește într-un bar de lobby mid-century, slab luminat și bogat în botanice. Băuturile merg spre un stil nordic-carpatic: vârfuri de brad, cătină, conuri de brad și siropuri sezoniere stau la baza unor rețete elegante, cu pierderi minime, servite în sticlărie frumoasă. Camera este liniștită fără a fi rigidă; barmanii te ghidează blând spre lucruri interesante. Publicul este orientat spre design - bucătari, creativi și călători care planifică itinerarii în jurul barurilor. Te poți aștepta la o experiență doar cu loc pe scaun în orele de vârf și la un playlist care menține conversația centrată. E o lecție de măiestrie a terenurilor românești, turnată.', ARRAY['categorie:viata_noapte','energie:scazut','stare:sophisticated','stare:romantic','stare:relaxat','context:intalnire','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1939, 'Evening Lanes at Dinamo Olympic Pool', 'Under the echo of bleachers and the soft slap of water on tile, Dinamo''s 50-meter Olympic basin feels purpose-built for mileage. Eight crisp lanes, electronic timing boards, and regimented lane etiquette keep things moving, while the gallery above hums with club energy. Expect a mixed crowd of club swimmers, triathletes, and after-work lap fans—serious but friendly, more performance than preening. Fluorescents run bright, water is on the cool side, and the lifeguards are attentive. It''s old-school Bucharest sport culture: practical changing rooms, hot showers, and a schedule that favors training blocks over lounging. Grab a lane, set your intervals, and you''ll forget you''re in the middle of the city until you surface for air and hear the muffled whistle. Day passes available; peak lanes fill from 18:00 to 20:30, so come slightly before or after for clearer water.', 'social', NULL, NULL, NULL, 60, 120, ARRAY['category:social','energy:medium','mood:energetic','mood:social','context:solo','context:friends','context:small-group','time_of_day:evening','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Floreasca', 44.4550833, 26.1024444, '2025-10-23T14:10:25.799Z', '2025-11-18T13:35:30.672Z', 'bucharest-dinamo-evening-lanes', ARRAY[]::text[], ARRAY[]::text[], NULL, 'medium', 'mixed', 'any', 'moderate', 'https://www.csdinamo.eu/baze-sportive/baze-sportive/agrement-inot', 'Ore de Seară la Bazinul Olimpic Dinamo', 'Sub ecoul tribunelor și șoapta apei pe plăci, bazinul olimpic de 50 de metri de la Dinamo pare construit special pentru kilometri. Opt culoare curate, panouri de cronometrare electronică și o regulă strictă de bandă mențin lucrurile în mișcare, în timp ce galeria de deasupra vibrează cu energia clubului. Te poți aștepta la o mulțime amestecată de înotători de club, triatleți și pasionați de înot după program — serioși dar prietenoși, mai mult performanță decât pozat.', ARRAY['categorie:social','energie:mediu','stare:energetic','stare:social','context:solo','context:friends','context:grup_mic','moment_zi:seara','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1954, 'Moszkva Oradea Alt Scene', 'In an Art Nouveau courtyard just off the historic center, Moszkva is Oradea''s HQ for indie bands, poetry slams, and stubbornly alternative DJ nights. Inside the rambling rooms you''ll find murals, vintage chairs, and a small stage where everything from shoegaze to jazz trios squeezes in. The bar pours straightforward beers and long drinks; prices are friendly and the crowd is too—students, artists, and locals who''ll pull you into a conversation about the next festival. Friday ramps up with post-punk, techno, or Balkan mashups that spill energy onto the patio in summer. It''s the kind of place where you come for a gig and stay because the community feels like family. Dress however, bring cash for the door on concert nights, and expect your TBR list (and your heart) to grow.', 'nightlife', NULL, NULL, NULL, 150, 300, ARRAY['category:nightlife','energy:medium','mood:underground','mood:rebellious','mood:social','context:friends','context:small-group','context:solo','time_of_day:evening','time_of_day:night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Oradea', 'Centru', 47.05524, 21.93046, '2025-10-23T14:13:34.675Z', '2025-11-18T13:36:43.791Z', 'oradea-moszkva-alt-scene', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://mojomusicclub.com', 'Scena Alternativă Moszkva din Oradea', 'Într-o curte Art Nouveau chiar lângă centrul istoric, Moszkva este cartierul general al Oradei pentru trupe indie, recitaluri de poezie și seri de DJ-i alternativ îndărătnici. În camerele întinse vei găsi murale, scaune vintage și o scenă mică unde totul, de la shoegaze la trio-uri de jazz, se strecoară. Barul servește beri simple și cocktailuri lungi; prețurile sunt blânde, la fel și publicul - studenți, artiști și localnici care te vor trage într-o conversație despre următorul festival. Vinerea crește în intensitate cu post-punk, techno sau mashup-uri balcanice care varsă energie pe terasă vara. Este genul de loc unde ajungi pentru un concert și rămâi pentru că atmosfera seamănă cu una de familie. Îmbracă-te cum te simți, adu numerar pentru intrare în serile de concert și pregătește-te să ți se mărească lista de citit (și inima).', ARRAY['categorie:viata_noapte','energie:mediu','stare:underground','stare:rebellious','stare:social','context:friends','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1964, 'Premium Party at NOA – Nest of Angels', 'NOA is Cluj''s flagship big-room experience—an elevated, glossy box of light and sound where commercial house, EDM and pop remixes surge through a packed main floor. The booth is high and centered; strobes chase across a sea of hands while CO₂ bursts kiss the drop. It''s an upscale scene with Transylvanian edge: students, creators, visiting artists and tech teams out celebrating wins. Expect VIP islands and quick bottle service, plus a bar that moves efficiently on classics. The programming swings from resident crowd-pleasers to costumed theme nights; when the room hits its stride around 1am, it feels like a small festival indoors. Security is courteous but firm (ID, dress code). If you want Euro-club theatrics without losing the friendly Cluj vibe, this is the sweet spot—slick, social, and designed for a sunrise exit.', 'nightlife', NULL, NULL, NULL, 240, 420, ARRAY['category:nightlife','energy:high','mood:party','mood:energetic','mood:luxe','mood:social','context:date','context:friends','context:small-group','context:large-group','time_of_day:night','time_of_day:late-night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Cluj-Napoca', 'Republicii / Zorilor', 46.75678, 23.595085, '2025-10-23T14:16:35.292Z', '2025-11-18T13:38:07.955Z', 'romania-cluj-noa-nest-of-angels', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://fratelligroup.ro/biutiful-by-the-lake/', 'Petrecere Premium la NOA - Cuibul Îngerilor', 'NOA este experiența de big room a Clujului - o cutie ridicată și strălucitoare de lumină și sunet unde house comercial, EDM și remixuri pop năvălesc pe pista principală plină. Cabina DJ-ului este înaltă și centrată; stroboscoapele urmăresc o mare de mâini în timp ce erupțiile de CO₂ sărută momentul culminant. Este o scenă de nivel înalt cu un ușor accent transilvănean: studenți, creatori, artiști în vizită și echipe de tehnologie care sărbătoresc victorii. Așteptați-vă la insule VIP și serviciu rapid de sticle, plus un bar care lucrează eficient cu bauturi clasice. Programarea variază de la favoriți rezidenți la seri tematice cu costume; atunci când sala ajunge la punctul culminant pe la ora 1 dimineața, pare un mic festival în interior. Securitatea este curtenitoare, dar fermă (ID, cod vestimentar). Dacă doriți efecte de club european fără a pierde atmosfera prietenească a Clujului, aceasta este zona perfectă - lucios, social și conceput pentru o ieșire la răsărit.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:energetic','stare:luxe','stare:social','context:intalnire','context:friends','context:grup_mic','context:large-group','moment_zi:noapte','moment_zi:late-night','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1953, 'Viniloteca: Wine & Wax Nights', 'A hybrid record shop and bar, Viniloteca smells like cork and cardboard sleeves. Wooden crates brim with jazz, post-punk, afrobeat and local releases; staff spin mellow sets while guests swirl Romanian Fetească and international pours. By 21:00, the room is a low murmur of clinking stems and needle crackle; conversations arc from album nerdery to weekend plans. Expect tasting flights, occasional listening parties, and pop-up DJ takeovers that lift the energy without drowning talk. Seating is close, lighting soft, and the clientele mixed—vinyl heads, designers, curious tourists, couples on their second date. If beer''s your lane, a small but sharp craft list keeps hop fans happy. It''s intimate, unpretentious, and very Timișoara—culture first, buzz second.', 'nightlife', NULL, NULL, NULL, 120, 210, ARRAY['category:nightlife','energy:medium','mood:sophisticated','mood:chill','mood:social','context:date','context:small-group','context:friends','context:solo','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Timișoara', 'Central', 45.74803, 21.21976, '2025-10-23T14:13:34.674Z', '2025-11-18T13:36:43.790Z', 'timisoara-viniloteca-wine-and-wax', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://viniloteca.shop/', 'Viniloteca: Seri de Vin și Ceară', 'Un hibrid între magazin de discuri și bar, Viniloteca miroase a dop și învelișuri de carton. Lăzi de lemn pline cu jazz, post-punk, afrobeat și lansări locale; personalul mixează seturi line în timp ce oaspeții rotesc vinuri românești Fetească și selecții internaționale. Pe la ora 21:00, camera devine un murmur scăzut de ciocniri și pârâit de ac; conversațiile trec de la pasiunea pentru discuri la planurile de weekend. Te poți aștepta la degustări, ocazionale petreceri de ascultare și preluări DJ spontane care ridică energia fără a înăbuși discuțiile. Spațiul este intim, iluminatul este scăzut, iar publicul este variat - pasionați de vinil, designeri, turiști curioși, cupluri la a doua întâlnire. Dacă preferi berea, o selecție mică, dar ascuțită de beri artizanale îi mulțumește pe pasionații de hamei. Este intim, nepretențios și foarte specific Timișoarei - cultura pe primul loc, abia apoi distracția.', ARRAY['categorie:viata_noapte','energie:mediu','stare:sophisticated','stare:relaxat','stare:social','context:intalnire','context:grup_mic','context:friends','context:solo','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1962, 'Yolka Skybar Sundowners', 'Perched above Unirii Square''s cathedral spires, Yolka is a leafy terrace with panoramic Cluj views and a cocktail list that loves bright acids and garden-fresh infusions. Early evening is golden hour with light downtempo; later, the energy lifts as crowd chatter stacks against a gentle house groove. Expect a stylish but friendly mix—locals, visiting creatives, couples taking in the skyline. Heaters, blankets, and greenhouse corners keep the season long. It''s a polished service experience without stiffness, and the ideal bridge between dinner and a louder club. Dress smart-casual, book at weekends, and time your arrival for the moment the square''s lights flick on below.', 'nightlife', NULL, NULL, NULL, 90, 180, ARRAY['category:nightlife','energy:medium','mood:romantic','mood:sophisticated','mood:luxe','mood:chill','context:date','context:small-group','context:friends','time_of_day:evening','time_of_day:night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Cluj-Napoca', 'Centru', 46.769337, 23.589726, '2025-10-23T14:13:34.679Z', '2025-11-18T13:38:07.953Z', 'cluj-yolka-skybar-sundowners', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://yolka-cluj.com/', 'Yolka Skybar - Apusuri', 'Situat deasupra turnurilor catedralei din Piața Unirii, Yolka este o terasă verde cu vedere panoramică asupra Clujului și o carte de cocktailuri care adoră acizi strălucitori și infuzii proaspete din grădină. Primele ore ale serii sunt de lumină de aur cu downtempo ușor; mai târziu, energia crește pe măsură ce zgomotul mulțimii se adună cu un groove house blând. Așteptați-vă la un amestec stilat, dar prietenos - localnici, creatori în vizită, cupluri care admiră orizontul. Încălzitoare, pături și colțuri de seră prelungesc sezonul. Este o experiență de serviciu rafinată, fără rigiditate, și un pod ideal între cină și un club mai zgomotos. Îmbrăcați-vă smart-casual, rezervați la weekend și sincronizați-vă sosirea cu momentul în care luminile pieței se aprind dedesubt.', ARRAY['categorie:viata_noapte','energie:mediu','stare:romantic','stare:sophisticated','stare:luxe','stare:relaxat','context:intalnire','context:grup_mic','context:friends','moment_zi:seara','moment_zi:noapte','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1972, 'Euphoria Music Hall – Big-Room Live & Party', 'A cavernous Cluj institution that flips between concert hall and giant party, Euphoria packs students and alumni into a space built for sing-alongs and big drops. Live nights deliver Romanian rock staples and touring acts; weekends tilt to retro themes and high-energy club sets under an LED ceiling and a tight, chest-thumping low end. Bars are long and quick, security is visible but chill, and the mood is ''best friend''s graduation, but every night.'' It''s the city''s easiest way to end up shouting the chorus with strangers.', 'nightlife', NULL, NULL, NULL, 180, 360, ARRAY['category:nightlife','energy:high','mood:party','mood:social','mood:energetic','context:friends','context:large-group','context:small-group','time_of_day:evening','time_of_day:night','time_of_day:late-night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Cluj-Napoca', 'Bulgaria / 1 Decembrie 1918', 46.774, 23.585, '2025-10-23T14:16:35.299Z', '2025-11-18T13:39:10.204Z', 'romania-cluj-euphoria-live', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://www.clubguesthouse.ro/', 'Euphoria Music Hall – Concert Live și Petrecere mare', 'O instituție cavernică din Cluj care oscilează între sală de concert și petrecere uriașă, Euphoria înghesuie studenți și absolvenți într-un spațiu conceput pentru cântări în cor și drop-uri mari. Serile live aduc clasicii rock românesc și trupe în turneu; weekendurile virează spre teme retro și seturi de club de energie înaltă sub un tavan LED și un bass puternic. Barurile sunt lungi și rapide, securitatea este vizibilă dar relaxată, iar atmosfera este ''petrecerea de absolvire a celui mai bun prieten, în fiecare seară.'' Este cea mai ușoară modalitate din Cluj de a ajunge să strigi refrenul cu necunoscuți.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:social','stare:energetic','context:friends','context:large-group','context:grup_mic','moment_zi:seara','moment_zi:noapte','moment_zi:late-night','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1970, 'Fratelli Beach & Club – House by the Sea', 'An all-day, all-night compound—private beach, restaurant, and club—Fratelli Mamaia is the classic Black Sea luxury night out. Early evening is rosé on daybeds as the DJ warms with deep house; by midnight the dancefloor swells and the lighting rig takes the lid off, with live vocal features and sax flourishes popping over the drop. The crowd is glossy coastal: locals, Bucharest weekender crews, and visitors in linen, all moving between terrace breezes and the main room''s thunder. It''s a ''book ahead, dress well, stay late'' kind of night.', 'nightlife', NULL, NULL, NULL, 240, 420, ARRAY['category:nightlife','energy:high','mood:luxe','mood:party','mood:romantic','context:date','context:friends','context:small-group','context:large-group','time_of_day:night','time_of_day:late-night','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Mamaia (Constanța)', 'Mamaia Nord', 44.264, 28.633, '2025-10-23T14:16:35.297Z', '2025-11-18T13:39:10.200Z', 'romania-mamaia-fratelli-beach', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://www.facebook.com/ClubA.Bucuresti/', 'Fratelli Beach & Club – House lângă Mare', 'Un complex complet—plajă privată, restaurant și club—Fratelli Mamaia este ieșirea clasică de lux de pe Marea Neagră. Seara târzie începe cu rosé pe șezlonguri în timp ce DJ-ul încălzește cu deep house; la miezul nopții pista de dans se umple, iar instalația de lumini explodează, cu voci live și inflexiuni de saxofon peste drop. Publicul este strălucitor și coastal: localnici, echipe de weekend din București și vizitatori în in, toți mișcându-se între terasă și camera principală. Este genul de seară pentru care trebuie să rezervi în avans, să te îmbraci elegant și să rămâi târziu.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:luxe','stare:party','stare:romantic','context:intalnire','context:friends','context:grup_mic','context:large-group','moment_zi:noapte','moment_zi:late-night','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
