INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1920, 'LINEA / Closer to the Moon Rooftop', 'A staple rooftop right by Old Town, LINEA is all about twinkle-light skyline, relaxed couches, and crowd-pleasing mixes. Expect summer queues, winter domes, and a soundtrack that leans feel-good rather than niche. Ideal for groups, out-of-towners, and anyone who wants the ''Bucharest from above'' moment without a dress code.', 'nightlife', NULL, NULL, NULL, 90, 150, ARRAY['category:nightlife','energy:medium','mood:social','mood:romantic','mood:chill','context:friends','context:large-group','context:date','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Old Town', 44.4327, 26.0993, '2025-10-23T14:06:27.229Z', '2025-11-19T14:40:28.406Z', 'bucharest-linea-closer-to-the-moon-rooftop', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://booklinea.me', 'LINEA / Mai Aproape de Lună - Terasă', 'O terasă clasică lângă Centrul Vechi, LINEA se concentrează pe cerul înstelat, canapele relaxante și mixuri care plac tuturor. Așteptați-vă la cozi vara, cupole iarna și o selecție muzicală care preferă melodiile known și plăcute. Ideal pentru grupuri, turiști și oricine dorește momentul ''București de sus'' fără cod vestimentar strict.', ARRAY['categorie:viata_noapte','energie:mediu','stare:social','stare:romantic','stare:relaxat','context:friends','context:large-group','context:intalnire','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
