INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (34, 'Christmas Market Bucharest (Constitution Square)', 'Big city atmosphere with stages, food alleys and artisan stalls in front of the Parliament. Visit evenings for light shows and live music.', 'seasonal', ARRAY['winter']::text[], 'both', 'low', 60, 180, ARRAY['category:seasonal','context:date','context:family','context:friends','cost_band:$','energy:chill','equipment:none','experience_level:beginner','indoor_outdoor:either','mood:cozy','mood:romantic','mood:social','requirement:none','risk_level:low','seasonality:winter','skills:none_required','subtype:christmas_market','subtype:concerts','terrain:urban','time_of_day:evening','time_of_day:night','travel_time_band:in-city','weather_fit:cold_friendly']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'București', 44.4263, 26.088, '2025-10-20T20:49:36.085Z', '2025-11-18T15:22:43.671Z', 'bucharest-christmas-market', ARRAY['christmas_market','concerts']::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.4263,26.088', 'medium', 'mixed', 'any', 'moderate', 'https://www.bucharestchristmasmarket.ro/', 'Târgul de Crăciun București (Piața Constituției)', 'Atmosferă de mare city cu scene, alei cu mâncare și standuri de artizanat în fața Parlamentului. Vizitați seara pentru show-uri de lumini și muzică live.', ARRAY['categorie:sezonier','context:intalnire','context:familie','context:friends','pret:$','energie:relaxat','echipament:none','nivel_experienta:incepator','interior_exterior:oricare','stare:confortabil','stare:romantic','stare:social','cerinta:none','nivel_risc:scazut','sezonalitate:iarna','abilitati:none_required','subtype:christmas_market','sub