INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1956, 'Music Pub Sibiu Rock Nights', 'Down a vaulted brick cellar just off Piața Mare, Music Pub packs in guitar covers, rock karaoke, and weekend bands belting 80s anthems. It''s candle jars on tables, scuffed floors, and a stage so close you can feel the kick drum. The mix is students, craft-beer dads, and travelers ducking the tourist trail for something rowdier. Drinks are simple and cheap; service is fast and friendly; the set lists jump genres as the night deepens. If you''ve got pipes, open mic welcomes you; if not, the crowd will sing along for you. Expect a warm, zero-attitude room where strangers clink glasses and exchange festival tips. This is Sibiu when it lets its hair down—unbuttoned, melodic, and joyfully loud.', 'nightlife', NULL, NULL, NULL, 150, 300, ARRAY['category:nightlife','energy:high','mood:party','mood:social','mood:energetic','context:friends','context:small-group','context:solo','time_of_day:evening','time_of_day:night','time_of_day:late-night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Sibiu', 'Centru', 45.79882, 24.1516, '2025-10-23T14:13:34.676Z', '2025-11-18T13:38:07.945Z', 'sibiu-music-pub-rock-nights', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://hotelalpin.ro', 'Music Pub Sibiu - Nopți de Rock', 'Într-o pivniță cu boltă din cărămidă, chiar lângă Piața Mare, Music Pub adună interpretări de chitară, karaoke rock și formații de weekend care cântă imnuri din ''80. Borcane cu lumânări pe mese, podele uzate și o scenă atât de aproape încât poți simți lovitura tobei. Publicul este format din studenți, tați de bere craft și călători care ocolesc traseul turistic pentru ceva mai energic. Băuturile sunt simple și ieftine; serviciul este rapid și prietenos; listele de melodii trec prin genuri pe măsură ce noaptea înaintează. Dacă ai voce, open mic-ul te primește; dacă nu, publicul va cânta pentru tine. Așteptați-vă la o atmosferă caldă, fără pretenții, unde străinii ciocnesc pahare și schimbă sfaturi despre festivaluri. Aceasta este Sibiul atunci când se dezlănțuie - dezinhibat, melodic și vesel de zgomotos.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:social','stare:energetic','context:friends','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','moment_zi:late-night','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1960, 'Expirat Vama Veche Beach Sessions', 'Vama''s bohemian legend lives here: a wooden beach stage, salty air, and a barefoot dance floor where indie bands, alt rock, and electronic live sets roll straight into sunrise. Early evening is lazy—burgers, cold beer, and seagulls—then the PA roars and the deck becomes a small festival, complete with sand underfoot and a friendly, anything-goes crowd. You''ll meet students on road trips, Bucharest creatives, and travelers who planned one night and stayed three. Expect creative lineups, fair prices, and a vibe that''s inclusive and unpolished in all the right ways. Dress code? Swimsuit and hoodie. Bring cash, hydrate, and stash sandals—you''ll be dancing on sand. On long weekends, expect packed nights and cathartic sunrise applause when the final track lands.', 'nightlife', NULL, NULL, NULL, 180, 360, ARRAY['category:nightlife','energy:high','mood:party','mood:underground','mood:social','mood:rebellious','context:friends','context:small-group','context:solo','context:large-group','time_of_day:evening','time_of_day:night','time_of_day:late-night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Vama Veche', 'Beachfront', 43.75, 28.57, '2025-10-23T14:13:34.678Z', '2025-11-18T13:38:07.950Z', 'vama-veche-expirat-beach-sessions', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://www.facebook.com/TerasaBarakaBucuresti', 'Expirat Vama Veche - Sesiuni pe Plajă', 'Legenda bohemă a Vămii trăiește aici: o scenă de plajă din lemn, aer sărat și o pistă de dans fără pantofi unde formații indie, rock alternativ și concerte electronice merg direct către răsărit. Seara timpurie este leneșă - hamburgeri, bere rece și pescăruși - apoi sistemul audio răcnește și puntea devine un mic festival, cu nisip sub picioare și o mulțime prietenească, fără limite. Veți întâlni studenți în călătorii, creativi din București și călători care au plănuit o singură noapte și au rămas trei. Așteptați-vă la lineup-uri creative, prețuri corecte și o atmosferă incluzivă și neșlefuită în toate sensurile bune. Cod vestimentar? Costum de baie și hanorac. Aduceți numerar, hidratați-vă și ascundeți sandalele - veți dansa pe nisip. În weekendurile lungi, așteptați-vă la nopți aglomerate și aplauze catartice la răsărit când ultima piesă se termină.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:underground','stare:social','stare:rebellious','context:friends','context:grup_mic','context:solo','context:large-group','moment_zi:seara','moment_zi:noapte','moment_zi:late-night','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1965, 'High-End Night at Epic Society', 'Timisoara''s most polished dance room pairs crisp LEDs, clean low-end and a crowd that dresses to impress. Epic Society leans glossy: commercial dance, festival-flavored house, and a late set that often flips into hip-hop edits. The space is compact enough to feel electric but planned for flow—fast bars, raised VIP platforms, and a booth that keeps the floor glued to the drops. It''s the place to celebrate—birthdays, promotions, ''we shipped''—with bottle sparklers tracing arcs in the haze. The door is selective but friendly if you look the part; inside, the vibe is international-meets-Banat, with students and founders mixing with weekenders from Serbia and Hungary. Go for a full table if you want the parade; roll in after midnight if you just want the peak-time rush.', 'nightlife', NULL, NULL, NULL, 240, 420, ARRAY['category:nightlife','energy:high','mood:party','mood:energetic','mood:luxe','context:date','context:friends','context:small-group','context:large-group','time_of_day:night','time_of_day:late-night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Timișoara', 'Cetate / Str. Miresei', 45.76823, 21.22248, '2025-10-23T14:16:35.293Z', '2025-11-18T13:39:10.182Z', 'romania-timisoara-epic-society', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://bricksrestaurant.ro', 'Seara de Lux la Epic Society', 'Camera de dans cea mai rafinată din Timișoara combină LED-uri cristaline, bass-uri curate și o mulțime îmbrăcată impecabil. Epic Society mizează pe strălucire: dans comercial, house cu aromă de festival și un set târziu care adesea trece în prelucrări hip-hop. Spațiul este compact pentru a crea o atmosferă electrică, dar conceput pentru a permite mișcarea—bare rapide, platforme VIP înălțate și un stand care ține pista lipită de drop-uri. Este locul perfect pentru a sărbători—zile de naștere, promovări, ''am livrat''—cu artificii pentru sticle trasând arcade în ceață. Intrarea este selectivă dar prietenoasă dacă arăți bine; în interior, atmosfera este internațional-întâlnește-Banat, cu studenți și fondatori amestecându-se cu turiști din Serbia și Ungaria. Rezervă o masă întreagă dacă vrei paradă; intră după miezul nopții dacă vrei doar energia momentului de vârf.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:energetic','stare:luxe','context:intalnire','context:friends','context:grup_mic','context:large-group','moment_zi:noapte','moment_zi:late-night','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1742, 'Corks Cozy Winebar Social Tasting', 'Slide onto a high stool and explore Romanian and European labels with staff who love to recommend flights. Conversations spark easily at the bar; you leave a little more wine-literate and with a couple of new friends.', 'nightlife', ARRAY['all']::text[], 'indoor', 'medium', 60, 120, ARRAY['category:nightlife','energy:medium','experience_level:beginner','mood:social','mood:relaxed','mood:romantic','terrain:urban','equipment:none','context:date','context:solo','context:small-group','requirement:booking-optional','risk_level:low','weather_fit:all_weather','time_of_day:evening','time_of_day:night','travel_time_band:in-city','skills:none_required','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'București', 44.4328, 26.1022, '2025-10-22T23:31:56.846Z', '2025-11-19T14:40:28.406Z', 'bucharest-corks-wine-social', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.4328,26.1022', 'large', 'mixed', 'any', 'moderate', 'https://corks.ro', 'Degustare Socială la Corks Wine Bar', 'Urcă-te pe un scaun înalt și explorează etichete românești și europene cu un personal pasionat de recomandări de vinuri. Conversațiile pornesc ușor la bar; pleci puțin mai cunoscător în vinuri și cu câțiva prieteni noi.', ARRAY['categorie:viata_noapte','energie:mediu','nivel_experienta:incepator','stare:social','stare:relaxat','stare:romantic','teren:urban','echipament:none','context:intalnire','context:solo','context:grup_mic','cerinta:booking-optional','nivel_risc:scazut','vreme:orice_vreme','moment_zi:seara','moment_zi:noapte','timp_calatorie:in_oras','abilitati:none_required','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1938, 'Halftime Sports Pub (Gabroveni)', 'Old Town''s reliable match-night HQ—multiple screens, pub grub, and beers that keep coming. Atmosphere swings from banter to full-on roar during derbies. Staff handle big groups well; bookings recommended for major fixtures.', 'nightlife', NULL, NULL, NULL, 120, 210, ARRAY['category:nightlife','energy:medium','mood:party','mood:social','mood:chill','context:friends','context:large-group','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Lipscani', 44.4323, 26.1022, '2025-10-23T14:07:36.441Z', '2025-11-19T14:40:28.406Z', 'bucharest-halftime-sports-pub', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://www.facebook.com/halftimebucharest', 'Halftime Sports Pub (Gabroveni)', 'Cartierul general de nădejde pentru serile de meci din Centrul Vechi — mai multe ecrane, mâncare de pub și beri care nu se opresc. Atmosfera oscilează de la conversații ușoare la urale în timpul meciurilor. Personalul gestionează bine grupurile mari; rezervări recomandate pentru meciuri importante.', ARRAY['categorie:viata_noapte','energie:mediu','stare:party','stare:social','stare:relaxat','context:friends','context:large-group','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1966, 'Metal Nights at Rockstadt', 'Rockstadt is Romania''s metal heartland: a black-walled, poster-plastered den where riffs rule and the beer is cold. The room hums with anticipation before doors—patches, leather, and band tees—then detonates when the first kick drum hits. Sightlines are strong, sound is loud but dialed, and the bar serves fast between sets. Expect a calendar heavy on heavy: death, black, thrash, stoner, plus alt-rock and punk weeknights, with touring acts and cult local heroes. It''s inclusive, unpretentious, and cathartic—mosh at the edge, headbang from the rail, or talk gear at the merch table. If your luxury is authenticity and a stage five meters away, this is premium.', 'nightlife', NULL, NULL, NULL, 180, 300, ARRAY['category:nightlife','energy:high','mood:rebellious','mood:underground','mood:party','mood:social','context:friends','context:small-group','context:solo','time_of_day:evening','time_of_day:night','time_of_day:late-night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Brașov', 'Centru (Nicolae Titulescu)', 45.657974, 25.601198, '2025-10-23T14:16:35.294Z', '2025-11-18T13:39:10.194Z', 'romania-brasov-rockstadt-metal', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://bucharest2night.com', 'Nopți de Metal la Rockstadt', 'Rockstadt este inima metalului românesc: o încăpere cu pereți negri, tapetată cu postere, unde riffurile conduc și berea e rece. Camera vibrează de anticipare înainte de deschidere—patch-uri, piele și tricouri de formații—apoi explodează când primul kick drum lovește. Vizibilitatea este bună, sunetul este puternic dar controlat, iar barul servește rapid între concerte. Programul este încărcat cu muzică grea: death, black, thrash, stoner, plus rock alternativ și punk în serile de mijloc săptămânii, cu trupe în turneu și eroi locali de cult. Este incluziv, nepretențios și cathartic—mosh la margine, headbang de la balustradă sau discută echipamente la standul de merchandise. Dacă luxul tău este autenticitatea și o scenă la cinci metri distanță, acesta este locul premium.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:rebellious','stare:underground','stare:party','stare:social','context:friends','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','moment_zi:late-night','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1967, 'Live Jazz at Jazz & Blues Club', 'A veteran room with candlelit tables and a stage barely an arm''s length away, Jazz & Blues Club is all about sound, nuance and late conversations. Horns glow under amber spots, cymbals shimmer, and the PA treats vocals with warmth—perfect for standards, bebop, and bluesy detours. The crowd is mixed—students, doctors fresh off shift, couples on a third date—united by an appetite for live music. Bartenders lean classic (Manhattan, Old Fashioned) with local labels for beer and wine. On jam nights, the energy softens into community: solos passed, smiles traded, time stretching. It''s an old-school cultural anchor that still swings.', 'nightlife', NULL, NULL, NULL, 120, 210, ARRAY['category:nightlife','energy:medium','mood:sophisticated','mood:chill','mood:romantic','mood:social','context:date','context:friends','context:small-group','context:solo','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Târgu Mureș', 'Str. Sinaia', 46.55219, 24.55707, '2025-10-23T14:16:35.295Z', '2025-11-18T13:39:10.196Z', 'romania-targu-mures-jazz-blues', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://caroclub.ro', 'Jazz Live la Jazz & Blues Club', 'O sală veterană cu mese luminate de lumânări și o scenă la doar o întindere de braț, Jazz & Blues Club se concentrează pe sunet, nuanță și conversații târzii. Instrumentele de suflat strălucesc sub reflectoare ambrate, chimvalele sclipesc, iar sistemul de sunet tratează vocile cu căldură—perfect pentru standarde, bebop și devieri blues. Publicul este amestecat—studenți, medici proaspăt ieșiți din tură, cupluri la a treia întâlnire—uniți de dorința de muzică live. Barmanii preferă clasicele (Manhattan, Old Fashioned) cu etichete locale pentru bere și vin. În serile de jam, energia se transformă în comunitate: solo-uri transmise, zâmbete schimbate, timpul întinzându-se. Este un punct de reper cultural vechi-stil care încă vibrează.', ARRAY['categorie:viata_noapte','energie:mediu','stare:sophisticated','stare:relaxat','stare:romantic','stare:social','context:intalnire','context:friends','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1987, 'Coastal Tennis at Tenis Club IDU', 'Mamaia''s premier tennis facility combines Black Sea breezes with professional-grade courts. Multiple outdoor surfaces host everything from casual rallies to competitive matches, while the coastal location adds a resort atmosphere. The club attracts both local players and summer visitors, creating a social, energetic vibe. Expect quality court maintenance, equipment rental, and coaching available by appointment.', 'sports', ARRAY['spring','summer','fall']::text[], 'outdoor', 'high', 60, 120, ARRAY['category:sports','energy:high','mood:social','mood:energetic','context:friends','context:date','time_of_day:evening','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Mamaia', 'Constanța', 44.2147, 28.6139, '2025-11-13T09:38:30.250Z', '2025-11-19T14:40:28.406Z', 'mamaia-tenis-club-idu', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://idu.ro', 'Tenis Coastal la Tenis Club IDU', 'Principala facilitate de tenis din Mamaia combină briza Mării Negre cu terenuri de nivel profesionist. Suprafețe exterioare multiple găzduiesc totul de la schimburi casual la meciuri competitive, iar locația de coastă adaugă o atmosferă de resort. Clubul atrage atât jucători locali, cât și turiști de vară, creând o ambianță socială și energică. Veți beneficia de întreținere de calitate a terenurilor, închiriere de echipamente și antrenament disponibil la cerere.', ARRAY['categorie:sport','energie:ridicat','stare:social','stare:energetic','context:friends','context:intalnire','moment_zi:seara','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (2007, 'Amateur Cooking at Atelierele ILBAH', 'Atelierele ILBAH''s amateur cooking course teaches home cooks essential techniques in a supportive environment. Over multiple sessions, you''ll master knife skills, cooking methods, and recipe building. Instructors demonstrate each technique before you practice, with close attention to detail and safety. Expect a structured curriculum that builds confidence, from basic preparations to complete multi-course meals. You''ll leave with a solid foundation for everyday cooking.', 'culinary', ARRAY['year-round']::text[], 'indoor', 'medium', 150, 240, ARRAY['category:culinary','energy:medium','mood:creative','mood:focused','context:solo','context:friends','time_of_day:evening','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'București', 'București', 44.4475, 26.084, '2025-11-13T09:38:30.271Z', '2025-11-19T14:40:28.406Z', 'bucharest-ilbah-amateur-cooking', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://www.ateliereleilbah.ro', 'Gătit pentru Amatori la Atelierele ILBAH', 'Cursul de gătit pentru amatori de la Atelierele ILBAH predă tehnici esențiale pentru gătitul la domiciliu într-un mediu supportiv. Pe parcursul mai multor sesiuni, veți stăpâni mânuirea cuțitului, metode de gătit și construcția rețetelor. Instructorii demonstrează fiecare tehnică înainte ca dumneavoastră să o exersați, cu atenție la detalii și siguranță. Veți avea parte de un curriculum structurat care vă va crește încrederea, de la preparări de bază până la mese complete cu mai multe feluri.', ARRAY['categorie:culinar','energie:mediu','stare:creativ','stare:focused','cont