INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (2009, 'Gourmet Techniques at Société Gourmet', 'Société Gourmet elevates home cooking with refined techniques and sophisticated flavor combinations. Classes focus on French-inspired methods—emulsions, reductions, proper seasoning—taught in a polished teaching kitchen. Expect smaller class sizes, premium ingredients, and instructors who explain the ''why'' behind each technique. The atmosphere is focused yet approachable, perfect for cooks ready to level up their skills.', 'culinary', ARRAY['year-round']::text[], 'indoor', 'medium', 120, 180, ARRAY['category:culinary','energy:medium','mood:creative','mood:sophisticated','context:solo','context:date','context:friends','time_of_day:evening','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'București', 'București', 44.4479, 26.0968, '2025-11-13T09:38:30.273Z', '2025-11-19T14:40:28.406Z', 'bucharest-societe-gourmet', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://societegourmet.ro', 'Tehnici Gastronomice la Société Gourmet', 'Société Gourmet ridică gătitul la domiciliu prin tehnici rafinate și combinații sofisticate de arome. Clasele se concentrează pe metode de inspirație franceză - emulsii, reduceri, condimentare corectă - predate într-o bucătărie complet echipată. Veți avea parte de clase mici, ingrediente premium și instructori care explică rațiunea din spatele fiecărei tehnici. Atmosfera este concentrată, dar accesibilă, perfectă pentru bucătari gata să-și ridice abilitățile la un nivel superior.', ARRAY['categorie:culinar','energie:mediu','stare:creativ','stare:sophisticated','context:solo','context:intalnire','context:frien