INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1961, 'Tipografia Culture Bar', 'A creative hub steps from Council Square, Tipografia pours daytime specialty coffee that flips to aperitivi, craft lagers, and spritzes after dark. The space is all bookish charm—poster-lined walls, mismatched chairs, and readings or micro-gigs that warm the room—before the playlist nudges into indie, electronica, or chilled hip-hop. The crowd leans locals and in-the-know visitors escaping tourist traps; conversation is the headliner, but the small hours can get unexpectedly lively. Expect thoughtful bar staff, fair prices, and a cozy urban patio in season. Start here for a low-key pregame before climbing toward livelier bars on Republicii—or stay for the unhurried, intelligent hum Brasov does best at night.', 'nightlife', NULL, NULL, NULL, 120, 210, ARRAY['category:nightlife','energy:medium','mood:chill','mood:social','mood:sophisticated','context:date','context:small-group','context:friends','context:solo','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Brașov', 'Centru', 45.6429, 25.5908, '2025-10-23T14:13:34.679Z', '2025-11-19T14:40:28.406Z', 'brasov-tipografia-culture-bar', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://tipografia.ro', 'Tipografia - Bar Cultural', 'Un hub creativ la câțiva pași de Piața Sfatului, Tipografia servește cafea specialitate în timpul zilei care se transformă în aperitive, bere craft și spritz-uri după lăsarea serii. Spațiul este plin de farmec livresc - pereți decorați cu postere, scaune dezasortate, și lecturi sau concerte mici care încălzesc camera - înainte ca playlist-ul să alunece către indie, electronica sau hip-hop calm. Publicul este format din localnici și vizitatori avizați care scapă de capcanele turistice; conversația este cap de afiș, dar orele mici pot deveni neașteptat de animate. Așteptați-vă la personal de bar atent, prețuri corecte și o curte urbană primitoare în sezon. Începeți de aici pentru o pregătire discretă înainte de a urca către baruri mai animate de pe Republicii - sau rămâneți pentru bâzâiala inteligentă și negrăbită pe care Brașovul o face cel mai bine noaptea.', ARRAY['categorie:viata_noapte','energie:mediu','stare:relaxat','stare:social','stare:sophisticated','context:intalnire','context:grup_mic','context:friends','context:solo','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
