INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (315, 'Tandem Skydive at Clinceni (TNT Brothers)', 'Clip into a harness, step into the plane, and watch the airfield shrink to a toy set as you climb to 4,000 m. Your instructor handles the tech; you focus on the feeling. The door slides open, wind roars in, and then it''s pure rush—up to ~200 km/h of freefall where your body becomes a needle on a speedometer. At canopy, everything flips to quiet control: big turns, soft glide, and the surreal calm of floating over fields outside Bucharest. You''ll learn the sequence—briefing, gear-up, exit position, freefall body shape, canopy ride, landing—so you know exactly what''s coming. Walk away buzzing, with video proof if you want it, and that ''did-the-thing'' pride that stays for weeks.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:focused','mood:social','terrain:urban','equipment:provided','context:solo','context:friends','context:small-group','requirement:booking-required','requirement:lesson-recommended','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:nearby','skills:none_required','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Cornetu', 'Ilfov', 44.3583, 25.9306, '2025-10-22T21:28:05.309Z', '2025-11-19T14:40:28.406Z', 'clinceni-tandem-skydive-tnt-brothers', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.3583,25.9306', 'medium', 'mixed', 'any', 'moderate', 'https://www.tnt-brothers.ro', 'Salt în Tandem cu Parașuta la Clinceni (TNT Brothers)', 'Fixează-te în hamul de siguranță, urcă în avion și privește cum câmpul de aviație se micșorează precum un set de jucărie în timp ce urcați la 4.000 m. Instructorul se ocupă de tehnică, tu te concentrezi asupra senzației. Ușa alunecă, vântul urlă, și apoi vine acea cursă pură - o cădere liberă de până la ~200 km/h în care corpul tău devine un ac pe un vitezometru. La deschiderea parașutei, totul devine control liniștit: viraje mari, planare ușoară și liniștea suprareală a plutirii deasupra câmpurilor de lângă București. Vei învăța secvența - briefing, echipare, poziție de lansare, formă de corp în cădere liberă, zbor cu parașuta, aterizare - astfel încât să știi exact ce urmează. Vei pleca entuziasmat, cu dovadă video dacă dorești, și cu acea mândrie de ''am făcut-o'' care durează săptămâni întregi.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:focused','stare:social','teren:urban','echipament:furnizat','context:solo','context:friends','context:grup_mic','cerinta:rezervare_necesara','cerinta:lectie_recomandata','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:aproape','abilitati:none_required','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
