INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1901, 'Intimate Jazz Night at Green Hours', 'Bucharest''s legendary jazz café hides behind a leafy garden on Calea Victoriei. Inside, it''s all candlelit tables, compact stage, and discreet service; outside, a bohemian terrace hums when the weather''s kind. Expect modern jazz trios, singer-songwriters, and theater/alternative events. The room''s acoustics flatter warm horns and brushed drums; conversation is possible between sets. Crowd skews artsy and local with visiting jazz heads in the mix. It''s a slower, more soulful slice of the city—perfect for dates or a decompression night after heavy clubbing.', 'nightlife', NULL, NULL, NULL, 120, 180, ARRAY['category:nightlife','energy:medium','mood:sophisticated','mood:chill','mood:romantic','context:date','context:small-group','context:solo','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Obor & Romana', 44.4429, 26.0965, '2025-10-23T14:02:11.826Z', '2025-11-19T14:40:28.406Z', 'bucharest-green-hours-jazz-night', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://greenhours.ro', 'Seară de Jazz Intimă la Green Hours', 'Celebrul café de jazz al Bucureștiului se ascunde în spatele unei grădini înverzte de pe Calea Victoriei. La interior, totul este lumânări, scenă compactă și servicii discrete; în exterior, o terasă bohemă prinde viață când vremea permite. Te poți aștepta la trio-uri de jazz modern, cântăreți-compozitori și evenimente de teatru sau alternative. Acustica camerei avantajează sunetele calde de corn și tobele cu perii; conversația este posibilă între momente. Publicul este artistic și local, cu pasionați de jazz ocazional. Este o parte mai lentă și mai sufletească a orașului - perfectă pentru întâlniri sau o seară de relaxare după clubbing intens.', ARRAY['categorie:viata_noapte','energie:mediu','stare:sophisticated','stare:relaxat','stare:romantic','context:intalnire','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1905, 'Berăria H Beer Hall & Live Shows', 'One of Eastern Europe''s largest beer halls sits by Herăstrău Park—vast wooden tables, a stage built for everything from tribute bands to pop stars, and a raucous, happy energy. Expect towers of lager, hearty platters, and a mixed crowd of families early and revellers later. The sound is big but clean for the size; lighting runs concert-style on show nights. It''s not a club—it''s a beer arena where you can sing, clink, and soak up a very Bucharest kind of spectacle.', 'nightlife', NULL, NULL, NULL, 150, 240, ARRAY['category:nightlife','energy:medium','mood:social','mood:party','mood:energetic','context:large-group','context:friends','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Herăstrău Park area', 44.4725, 26.0805, '2025-10-23T14:03:00.632Z', '2025-11-19T11:21:55.327Z', 'bucharest-berariah-beerhall-live', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://berariah.ro', 'Berăria H: Berărie și Spectacole Live', 'Una dintre cele mai mari berării din Europa de Est, situată lângă Parcul Herăstrău - cu mese mari din lemn, o scenă pregătită pentru tot felul de formații tribut și vedete pop, și o energie zgomotoasă și veselă. Te poți aștepta la turnuri de lager, farfurii consistente și o mulțime diversă, de la familii la petrecăreți. Sunetul este puternic dar clar pentru dimensiunea locului; iluminatul este de stil concert în serile de spectacol. Nu e un club - e o arenă de bere unde poți cânta, ciocni pahare și savura un spectacol tipic bucureștean.', ARRAY['categorie:viata_noapte','energie:mediu','stare:social','stare:party','stare:energetic','context:large-group','context:friends','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (797, 'High Ropes & Mega Ziplines at Adrenalin Park', 'Clip into a continuous-belay system and navigate a forest canopy like an obstacle course athlete: swinging logs, cargo nets, and balance beams culminating in long, whooping zipline flights through Făget''s pines. Courses scale from warm-up to arm-pumping, so you can push your threshold progressively. You''ll finish with a confident body buzz—hands a little shaky, smile fully on. Guides fit your harness/helmet and coach you through smart technique, making it a safe first taste of vertical adventure.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:mixed','mood:adrenaline','mood:adventurous','mood:explorer','terrain:forest','terrain:hill','equipment:provided','context:friends','context:group','context:small-group','requirement:booking-required','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:balance','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Casele Micești (Cluj)', 'Cluj', 46.69603, 23.57802, '2025-10-22T21:54:01.817Z', '2025-11-19T14:40:28.406Z', 'cluj-adrenalin-park-zipline-ropes', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.69603,23.57802', 'medium', 'mixed', 'any', 'moderate', 'https://www.adrenalinpark.ro', 'Curse Înalte pe Frânghii și Zboruri cu Tiroliana la Adrenalin Park', 'Prindeți-vă în sistemul de asigurare continuă și navigați prin coronamentul unei păduri ca un atlet de obstacole: bușteni oscilânți, plase de cargo și grinzi de echilibru culminând cu zboruri lungi și entuziaste cu tiroliana prin pinii din Făget. Traseele variază de la încălzire la cele care vă vor pompa brațele, astfel încât să vă puteți depăși pragul treptat. Veți termina cu un zvâc corporal confident - mâini puțin tremurânde, zâmbet total prezent. Ghizii vă potrivesc hamul/casca și vă ghidează prin tehnici inteligente, făcând din aceasta o primă experiență sigură de aventură verticală.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:adventurous','stare:explorator','teren:padure','teren:hill','echipament:furnizat','context:friends','context:grup','context:grup_mic','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:balance','pret:$$']::text[]) ON CONFLICT DO NOTHING;
