INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1893, 'Alt & Electronic Nights at Control Club', 'A Bucharest institution for the city''s indie and electronic scenes, Control''s two rooms swing from guitar-forward live sets to late-night DJ takeovers. Sound is crisp and loud without getting harsh; lights stay moody—neon washes, minimal strobes, and a hazy edge that makes the concrete-and-brick interior feel underground. Expect a smart, mixed crowd of students, creatives, and travelers who came for music first and small talk second. Weeknights bring concerts and listening parties; weekends dial up techno/house in Sala Berlin while the front room hums with social energy at the bar. Jeans-and-sneakers are standard; cover varies by lineup and it''s cash/QR friendly at the bar. If you want a quintessential ''new Bucharest'' night—unpretentious, culture-forward, and genuinely local—this is it.', 'nightlife', NULL, NULL, NULL, 180, 360, ARRAY['category:nightlife','energy:high','mood:party','mood:energetic','mood:underground','mood:rebellious','context:friends','context:small-group','context:solo','time_of_day:evening','time_of_day:night','time_of_day:late-night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Universitate', 44.4359, 26.0966, '2025-10-23T14:01:13.703Z', '2025-11-19T11:21:55.327Z', 'bucharest-alternative-electronic-control-club', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://control-club.ro', 'Seri Alternative și Electronice la Control Club', 'O instituție bucureșteană pentru scenele indie și electronice, cele două camere ale Control Club trec de la concerte live cu chitară la preluări de DJ târzii în noapte. Sunetul este clar și puternic fără să devină deranjant; luminile rămân discrete—valuri de neon, stroboscoape minimale și o margine cețoasă care face interiorul de beton și cărămidă să pară subteran. Prezență inteligentă și diversă de studenți, creativi și călători veniți mai întâi pentru muzică și abia apoi pentru conversație. Serile de week-day aduc concerte și petreceri de ascultare; week-end-urile urcă techno/house în Sala Berlin, în timp ce camera din față vibrează cu energie socială la bar. Blugi și adidași sunt standardul; prețul biletului variază în funcție de lineup și se acceptă plată cash/QR la bar. Dacă vrei o seară bucureșteană autentică—fără pretenții, centrată pe cultură și cu adevărat locală—aceasta este.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:energetic','stare:underground','stare:rebellious','context:friends','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','moment_zi:late-night','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1910, 'Alt-Rock & Indie Nights at Expirat', 'A Bucharest institution, Expirat hums with that warehouse-meets-art-space energy: concrete, murals, and a system that hits crisp without frying your ears. Early evening starts with beers on the terrace; later, the main room swells with guitar-driven indie, alt-rock and crossover electronic bookings. The crowd is mixed—students, creatives, longtime regulars—who come for dancing, stage-diving energy near the front, and smoke-and-strobe silhouettes at the back. Expect themed parties, live sets from local heroes, and touring bands mid-week, with weekend lineups running until late. Drinks are fair-priced; door policies are relaxed; vibe is emphatically local. If you want Bucharest''s alternative heart, this is it.', 'nightlife', NULL, NULL, NULL, 180, 300, ARRAY['category:nightlife','energy:high','mood:party','mood:underground','mood:energetic','mood:social','context:friends','context:small-group','context:solo','time_of_day:evening','time_of_day:night','time_of_day:late-night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Universitate', 44.417, 26.0978, '2025-10-23T14:05:26.500Z', '2025-11-19T11:21:55.327Z', 'bucharest-expirat-alt-rock-nights', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://expirat.org', 'Seri de Rock Alternativ și Indie la Expirat', 'O instituție bucureșteană, Expirat vibrează cu energia unui spațiu industrial-artistic: beton, murale și un sistem audio care lovește clar fără să-ți appreciate urechile. Serile timpurii încep cu bere pe terasă; mai târziu, camera principală se umple de indie ghitară, rock alternativ și înregistrări electronice hibride. Publicul este divers - studenți, creativi, obișnuiți - care vin pentru dans, energie de stage diving în față și umbrele de fum și stroboscopuri la spate. Vei găsi petreceri tematice, concerte live de la formații locale și trupe care concertează în mijlocul săptămânii, cu lineup-uri de weekend care merg târziu. Băuturile sunt la prețuri corecte, politica de intrare este relaxată, atmosfera este autenticitate locală pură. Dacă vrei inima alternativă a Bucureștiului, acesta este locul.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:underground','stare:energetic','stare:social','context:friends','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','moment_zi:late-night','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1969, 'Beach Club Night at EGO Mamaia', 'When summer lands on the Black Sea, EGO turns into a white-on-sand playground of champagne, sax-over-house sets, and sunrise selfies on the pier. The sound is glossy—melodic house into commercial peaks—with roaming performers and the occasional beach fireworks. Expect designer resortwear, lots of table service, and a soundtrack that refuses to quit as the sky goes pink. It''s a scene—arrive early for seamless entry, later for that ''the night found us'' energy.', 'nightlife', NULL, NULL, NULL, 240, 420, ARRAY['category:nightlife','energy:high','mood:luxe','mood:party','mood:romantic','mood:energetic','context:date','context:friends','context:small-group','context:large-group','time_of_day:night','time_of_day:late-night','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Mamaia', 'Mamaia Nord', 44.2578, 28.6306, '2025-10-23T14:16:35.296Z', '2025-11-19T11:21:55.327Z', 'romania-mamaia-ego-club-beach', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://egoclub.ro', 'Seară la Beach Club EGO Mamaia', 'Când vara ajunge pe Marea Neagră, EGO devine un paradis alb pe nisip cu șampanie, seturi de house cu saxofon și selfie-uri la răsărit pe dig. Sunetul este strălucitor—house melodic spre vârfuri comerciale—cu performeri care se plimbă și ocazionale focuri de artificii pe plajă. Echipament de designer, mult serviciu la masă și o coloană sonoră care refuză să se oprească pe măsură ce cerul devine roz. Este o scenă—soseşte devreme pentru intrare lină, târziu pentru energia ''noaptea ne-a găsit''.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:luxe','stare:party','stare:romantic','stare:energetic','context:intalnire','context:friends','context:grup_mic','context:large-group','moment_zi:noapte','moment_zi:late-night','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1896, 'BOA (Beat of Angels) Hip-Hop & R&B Night', 'A glamorous, showy Herăstrău staple where hip-hop, R&B, and commercial hits soundtrack a fashion parade. Inside, BOA is all mirrors, LEDs, and tight table layouts—the floor gets packed around midnight as dancers, performers, and a sharply dressed crowd lift the energy. Think bottle service, sparklers, and immaculate fits; DJs keep transitions fast so the room never dips. If the techno basements are Bucharest''s soul, BOA is its sparkle: upscale, social, and unapologetically extra. Book a table if you''re in a group; expect a firm door, a cover on prime nights, and a late peak (1–3am).', 'nightlife', NULL, NULL, NULL, 240, 360, ARRAY['category:nightlife','energy:high','mood:party','mood:luxe','mood:energetic','context:friends','context:large-group','context:date','time_of_day:night','time_of_day:late-night','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Herăstrău Park area', 44.4747, 26.1104, '2025-10-23T14:01:13.713Z', '2025-11-19T11:21:55.327Z', 'bucharest-boa-herastrau-hiphop-glam', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://boa.ro', 'BOA (Beat of Angels) - Noaptea de Hip-Hop & R&B', 'Un reper glamuros și spectaculos din Herăstrău unde hip-hop-ul, R&B-ul și hiturile comerciale acompaniază un adevărat spectacol de modă. La interior, BOA este plin de oglinzi, LED-uri și aranjamente compacte de mese. Pista de dans se umple pe la miezul nopții, iar dansatori, performeri și un public elegant ridică energia. Totul înseamnă servicii VIP, focuri de artificii și ținute impecabile; DJ-ii mențin tranzițiile rapide pentru ca atmosfera să nu scadă niciodată. Dacă underground-ul techno este sufletul Bucureștiului, BOA este sclipirea sa: rafinat, social și fără nicio reținere.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:luxe','stare:energetic','context:friends','context:large-group','context:intalnire','moment_zi:noapte','moment_zi:late-night','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
