INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1918, 'City-High Cocktails at NOR Sky (SkyTower)', 'Perched atop SkyTower, NOR frames Bucharest in floor-to-ceiling glass: sunset over Barbu Văcărescu, highways threading neon, planes blinking out by Otopeni. Drinks are polished—classics, spritzes, and elegant signatures—with a kitchen that holds its own. The vibe is ''date night meets business dinner,'' morphing later into clinking glasses and photo-ops against the skyline. Smart-casual dress works; reservations strongly advised.', 'nightlife', NULL, NULL, NULL, 90, 150, ARRAY['category:nightlife','energy:medium','mood:romantic','mood:luxe','mood:sophisticated','context:date','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Floreasca', 44.474112, 26.109038, '2025-10-23T14:05:26.509Z', '2025-11-19T11:21:55.327Z', 'bucharest-nor-skytower-rooftop-cocktails', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://nor-sky.ro', 'Cocktailuri la Înălțime la NOR Sky (SkyTower)', 'Situat în vârful SkyTower, NOR oferă o vedere a Bucureștiului prin geamuri de la podea la tavan: apus peste Barbu Văcărescu, autostrăzi țesând lumini de neon, avioane clipind spre Otopeni. Băuturile sunt rafinate—clasice, spritz-uri și semnături elegante—cu o bucătărie care se ridică la standarde înalte. Atmosfera este ''seară de întâlnire întâlnită cu cină de afaceri'', transformându-se spre final în ciocniri de pahare și momente foto cu fundalul urban. Ținuta smart-casual este potrivită; rezervările sunt puternic recomandate.', ARRAY['categorie:viata_noapte','energie:mediu','stare:romantic','stare:luxe','stare:sophisticated','context:intalnire','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
