INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (217, 'Guided Mountain Biking — Postăvarul Peak', 'Climb fire roads, drop into forest singletrack, and feel the bike float over roots. A guide sets pace, picks lines, and coaches braking and cornering. You’ll finish mud-flecked, grinning, and braver—exactly what a proper trail day should feel like.', 'adventure', ARRAY['shoulder']::text[], 'outdoor', 'high', 180, 240, ARRAY['category:adventure','energy:high','experience_level:intermediate','mood:adventurous','mood:adrenaline','mood:explorer','terrain:mountain','terrain:forest','equipment:rental-gear','context:small-group','context:friends','requirement:guide-required','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:technique','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Brașov', 'Brașov', 45.588, 25.552, '2025-10-22T14:05:02.278Z', '2025-11-19T14:40:28.406Z', 'brasov-mtb-postavarul', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.588,25.552', 'medium', 'mixed', 'any', 'moderate', 'https://www.mtbtours.ro', 'Mountain Bike Îndrumat — Vârful Postăvarul', 'Urcați pe drumuri forestiere, coborâți pe poteci înguste prin pădure și simțiți cum bicicleta plutește peste rădăcini. Un ghid stabilește ritmul, alege traseul și vă antrenează la frânare și viraje. Veți termina stropit de noroi, zâmbind și mai curajos - exact cum ar trebui să fie o zi adevărată de trail.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:intermediar','stare:adventurous','stare:adrenalina','stare:explorator','teren:munte','teren:padure','echipament:inchiriere_echipament','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:tehnica','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (219, 'Via Ferrata “Casa Zmeului”', 'Clip, test, move. The cable keeps you safe while you climb ladders, traverse airy walls, and step onto rock like a pro. It feels like flying with training wheels—heart in throat, grin on face, and a guide making sure each step is confident.', 'adventure', ARRAY['shoulder']::text[], 'outdoor', 'high', 150, 210, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:explorer','terrain:mountain','terrain:river','terrain:cliff','equipment:provided','context:small-group','context:friends','requirement:guide-required','risk_level:exposed','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:balance','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Vadu Crișului', 'Bihor', 46.981, 22.514, '2025-10-22T14:05:02.279Z', '2025-11-19T14:40:28.406Z', 'bihor-via-ferrata-zmeului', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.981,22.514', 'medium', 'mixed', 'any', 'moderate', 'https://www.via-ferrata.ro/casa-zmeului/', 'Via Ferrata "Casa Zmeului"', 'Prindere, testare, mișcare. Cablul te ține în siguranță în timp ce urci scări, traversezi pereți abrupți și pășești pe stânci ca un profesionist. Parcă zbori cu roți de antrenaj - inima-ți bate, zâmbești larg, iar un ghid are grijă ca fiecare pas să fie sigur.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:explorator','teren:munte','teren:river','teren:cliff','echipament:furnizat','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc