INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1934, 'Trickshot Promenada Rooftop', 'A Floreasca favorite: rooftop lanes with skyline views over Barbu Văcărescu. Cocktails and pizzas round out the formula; it''s as photogenic as it is fun. Expect corporate groups mid-week and party crews on weekends.', 'social', NULL, NULL, NULL, 120, 180, ARRAY['category:social','energy:medium','mood:social','mood:energetic','mood:romantic','context:friends','context:large-group','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Floreasca', 44.471, 26.103, '2025-10-23T14:07:36.438Z', '2025-11-19T14:40:28.406Z', 'bucharest-trickshot-promenada-rooftop-bowling', ARRAY[]::text[], ARRAY[]::text[], NULL, 'medium', 'mixed', 'any', 'moderate', 'https://trickshot.ro', 'Trickshot Promenada Rooftop', 'Un favorit în Floreasca: piste pe acoperiș cu vedere panoramică peste Barbu Văcărescu. Cocktailuri și pizza completează formula; este la fel de fotogenic pe cât este de distractiv. Așteptați-vă la grupuri corporative la mijlocul săptămânii și echipe de petrecere în weekend.', ARRAY['categorie:social','energie:mediu','stare:social','stare:energetic','stare:romantic','context:friends','context:large-group','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (320, 'Via Ferrata Casa Zmeului (Vadu Crișului)', 'Steel cable, iron rungs, and a vertical limestone face that makes your calves whisper ''are you sure?''. After a trail approach, you clip lanyards to the lifeline and climb slabby sections into airy traverses. It''s methodical and satisfying: move one lanyard at a time, test feet, breathe, repeat. Exposure builds—then a suspended bridge spices things up before the final push. Guided trips keep the system simple and safe; you get the headspace and the view over Crișul Repede valley. Perfect if you crave real cliff feel without full-on lead climbing.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:intermediate','mood:adrenaline','mood:adventurous','mood:focused','mood:explorer','terrain:cliff','terrain:forest','terrain:valley','equipment:rental-gear','context:friends','context:small-group','requirement:guide-required','requirement:booking-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:balance','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Vadu Crișului', 'Bihor', 46.97123, 22.51355, '2025-10-22T21:28:05.316Z', '2025-11-19T14:40:28.406Z', 'bihor-vadu-crisului-via-ferrata-casa-zmeului', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.971230,22.513550', 'medium', 'mixed', 'any', 'moderate', 'https://www.via-ferrata.ro/casa-zmeului/', 'Via Ferrata Casa Zmeului (Vadu Crișului)', 'Cablu de oțel, trepte de fier și o față verticală de calcar care îți face mușchii să șoptească ''ești sigur?''. După un traseu de abordare, fixezi lanțurile de siguranță și urci secțiuni plate spre traversări aeriene. Este metodic și satisfăcător: mișcă un lanț de siguranță odată, testează picioarele, respiră, repetă. Expunerea crește - apoi un pod suspendat adaugă puțină picanterie înainte de ultima parte. Turele ghidate mențin sistemul simplu și sigur; tu primești spațiul mental și priveliștea peste Valea Crișului Repede. Perfect pentru cei care doresc senzația de stâncă fără alpinism adevărat.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:intermediar','stare:adrenalina','stare:adventurous','stare:focused','stare:explorator','teren:cliff','teren:padure','teren:valley','echipament:inchiriere_echipament','context:friends','context:grup_mic','cerinta:ghid_necesar','cerinta:rezervare_necesara','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:balance','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (321, 'Via Ferrata Hili''s Cave (Cheile Turzii)', 'Turda Gorge funnels you into a dramatic limestone amphitheatre. Clipped to the cable, you edge across ledges, climb sustained rungs, and feel that tingling ''nothing under my heels'' exposure. The route builds smartly—balancey traverses, short pulls, a final heady section—so you finish with shaky legs and a huge grin. A guide handles pacing, checks your kit, and coaches efficient clipping so your forearms last. You leave with a new respect for empty air and a phone full of canyon panoramas.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 150, 210, ARRAY['category:adventure','energy:high','experience_level:intermediate','mood:adrenaline','mood:adventurous','mood:focused','mood:explorer','terrain:cliff','terrain:valley','terrain:forest','equipment:rental-gear','context:friends','context:small-group','requirement:guide-required','requirement:booking-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:balance','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Cheia (near Turda)', 'Cluj', 46.565667, 23.676194, '2025-10-22T21:28:05.317Z', '2025-11-19T14:40:28.406Z', 'cluj-cheile-turzii-via-ferrata-hilis-cave', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.565667,23.676194', 'medium', 'mixed', 'any', 'moderate', 'https://www.via-ferrata.ro/sky-fly/', 'Via Ferrata Peștera Hili (Cheile Turzii)', 'Cheile Turzii te conduc într-un amfiteatru dramatic de calcar. Fixat de cablu, te deplasezi pe margini, urci trepte susținute și simți acea expunere care îți dă fiori - nimic sub călcâie. Traseul construiește inteligent - traversări de echilibru, ridicări scurte, o secțiune finală care îți taie respirația - astfel încât să termini cu picioare tremurânde și un zâmbet imens. Un ghid gestionează ritmul, verifică echipamentul și te antrenează pentru fixare eficientă astfel încât antebrațele să reziste. Pleci cu un nou respect pentru spațiul gol și telefonul plin de panorame ale canionului.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:intermediar','stare:adrenalina','stare:adventurous','stare:focused','stare:explorator','teren:cliff','teren:valley','teren:padure','echipament:inchiriere_echipament','context:friends','context:grup_mic','cerinta:ghid_necesar','cerinta:rezervare_necesara','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:balance','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (322, 'Downhill & Enduro Runs (Postăvaru Bike Park)', 'Load the gondola, drop in, and let gravity write the story. Postăvaru''s network mixes berms, tables, roots, and rock gardens so you work fast feet, quiet hands, and look-far vision. Laps are short enough to iterate: session a section, then try again cleaner and faster. Guides can set line choice and braking markers so you build speed safely. It''s max-focus, max-fun riding with that delicious lactic burn in your forearms and legs. Finish in Poiana with mud-striped shins and a very real sense of progress.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 240, ARRAY['category:adventure','energy:high','experience_level:mixed','mood:adrenaline','mood:adventurous','mood:focused','mood:social','terrain:mountain','terrain:forest','terrain:urban','equipment:rental-gear','context:friends','context:small-group','requirement:booking-optional','requirement:lesson-recommended','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:nearby','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Poiana Brașov', 'Brașov', 45.59671, 25.55619, '2025-10-22T21:28:05.318Z', '2025-11-19T14:40:28.406Z', 'poiana-brasov-postavarul-bikepark', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.596710,25.556190', 'medium', 'mixed', 'any', 'moderate', 'https://www.facebook.com/bikeparkpostavaruloficial/', 'Coborâri Downhill și Enduro (Postăvaru Bike Park)', 'Urcă cu telegondola, lansează-te și lasă gravitația să scrie povestea. Rețeaua Postăvaru amestecă berme, platforme, rădăcini și grădini stâncoase astfel încât să lucrezi cu picioare rapide, mâini liniștite și viziune departe. Turele sunt scurte pentru a permite iterații: lucrează o secțiune, apoi încearcă din nou mai curat și mai rapid. Ghizii pot stabili linia și punctele de frânare pentru a construi viteză în siguranță. Este ciclism cu concentrare maximă și distracție maximă, cu acea arsură delicioasă de acid lactic în antebrațe și picioare. Termină la Poiana cu tibii pătate de noroi și un sentiment foarte real de progres.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:adventurous','stare:focused','stare:social','teren:munte','teren:padure','teren:urban','echipament:inchiriere_echipament','context:friends','context:grup_mic','cerinta:booking-optional','cerinta:lectie_recomandata','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:aproape','abilitati:tehnica','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (740, 'Via Ferrata Casa Zmeului – Vadu Crișului', 'Clip your lanyards, test your weight on the first rungs, and feel that thrilling exposure rise as the wall drops away beneath your boots. The route climbs clean limestone above the Crișul Repede, with traverses that make your calves hum and vertical steps that ask for rhythm and trust. A guide manages spacing and technique—always two points clipped—so beginners can focus on breathing, movement, and the expanding views. You finish on a belvedere ledge, forearms warm, grin wide, and a new respect for airy terrain.', 'adventure', ARRAY['shoulder']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:focused','mood:explorer','terrain:cliff','terrain:river','terrain:forest','equipment:provided','context:small-group','context:friends','requirement:guide-required','requirement:booking-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:balance','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Vadu Crișului', 'Bihor', 46.977, 22.548, '2025-10-22T21:46:52.750Z', '2025-11-19T14:40:28.406Z', 'vadu-crisului-casa-zmeului-via-ferrata', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.977,22.548', 'medium', 'mixed', 'any', 'moderate', 'https://www.via-ferrata.ro/casa-zmeului/', 'Via Ferrata Casa Zmeului – Vadu Crișului', 'Prinde-ți lanțurile de siguranță, testează-ți greutatea pe primele trepte și simte acea expunere emoționantă pe măsură ce peretele coboară sub bocancii tăi. Traseul urcă pe calcar curat deasupra Crișului Repede, cu traverse care îți fac mușchii gambelor să vibreze și cu pași verticali care cer ritm și încredere. Un ghid gestionează distanțarea și tehnica - întotdeauna cu două puncte de ancorare - astfel încât începătorii să se poată concentra pe respirație, mișcare și priveliștile din ce în ce mai extinse. Termini pe o terasă de belvedere, cu antebrahele încălzite, cu un zâmbet larg și un nou respect pentru terenul aerian.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:focused','stare:explorator','teren:cliff','teren:river','teren:padure','echipament:furnizat','context:grup_mic','context:friends','cerinta:ghid_necesar','cerinta:rezervare_necesara','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:balance','abilitati:tehnica','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (744, 'Tandem Skydive – Tuzla Aerodrome (Black Sea)', 'Strapped to a pro, you edge to the door, feel the prop wash, and then the floor disappears. The first seconds are pure sensory overload—wind roaring, cheeks pulled back, a wild blend of fear and elation—before your brain recalibrates and you notice the coastline fanning out below. At canopy, silence blooms. You steer gentle turns, breathe again, and float toward the drop zone with a goofy grin you won''t shake all day. Video add-ons capture the exact moment courage beat hesitation.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:focused','mood:social','terrain:coast','terrain:urban','equipment:provided','context:solo','context:friends','context:small-group','requirement:booking-required','requirement:lesson-recommended','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:none_required','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Tuzla', 'Constanța', 43.994, 28.642, '2025-10-22T21:46:52.752Z', '2025-11-19T14:40:28.406Z', 'tuzla-skydiving-regional-air', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=43.994,28.642', 'medium', 'mixed', 'any', 'moderate', 'https://www.tnt-brothers.ro', 'Salt în Tandem cu Parașuta – Aerodromul Tuzla (Marea Neagră)', 'Legat de un profesionist, te apropii de ușă, simți vântul elicei, și apoi podeaua dispare. Primele secunde sunt o pură supraîncărcare senzorială - vântul urlând, obrajii trași înapoi, un amestec sălbatic de teamă și euforie - înainte ca creierul să se recalibreze și să observi linia de coastă desfășurându-se dedesubt. La parașută, se instalează liniștea. Ghidezi viraje line, respiri din nou și plutești către zona de aterizare cu un rânjet obraznic pe care nu-l vei mai șterge toată ziua. Înregistrările video capturează momentul exact în care curajul a învins ezitarea.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:focused','stare:social','teren:coasta','teren:urban','echipament:furnizat','context:solo','context:friends','context:grup_mic','cerinta:rezervare_necesara','cerinta:lectie_recomandata','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:none_required','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (750, 'Rock Ridge Climb – Acele Morarului (Bucegi)', 'A classic alpine day: early start from Bușteni, steady approach to the ridge, then roped movement over knife-edge towers with airy steps and easy pitches. Your guide manages the rope, protection, and belays so you can focus on footwork, three-point contact, and the exhilarating emptiness on both sides. It''s immersive and mental—calm breathing, precise movement, and trust in the system—with huge views of the Bucegi plateau. Expect tired legs, buzzing nerves, and the quiet pride that only real exposure gives.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 360, 540, ARRAY['category:adventure','energy:high','experience_level:intermediate','mood:adrenaline','mood:focused','mood:explorer','mood:adventurous','terrain:cliff','terrain:mountain','terrain:forest','equipment:bring-own','context:small-group','context:friends','requirement:guide-required','requirement:booking-required','risk_level:exposed','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:technique','skills:endurance','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Bușteni', 'Prahova', 45.409, 25.543, '2025-10-22T21:46:52.755Z', '2025-11-19T14:40:28.406Z', 'busteni-acele-morarului-climb', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.409,25.543', 'medium', 'mixed', 'any', 'moderate', 'https://zaikoadventures.com', 'Alpinism pe Creasta Rock Ridge – Acele Morarului (Bucegi)', 'O zi clasică de alpinism: plecare timpurie din Bușteni, apropiere constantă spre creastă, apoi deplasare cu frânghia peste turnuri cu margini ascuțite cu pași aerieni și lungimi ușoare. Ghidul dvs. gestionează frânghia, protecția și asigurarea astfel încât vă puteți concentra pe tehnica piciorului, contactul cu trei puncte și golul amețitor de pe ambele părți. Este imersiv și mental - respirație calmă, mișcare precisă și încredere în sistem - cu priveliști uriașe ale platoului Bucegi. Vă așteaptă picioare obosite, nervi vibrând și mândrie tăcută pe care numai adevărata expunere o oferă.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:intermediar','stare:adrenalina','stare:focused','stare:explorator','stare:adventurous','teren:cliff','teren:munte','teren:padure','echipament:bring-own','context:grup_mic','context:friends','cerinta:ghid_necesar','cerinta:rezervare_necesara','nivel_risc:exposed','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:tehnica','abilitati:endurance','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (751, 'First-Time Caving – Apuseni Mountains (Guided)', 'Helmet light on, you duck under a limestone arch and step into chill air that smells like stone and water. The guide teaches three-point movement, spacing, and mindful footwork as you squeeze through sculpted passage, descend an easy fixed rope, and hear subterranean streams echo off cathedral rooms. It''s physical but meditative: crawling, shuffling, and then standing in formations that took millennia to grow. You emerge muddy, happy, and strangely calm—like you pressed a reset button you didn''t know you had.', 'adventure', ARRAY['all']::text[], 'outdoor', 'high', 180, 240, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adventurous','mood:focused','mood:explorer','terrain:cave','terrain:forest','terrain:mountain','equipment:provided','context:small-group','context:friends','requirement:guide-required','requirement:booking-required','risk_level:moderate','weather_fit:cold_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:technique','skills:endurance','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Meziad/Apuseni', 'Bihor', 46.666, 22.466, '2025-10-22T21:46:52.756Z', '2025-11-19T14:40:28.406Z', 'apuseni-first-time-caving', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.666,22.466', 'medium', 'mixed', 'any', 'moderate', 'https://www.carpathian-travel-center.com/en/offers/caving-in-the-apuseni-mountains_57/', 'Primă Inițiere în Speologie – Munții Apuseni (Ghidat)', 'Lumina căștii pornită, vă strecurați sub o arcadă de calcar și pășiți într-un aer răcoros ce miroase a piatră și apă. Ghidul vă predă deplasarea în trei puncte, distanțarea și tehnica atentă a piciorului pe măsură ce vă strecurați prin pasaje sculptate, coborâți pe o frânghie fixă ușoară și auziți pâraie subterane răsunând în camere ca niște catedrale. Este fizic, dar mediativ: târându-vă, târșâindu-vă, apoi ridicându-vă în formațiuni care au luat milenii să se formeze. Veți ieși murdar, fericit și ciudat de calm - ca și cum ați fi apăsat un buton de resetare pe care nu știați că îl aveați.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adventurous','stare:focused','stare:explorator','teren:cave','teren:padure','teren:munte','echipament:furnizat','context:grup_mic','context:friends','cerinta:ghid_necesar','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:cold_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:tehnica','abilitati:endurance','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (794, 'Tandem Skydiving over Cluj with Skydive Transilvania', 'Clip into a tandem harness, shuffle to the aircraft door, feel the wind punch at your cheeks—then launch. Forty to sixty seconds of freefall flood your system: noise, speed, horizon flattening into pure sensation. Your instructor handles exits, stability, and deployment; you just breathe, spread your arms, and ride the drop. Canopy time slows everything down—quiet views of the Apuseni countryside as you steer gentle turns and practice flaring for landing. It''s a visceral reset and a brag-worthy milestone in one afternoon. Photos/video are optional; all safety briefings and kit are provided. Ideal for first-timers who want maximum adrenaline with minimal complexity.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:explorer','mood:focused','terrain:valley','terrain:urban','equipment:provided','context:solo','context:small-group','context:friends','requirement:booking-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','time_of_day:sunset','travel_time_band:day-trip','skills:none_required','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Luncani', 'Cluj', 46.4817, 23.9341, '2025-10-22T21:54:01.812Z', '2025-11-19T14:40:28.406Z', 'luncani-cluj-tandem-skydive-transilvania', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.4817,23.9341', 'medium', 'mixed', 'any', 'moderate', 'https://www.skydivetransilvania.ro', 'Salt în Tandem cu Parașuta deasupra Clujului cu Skydive Transilvania', 'Vă prindeți în hamul tandem, vă apropiați de ușa avionului, simțiți vântul lovind peste obraz - apoi lansarea. Patruzeci până la șaizeci de secunde de cădere liberă vă inundă sistemul: zgomot, viteză, orizont aplatizat în senzație pură. Instructorul vă gestionează ieșirea, stabilitatea și deschiderea parașutei; dvs. doar respirați, vă deschideți brațele și alunecați în cădere. Timp de parașută încetinește totul - priveliști liniștite ale țării Apuseni în timp ce virați ușor și exersați frânarea pentru aterizare. Este o resetare viscerală și o piatră de hotar demnă de povestit într-o după-amiază. Fotografii/video opționale; toate briefingurile de siguranță și echipamentul sunt asigurate. Ideal pentru debutanți care doresc maximum de adrenalină cu minimă complexitate.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:explorator','stare:focused','teren:valley','teren:urban','echipament:furnizat','context:solo','context:grup_mic','context:friends','cerinta:rezervare_necesara','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','moment_zi:sunset','timp_calatorie:excursie_zi','abilitati:none_required','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (798, 'Zipline & Rope Courses at Comana Adventure Park', 'Forest catwalks, wobbly bridges, and quick ziplines over wetlands deliver a compact dose of nerves-then-euphoria. Courses are color-graded: start easy, then test your grip and footwork as elements narrow and exposure increases. Instructors manage briefings and checks so you can focus on movement. End with a final zip and sandy-leg landing—adrenaline high, confidence higher. Easy day-trip from Bucharest; great for groups wanting a physical, playful challenge.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 90, 150, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:social','mood:adventurous','terrain:forest','terrain:wetland','equipment:provided','context:friends','context:group','context:small-group','requirement:booking-optional','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:nearby','skills:balance','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Comana', 'Giurgiu', 44.172347, 26.132858, '2025-10-22T21:54:01.818Z', '2025-11-19T14:40:28.406Z', 'comana-adventure-park-zip-ropes', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.172347,26.132858', 'medium', 'mixed', 'any', 'moderate', 'https://casacomana.ro', 'Tiroliene și Trasee pe Frânghii la Parcul de Aventură Comana', 'Pasarele forestiere, poduri șubrede și tiroliene rapide deasupra zonelor umede oferă o doză compactă de emoții urmate de euforie. Traseele sunt codificate pe culori: începeți ușor, apoi testați-vă prinderea și tehnica piciorului pe măsură ce elementele se îngustează și expunerea crește. Instructorii gestionează prezentările și verificările astfel încât să vă puteți concentra pe mișcare. Încheieți cu o ultimă tirolină și aterizare pe nisip - nivel de adrenalină ridicat, încredere și mai mare. Excursie ușoară de o zi de la București; perfect pentru grupuri care doresc o provocare fizică și distractivă.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:social','stare:adventurous','teren:padure','teren:wetland','echipament:furnizat','context:friends','context:grup','context:grup_mic','cerinta:booking-optional','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:aproape','abilitati:balance','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (799, 'Via Ferrata Wild Ferenc in Bicaz Gorges', 'Steel rungs lift you onto limestone walls where the gorge yawns below. Tethered to a fixed cable, you ladder, traverse, and breathe through exposure as the route steepens—an accessible taste of big-wall movement without needing rock-climbing skills. Guides brief clipping technique and pacing, then keep the group flowing. The payoff is visceral: calves humming on tiny steps, eyes level with vultures, and a triumphant top-out over epic scenery.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:intermediate','mood:adrenaline','mood:adventurous','mood:focused','terrain:cliff','terrain:mountain','terrain:forest','equipment:rental-gear','context:small-group','context:friends','requirement:guide-required','risk_level:exposed','weather_fit:wind_sensitive','time_of_day:daytime','time_of_day:sunrise','travel_time_band:day-trip','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Lacu Roșu', 'Harghita', 46.798428, 25.793445, '2025-10-22T21:54:01.818Z', '2025-11-19T14:40:28.406Z', 'bicaz-wild-ferenc-via-ferrata', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.798428,25.793445', 'medium', 'mixed', 'any', 'moderate', 'https://www.via-ferrata.ro/en/wild-ferenc-english/', 'Via Ferrata Wild Ferenc în Cheile Bicazului', 'Trepte de oțel vă ridică pe pereți de calcar unde defileul căscat sub voi. Asigurat la un cablu fix, urcați în scară, traversați și respirați prin expunere pe măsură ce traseul devine mai abrupt - o experiență accesibilă de escaladă mare fără a avea nevoie de abilități de alpinism. Ghizii vă prezintă tehnica de fixare și ritm, apoi mențin grupul în mișcare. Răsplata este viscerală: tulei zvâcnind pe pași mici, privirea la nivelul vulturului și o ieșire triumfătoare peste un peisaj epic.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:intermediar','stare:adrenalina','stare:adventurous','stare:focused','teren:cliff','teren:munte','teren:padure','echipament:inchiriere_echipament','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:exposed','vreme:wind_sensitive','moment_zi:zi','moment_zi:sunrise','timp_calatorie:excursie_zi','abilitati:tehnica','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (800, 'Guided Canyoning in Valea lui Stan (Vidraru)', 'Follow a guide into a sculpted canyon where iron ladders and cables thread past waterfalls and pools. Expect wet boots, short scrambles, cable traverses, and steady adrenaline as you problem-solve each obstacle. It''s hands-on and rhythmic: step, clip, move; repeat. You''ll emerge at Lake Vidraru both muddy and beaming—with that earned sense of having navigated real terrain safely. No rope skills required; all the tricky bits are safeguarded.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 180, 300, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:explorer','mood:focused','terrain:river','terrain:forest','terrain:mountain','equipment:provided','context:small-group','context:friends','requirement:guide-required','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:balance','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Arefu', 'Argeș', 45.37273, 24.59991, '2025-10-22T21:54:01.819Z', '2025-11-19T14:40:28.406Z', 'vidraru-valea-lui-stan-canyoning', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.37273,24.59991', 'medium', 'mixed', 'any', 'moderate', 'https://exploretransfagarasan.ro', 'Canyoning Ghidat în Valea lui Stan (Vidraru)', 'Urmați un ghid într-un canion sculptat unde scări de fier și cabluri sunt țesute pe lângă cascade și bazine. Așteptați-vă la bocanci uzi, mici escaladări, traversări pe cablu și adrenalină constantă în timp ce rezolvați fiecare obstacol. Este practic și ritmic: pas, fixare, mișcare; repetă. Veți ieși la Lacul Vidraru murdari, dar zâmbitori - cu acel sentiment câștigat de a fi navigat un teren real în siguranță. Nici o abilitate cu frânghia nu este necesară; toate porțiunile dificile sunt protejate.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:explorator','stare:focused','teren:river','teren:padure','teren:munte','echipament:furnizat','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:balance','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (936, 'Tandem Skydive over the Black Sea (Tuzla)', 'Strap in for a coastal jump that blends raw speed with seaside scenery. Ground staff brief you on body position, exit count, and how to lift your legs for landing. The climb to altitude tracks the shoreline; the door opens to a wall of salt air and blue. Freefall is loud, fast, and clarity-inducing—then the canopy blooms and everything goes quiet. Your instructor lets you try mellow turns as the Constanța coast slides by beneath. You''ll touch down near the airfield, wobble-walk the high away, and leave with pro video that actually looks like you. Structured, supervised, and pure adrenaline.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:social','terrain:coast','terrain:urban','equipment:provided','context:small-group','context:friends','requirement:booking-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:none_required','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Tuzla', 'Constanța', 43.9845, 28.6062, '2025-10-22T22:05:06.963Z', '2025-11-19T14:40:28.406Z', 'constanta-tuzla-tandem-skydive-regional-air', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=43.98450,28.60620', 'medium', 'mixed', 'any', 'moderate', 'https://www.tnt-brothers.ro', 'Salt în Tandem deasupra Mării Negre (Tuzla)', 'Leagă-te pentru un salt de coastă care îmbină viteza brută cu peisajul maritim. Personalul de la sol te instruiește asupra poziției corpului, numărătorii pentru salt și cum să-ți ridici picioarele pentru aterizare. Urcarea spre înălțime urmărește linia țărmului; ușa se deschide către un zid de aer sărat și albastru. Căderea liberă este zgomotoasă, rapidă și clarifică totul - apoi parașuta se deschide și totul devine liniște. Instructorul tău te lasă să încerci câteva viraje line în timp ce litoralul Constanței alunecă dedesubt. Vei atinge pământul lângă aeroport, vei merge puțin clătinându-te, și vei pleca cu un video profesionist care chiar arată bine.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:social','teren:coasta','teren:urban','echipament:furnizat','context:grup_mic','context:friends','cerinta:rezervare_necesara','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:none_required','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (937, 'Tandem Skydive Luncani (Skydive Transilvania)', 'Briefing, harness, goggles—then wheels up over patchwork Transylvanian fields. At the door your instructor does the counting; your job is ''hips forward, chin up''. The world becomes a cold roar and a tunnel of focus for ~45 seconds until the canopy snaps open and the sound drops to a gentle flapping hiss. On the glide you''ll practice holding the toggles and feel how small inputs bank big views of the Apuseni foothills. Touchdown is a slide or stand, coached in your ear the whole way. You leave not just with footage, but with the clean pride of doing something hard on purpose.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:social','mood:focused','terrain:valley','terrain:urban','equipment:provided','context:small-group','context:friends','requirement:booking-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:none_required','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Luncani', 'Cluj', 46.48167, 23.93139, '2025-10-22T22:05:06.964Z', '2025-11-19T14:40:28.406Z', 'cluj-luncani-tandem-skydive-skydive-transilvania', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.48167,23.93139', 'medium', 'mixed', 'any', 'moderate', 'https://www.skydivetransilvania.ro', 'Salt în Tandem Luncani (Skydive Transilvania)', 'Instruire, ham, ochelari - apoi decolare deasupra câmpurilor mozaicate transilvănene. La ușă, instructorul numără; treaba ta e ''șolduri înainte, bărbie sus''. Lumea devine un vuiet rece și un tunel de concentrare pentru ~45 de secunde până când parașuta se deschide brusc și sunetul scade la un fâșâit lin. În planare vei exersa ținerea comenzilor și vei simți cum mișcări mici produc priveliști mari ale dealurilor Apuseni. Aterizarea e un alunecat sau stat în picioare, ghidat tot timpul. Pleci nu doar cu înregistrare, ci și cu mândria curată de a face ceva dificil cu bună știință.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:social','stare:focused','teren:valley','teren:urban','echipament:furnizat','context:grup_mic','context:friends','cerinta:rezervare_necesara','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:none_required','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (938, 'Tandem Skydive Timișoara (GoJump LRSG)', 'A club vibe with serious instructors: you''ll check in at the grass strip in Sânmihaiu German, get a harness fit, and practice your exit arch on the ground. The climb gives you a slow preview of the Banat plains before the door slides and the wind hits. Freefall is a clean bolt of adrenaline; under canopy your tandem pilot hands you the toggles to feel the wing respond. Landing is soft and coached—then the whole dropzone cheers your first ''A'' in adulting. It''s social, supportive, and designed to turn nerves into a lifetime ''I did that'' memory.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:social','mood:adventurous','terrain:urban','terrain:valley','equipment:provided','context:friends','context:small-group','requirement:booking-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:nearby','skills:none_required','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Sânmihaiu German', 'Timiș', 45.70372, 21.05524, '2025-10-22T22:05:06.964Z', '2025-11-19T14:40:28.406Z', 'timisoara-sanmihaiu-german-tandem-skydive-gojump', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.70372,21.05524', 'medium', 'mixed', 'any', 'moderate', 'https://www.gojump.ro', 'Salt în Tandem Timișoara (GoJump LRSG)', 'Atmosferă de club cu instructori serioși: te înregistrezi pe pista de iarbă din Sânmihaiu German, primești un ham și exersezi poziția de salt pe sol. Urcarea îți oferă o previzualizare lentă a câmpiei Banat înainte ca ușa să culunece și vântul să te lovească. Căderea liberă e un bolt curat de adrenalină; sub parașută, pilotul tandem îți dă comenzile să simți răspunsul aripii. Aterizarea e moale și ghidată - apoi întregul aerodrom te aplaudă pentru prima ta realizare adevărată. E social, susținător și conceput să transforme emoțiile în amintirea de-o viață ''am făcut-o''.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:social','stare:adventurous','teren:urban','teren:valley','echipament:furnizat','context:friends','context:grup_mic','cerinta:rezervare_necesara','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:aproape','abilitati:none_required','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (939, 'Cheile Râșnoavei Bungee Jump', 'Walk the catwalk, clip to the cord, and stare into a forested void. Your jump master counts you down; you step into space and feel your stomach try to stay behind as the gorge rises toward you. There''s a clean second where time slows—then the cord tightens and you rebound, laughing and swearing at the same time. It''s a pure courage rep: short, intense, and crystal-memorable. Expect a precise harness check, clear instructions, and a debrief while your hands still shake. Come as a pair or crew and trade camera angles—half the fun is hyping each other over the rail.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 60, 90, ARRAY['category:adventure','energy:high','experience_level:mixed','mood:adrenaline','mood:adventurous','mood:social','mood:focused','terrain:cliff','terrain:forest','equipment:provided','context:friends','context:small-group','requirement:booking-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:none_required','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Râșnov', 'Brașov', 45.54589, 25.51717, '2025-10-22T22:05:06.965Z', '2025-11-19T14:40:28.406Z', 'rasnov-cheile-rasnoavei-bungee-jump-jumpadventure', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.54589,25.51717', 'medium', 'mixed', 'any', 'moderate', 'https://jumpadventure.ro', 'Sări cu Bungee în Cheile Râșnoavei', 'Mergi pe pasarelă, te fixezi de cablu și privești în gol spre pădure. Maestrul saltului îți numără secundele; pășești în spațiu și simți cum stomacul încearcă să rămână în urmă pe măsură ce cheia urcă spre tine. Este un moment perfect în care timpul încetinește - apoi cablul se întinde și recadezi, râzând și înjurând în același timp. E o repetiție pură de curaj: scurtă, intensă și cristal de memorabilă. Anticipează o verificare precisă a hamului, instrucțiuni clare și o discuție de final în timp ce mâinile îți mai tremură. Vino cu un prieten sau echipă și schimbă unghiurile camerei - jumătate din distracție e să te motivați reciproc peste balustradă.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:adventurous','stare:social','stare:focused','teren:cliff','teren:padure','echipament:furnizat','context:friends','context:grup_mic','cerinta:rezervare_necesara','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:none_required','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (940, 'Adventure Park Brașov Mega Zip & Ropes', 'Clip into a continuous-belay system and move tree to tree across wobbly bridges, swinging logs, and mini-zips that pump the forearms and quiet the overthinking. The headline feature is the lake-crossing zipline: a long, smooth ride out and back above water that flips your stomach and then turns into a grin. Routes are color-graded; you choose your intensity and retry obstacles to beat them with better balance. It''s a social kind of fear-facing—short, repeatable reps with friends shouting ''you''ve got it!'' from the next platform. Expect gloves, helmets, and staff who coach technique when you stall.', 'adventure', ARRAY['summer']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:adventure','energy:high','experience_level:mixed','mood:adrenaline','mood:social','mood:adventurous','terrain:forest','terrain:urban','equipment:provided','context:friends','context:group','context:small-group','requirement:booking-required','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:in-city','skills:balance','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Brașov', 'Brașov', 45.6097, 25.6572, '2025-10-22T22:05:06.965Z', '2025-11-19T14:40:28.406Z', 'brasov-adventure-park-zipline-mega-tyrolean', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.60970,25.65720', 'medium', 'mixed', 'any', 'moderate', 'https://www.parc-aventura.ro', 'Parc de Aventură Brașov - Mega Zip și Frânghii', 'Fixează-te într-un sistem de asigurare continuă și deplasează-te din copac în copac peste poduri instabile, bușteni care se leagănă și mini-zippi care antrenează antebrațele și liniștesc gândirea excesivă. Punctul principal este o linie de zbor care traversează lacul: o cursă lungă și netedă deasupra apei care-ți răstoarnă stomacul și apoi se transformă într-un zâmbet. Traseele sunt marcate pe culori; alegi intensitatea și reiei obstacolele pentru a le depăși cu un echilibru mai bun. E o modalitate socială de a-ți înfrunta temerile - scurte și repetabile cu prietenii care strigă ''poți s-o faci!'' de pe următoarea platformă. Anticipează mănuși, cască și personal care te ghidează tehnic când te blochezi.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:social','stare:adventurous','teren:padure','teren:urban','echipament:furnizat','context:friends','context:grup','context:grup_mic','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:in_oras','abilitati:balance','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (942, 'Seven Ladders Canyon Via Ferrata Sprint', 'Harness up, clip lanyards, and climb steel staples alongside waterfalls that spray a cool mist on your forearms. The route strings seven metal ladders and short traverses through a narrow gorge, so you get bursts of exposure followed by secure ledges to breathe. It''s technical enough to feel legit, but built to be repeatable—ideal fear management reps with a tangible payoff at every rung. You''ll exit the gorge into pine shade, grinning and swapping ''that step!'' stories with your crew. A great upgrade from simple hiking that rewards focus, footwork, and deliberate breathing.', 'adventure', ARRAY['shoulder']::text[], 'outdoor', 'high', 90, 150, ARRAY['category:adventure','energy:high','experience_level:beginner','mood:adrenaline','mood:focused','mood:explorer','mood:social','terrain:forest','terrain:cliff','equipment:rental-gear','context:friends','context:small-group','requirement:guide-required','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:nearby','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Timișu de Jos', 'Brașov', 45.5809, 25.6513, '2025-10-22T22:05:06.966Z', '2025-11-19T14:40:28.406Z', 'brasov-seven-ladders-canyon-viaferrata', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.58090,25.65130', 'medium', 'mixed', 'any', 'moderate', 'https://canionul7scari.ro', 'Sprintul Via Ferrata în Canionul Șapte Scări', 'Îmbracă-te în hamuri, prinde lanțurile și urcă pe scările din oțel de-a lungul cascadelor care stropesc cu o ceață răcoroasă pe antebrațe. Traseul leagă șapte scări metalice și traversări scurte printr-o strâmtoare îngustă, oferind rafale de expunere urmate de terase sigure pentru a respira. Este destul de tehnic pentru a părea autentic, dar construit pentru a fi repetat - ideal pentru gestionarea fricii cu o răsplată concretă la fiecare treaptă. Vei ieși din canion în umbra pinilor, zâmbind și schimbând povești despre ''acea treaptă'' cu echipa ta. O modernizare grozavă a mersului simplu pe jos care răsplătește concentrarea, tehnica piciorului și respirația deliberată.', ARRAY['categorie:aventura','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:focused','stare:explorator','stare:social','teren:padure','teren:cliff','echipament:inchiriere_echipament','context:friends','context:grup_mic','cerinta:ghid_necesar','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:aproape','abilitati:tehnica','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1112, 'Wheel Throwing Intro at Clay Play', 'Center the clay and feel it firm under your palms as you pull a cylinder from a spinning lump—breath syncing with the wheel. An instructor demos each step (centering, opening, pulling, shaping), then guides you one-to-one so you leave with 1–2 pieces ready for glazing. It''s tactile, a little messy, and deeply focusing—perfect if you need to get out of your head and make something real.', 'creative', ARRAY['all']::text[], 'indoor', 'medium', 90, 120, ARRAY['category:creative','energy:medium','experience_level:beginner','mood:creative','mood:mindful','mood:cozy','mood:focused','terrain:urban','equipment:provided','context:solo','context:small-group','context:date','requirement:booking-required','requirement:lesson-recommended','risk_level:low','weather_fit:all_weather','time_of_day:daytime','time_of_day:evening','travel_time_band:in-city','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'București', 44.4268, 26.1035, '2025-10-22T22:16:28.409Z', '2025-11-19T14:40:28.406Z', 'bucharest-clay-wheel-throwing-clay-play', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.4268,26.1035', 'small', 'locals', 'small-group', 'moderate', 'https://clayplay.ro', 'Introducere în Modelarea la Roată la Clay Play', 'Centrați lutul și simțiți-l ferm sub palmele voastre în timp ce trageți un cilindru dintr-o masă rotitoare - respirația sincronizându-se cu roata. Un instructor demonstrează fiecare etapă (centrare, deschidere, tragere, modelare), apoi vă ghidează individual pentru a pleca cu 1-2 piese gata pentru glazurare. Este tactil, puțin murdar și profund concentrat - perfect dacă aveți nevoie să ieșiți din minte și să creați ceva real.', ARRAY['categorie:creativ','energie:mediu','nivel_experienta:incepator','stare:creativ','stare:contemplativ','stare:confortabil','stare:focused','teren:urban','echipament:furnizat','context:solo','context:grup_mic','context:intalnire','cerinta:rezervare_necesara','cerinta:lectie_recomandata','nivel_risc:scazut','vreme:orice_vreme','moment_zi:zi','moment_zi:seara','timp_calatorie:in_oras','abilitati:tehnica','pret:$$']::text[]) ON CONFLICT DO NOTHING;
