INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1024, 'Rhein Azuga 1892 Cellar Tour & Bubbles', 'Descend into historic cellars where Romanian sparkling wine rests on lees, then learn the riddling and disgorging steps before tasting crisp glasses. It''s cool underground, the air smells faintly of chalk and cork, and each sip clicks into place after seeing the craft up close.', 'culinary', ARRAY['all']::text[], 'indoor', 'medium', 60, 120, ARRAY['category:culinary','energy:medium','experience_level:beginner','mood:romantic','mood:creative','mood:explorer','terrain:mountain','terrain:valley','equipment:provided','context:date','context:small-group','context:friends','requirement:booking-required','risk_level:low','weather_fit:all_weather','time_of_day:daytime','travel_time_band:day-trip','skills:none_required','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Azuga', 'Prahova', 45.443768, 25.56391, '2025-10-22T22:10:09.133Z', '2025-11-19T14:40:28.406Z', 'azuga-rhein-cellar-tour', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.443768,25.563910', 'medium', 'mixed', 'any', 'moderate', 'https://www.facebook.com/pensiunepivniteRhein/', 'Tur de Pivniță și Bule la Rhein Azuga 1892', 'Coboară în pivnițele istorice unde vinul spumant românesc se odihnește pe drojdie, apoi află etapele răsucirii și dezgropării înainte de a gusta pahare cristaline. Este răcoare sub pământ, aerul miroase ușor a cretă și dop, iar fiecare înghițitură prinde sens după ce vezi meșteșugul de aproape.', ARRAY['categorie:culinar','energie:mediu','nivel_experienta:incepator','stare:romantic','stare:creativ','stare:explorator','teren:munte','teren:valley','echipament:f