INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (2005, 'Professional Cooking at Horeca Culinary School', 'Horeca Culinary School brings professional chef training to Bucharest with comprehensive cooking courses. Learn fundamental techniques, knife skills, and recipe execution in a fully-equipped teaching kitchen. Instructors are working chefs who balance demonstration with hands-on practice. Expect structured lessons, quality ingredients, and a focus on both technique and flavor. Classes range from beginner fundamentals to advanced culinary methods. You''ll leave with practical skills and confidence to recreate dishes at home.', 'culinary', ARRAY['year-round']::text[], 'indoor', 'medium', 120, 240, ARRAY['category:culinary','energy:medium','mood:creative','mood:focused','context:solo','context:friends','time_of_day:daytime','time_of_day:evening','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'București', 'București', 44.4479, 26.0968, '2025-11-13T09:38:30.269Z', '2025-11-19T14:40:28.406Z', 'bucharest-horeca-culinary-school', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://horecaschool.ro', 'Gătit Profesional la Horeca Culinary School', 'Horeca Culinary School aduce pregătirea profesională de bucătar la București prin cursuri comprehensive de gătit. Veți învăța tehnici fundamentale, mânuirea cuțitului și executarea rețetelor într-o bucătărie complet echipată pentru predare. Instructorii sunt bucătari profesioniști care îmbină demonstrația cu practica directă. Veți avea parte de lecții structurate, ingrediente de calitate și o concentrare pe tehnică și aromă deopotrivă. Cursurile acoperă de la fundamentele pentru începători până la metode culinare avansate. Veți pleca cu abilități practice și încredere pentru a reproduce preparatele acasă.', ARRAY['categorie:culinar','energie:mediu','stare:creativ','stare:focused','context:solo','context:friends','moment_zi:zi','moment_zi:seara','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
