INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (2015, 'Traditional Cookery at Casa Ratiu', 'Casa Ratiu''s cookery lessons teach traditional Transylvanian recipes in a historic setting. Learn regional specialties using local ingredients and time-tested methods. Instructors share family recipes and cultural context alongside cooking techniques. Expect a warm, intimate atmosphere, hands-on preparation, and the chance to taste authentic regional cuisine. A unique opportunity to connect with culinary heritage.', 'culinary', ARRAY['year-round']::text[], 'indoor', 'medium', 120, 180, ARRAY['category:culinary','energy:medium','mood:creative','mood:explorer','context:solo','context:friends','time_of_day:daytime','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Turda', 'Cluj', 46.5742, 23.7864, '2025-11-13T09:38:30.279Z', '2025-11-19T14:40:28.406Z', 'turda-casa-ratiu-cookery', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://casaratiu.ro', 'Gătit Tradițional la Casa Ratiu', 'Lecțiile de gătit de la Casa Ratiu predau rețete tradiționale transilvănene într-un cadru istoric. Veți învăța specialități regionale folosind ingrediente locale și metode verificate de generații. Instructorii vor shared rețete de familie și context cultural, alături de tehnici de gătit. Veți experimenta o atmosferă caldă și intimă, cu pregătire hands-on și șansa de a gusta o cuisine autentică regională. O ocazie unică de a vă conecta la patrimoniul culinar.', ARRAY['categorie:culinar','energie:mediu','stare:creativ','stare:explorator','context:solo','context:friends','moment_zi:zi','pret:$$']::text[]) ON CONFLICT DO NOTHING;
