INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1983, 'Premium Club Swimming at World Class Mall Moldova', 'Iași''s World Class facility in Mall Moldova delivers premium swimming infrastructure with a semi-Olympic pool, modern filtration, and professional lane management. The club atmosphere adds saunas, steam rooms, and a full gym, making it easy to combine swimming with comprehensive training. Expect clean, warm water, bright lighting, and a crowd of members doing serious cardio work. Peak hours attract professionals squeezing in workouts before or after office hours.', 'fitness', ARRAY['year-round']::text[], 'indoor', 'high', 60, 120, ARRAY['category:fitness','energy:high','mood:focused','mood:sophisticated','context:solo','context:friends','time_of_day:evening','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Iași', 'Iași', 47.1615, 27.5878, '2025-11-13T09:38:30.246Z', '2025-11-19T14:40:28.406Z', 'iasi-world-class-mall-moldova', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://www.worldclass.ro/club/world-class-mall-moldova/', 'Înot de Club Premium la World Class Mall Moldova', 'Facilitatea World Class din Iași, situată în Mall Moldova, oferă infrastructură de înot premium cu un bazin semi-olimpic, filtrare modernă și gestionare profesională a culoarelor. Atmosfera de club adaugă săli de saună, cameră de aburi și o sală de fitness completă, făcând ușoară combinarea înotului cu antrenamentul cuprinzător. Te poți aștepta la apă curată și caldă, iluminat strălucitor și o audiență de membri care fac lucru cardio serios. Orele de vârf atrag profesioniști care și-și programează antrenamentele înainte sau după program.', ARRAY['categorie:fitness','energie:ridicat','stare:focused','stare:sophisticated','context:solo','context:friends','moment_zi:seara','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (316, 'Track Day Laps at MotorPark Romania', 'Helmet on, belts tight, nerves humming. MotorPark''s 4.129 km circuit rewards commitment: long 950 m straight to breathe, heavy braking zones to test feel, and linked corners that punish sloppy lines. A briefing breaks down flags, pit etiquette, and safe overtakes. Then it''s you and the car—focus narrows to turn-in points, throttle traces, and the satisfying bite of tires loading up. Sessions stack skill by skill: learn to pace out-laps, manage heat, build speed, and find flow without chasing ego. You leave with sharper car control, a few ''personal best'' lap times, and that heady afterglow only a proper racetrack delivers.', 'motorsports', ARRAY['shoulder']::text[], 'outdoor', 'high', 120, 240, ARRAY['category:motorsports','energy:high','experience_level:mixed','mood:adrenaline','mood:focused','mood:explorer','mood:social','terrain:urban','equipment:bring-own','context:friends','context:small-group','requirement:booking-required','requirement:lesson-recommended','risk_level:moderate','weather_fit:all_weather','time_of_day:daytime','travel_time_band:day-trip','skills:technique','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Adâncata', 'Ialomița', 44.779422, 26.471723, '2025-10-22T21:28:05.314Z', '2025-11-19T14:40:28.406Z', 'adancata-trackday-motorpark-experience', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.779422,26.471723', 'medium', 'mixed', 'any', 'moderate', 'https://motorparkromania.ro', 'Ture pe Circuit la MotorPark Romania', 'Cască pusă, centuri strânse, nervii vibrând. Circuitul de 4,129 km al MotorPark răsplătește angajamentul: o linie dreaptă lungă de 950 m pentru respirat, zone de frânare care testează simțul, și viraje conectate care pedepsesc liniile neglijente. Un briefing explică steagurile, etica boxelor și depășirile sigure. Apoi rămâi tu și mașina - concentrarea se îngustează la punctele de intrare în viraj, urmele de accelerație și mușcătura satisfăcătoare a anvelopelor. Sesiunile adună abilități pas cu pas: înveți să-ți gestionezi turele de ieșire, să controlezi temperatura, să construiești viteză și să găsești fluiditatea fără să-ți urmărești ego-ul. Pleci cu control auto mai ascuțit, câteva timpii personali de referință și acea stare de euforie pe care numai un circuit adevărat o poate oferi.', ARRAY['categorie:motorsports','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:focused','stare:explorator','stare:social','teren:urban','echipament:bring-own','context:friends','context:grup_mic','cerinta:rezervare_necesara','cerinta:lectie_recomandata','nivel_risc:moderate','vreme:orice_vreme','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:tehnica','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (318, 'Outdoor Karting Session (AMCKart Tunari)', 'Start slow, feel the grip, then chase clean lines: brake straight, turn once, power early. In 9–12 minute heats you''ll learn to string corners together—look ahead, rotate the chassis, keep momentum. It''s high-intensity cardio disguised as fun: forearms burn, heart rate spikes, grin widens. Optional timing transponders show real improvement lap-to-lap, which is addictive. Come solo and hunt a PB or bring friends and trade slipstreams. You leave smelling like rubber and smiling like a bandit.', 'motorsports', ARRAY['all']::text[], 'indoor', 'high', 30, 60, ARRAY['category:motorsports','energy:high','experience_level:mixed','mood:adrenaline','mood:social','mood:focused','terrain:urban','equipment:rental-gear','context:solo','context:friends','context:group','requirement:booking-optional','requirement:lesson-recommended','risk_level:moderate','weather_fit:all_weather','time_of_day:daytime','time_of_day:evening','travel_time_band:nearby','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Tunari', 'Ilfov', 44.53564, 26.14964, '2025-10-22T21:28:05.315Z', '2025-11-19T14:40:28.406Z', 'tunari-amckart-karting-session', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.53564,26.14964', 'medium', 'mixed', 'any', 'moderate', 'https://www.amckart.ro', 'Sesiune de Karting în Aer Liber (AMCKart Tunari)', 'Pornește încet, simte aderența, apoi urmărește linii curate: frânează drept, virează o dată, accelerează devreme. În sesiuni de 9-12 minute vei învăța să legi virajele - privește înainte, rotește șasiul, păstrează momentum-ul. Este cardio de înaltă intensitate deghizat în distracție: antebrațele ard, ritmul cardiac urcă, zâmbetul se lărgește. Transponderele opționale de cronometrare arată îmbunătățirea reală de la tură la tură, ceea ce devine rapid dependent. Vino singur și vânează un timp personal sau adu prieteni și schimbă-te la întrecere. Pleci miroșind a cauciuc și zâmbind ca un bandit.', ARRAY['categorie:motorsports','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:social','stare:focused','teren:urban','echipament:inchiriere_echipament','context:solo','context:friends','context:grup','cerinta:booking-optional','cerinta:lectie_recomandata','nivel_risc:moderate','vreme:orice_vreme','moment_zi:zi','moment_zi:seara','timp_calatorie:aproape','abilitati:tehnica','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (747, 'Outdoor Go-Karting – AMCKart (1.24 km CIK-FIA Track)', 'Helmet on, visored down, you settle into a low seat and feel the engine buzz through your ribs. Green light—launch hard, brake in a straight line, turn-in once, apex, and get back to throttle smooth enough to avoid a slide. Laps teach rhythm and focus: braking markers, steering input, the satisfaction of shaving tenths as your lines tighten. It''s competitive without being combative, and the debrief with friends is half the fun: split times, near-misses, and who carried speed through the long right-hander.', 'motorsports', ARRAY['all']::text[], 'indoor', 'high', 10, 60, ARRAY['category:motorsports','energy:high','experience_level:mixed','mood:adrenaline','mood:social','mood:focused','terrain:urban','equipment:rental-gear','context:solo','context:friends','context:team','requirement:booking-optional','risk_level:moderate','weather_fit:all_weather','time_of_day:daytime','time_of_day:evening','travel_time_band:in-city','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'București', 44.544, 26.147, '2025-10-22T21:46:52.754Z', '2025-11-19T14:40:28.406Z', 'bucharest-amckart-outdoor-karting', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.544,26.147', 'medium', 'mixed', 'any', 'moderate', 'https://www.amckart.ro', 'Karting în Aer Liber – AMCKart (Pistă CIK-FIA de 1,24 km)', 'Cască pusă, viziera coborâtă, te așezi într-un scaun jos și simți vibrațiile motorului prin coastele tale. Lumină verde - pleacă din forță, frânează în linie dreaptă, virează o dată, atinge punctul de vârf și revino la accelerație suficient de lin pentru a evita derapajul. Turele te învaață ritm și concentrare: puncte de frânare, comenzi de direcție, satisfacția eliminării sutimilor pe măsură ce liniile tale se strâng. Este competitiv fără să fie conflictual, iar discuțiile de după cu prietenii sunt pe jumătate distracție: timpii înregistrați, ratările și cine a păstrat viteza în virajul lung din dreapta.', ARRAY['categorie:motorsports','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:social','stare:focused','teren:urban','echipament:inchiriere_echipament','context:solo','context:friends','context:team','cerinta:booking-optional','nivel_risc:moderate','vreme:orice_vreme','moment_zi:zi','moment_zi:seara','timp_calatorie:in_oras','abilitati:tehnica','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (749, 'ATV/Quad Off-Road Tour – Zărnești & Plaiul Foii', 'Thumb the throttle, feel the rear tires bite, and follow your guide along forest roads, creek crossings, and grassy ridgelines under the Piatra Craiului ridge. You''ll learn body English for climbs and descents, how to read terrain and pick lines, and how good it feels to splash through a shallow ford in convoy. The pace adapts to the group—beginners get coaching; confident riders get spicier loops. Muddy smiles guaranteed.', 'motorsports', ARRAY['all']::text[], 'outdoor', 'high', 60, 120, ARRAY['category:motorsports','energy:high','experience_level:mixed','mood:adrenaline','mood:adventurous','mood:social','mood:explorer','terrain:forest','terrain:mountain','terrain:valley','equipment:rental-gear','context:friends','context:small-group','context:team','requirement:guide-required','requirement:booking-required','risk_level:moderate','weather_fit:wet_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:navigation','skills:balance','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Zărnești', 'Brașov', 45.561, 25.335, '2025-10-22T21:46:52.755Z', '2025-11-19T14:40:28.406Z', 'zarnesti-atv-offroad-plaiul-foii', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.561,25.335', 'medium', 'mixed', 'any', 'moderate', 'https://www.facebook.com/ATV-Rental-Adventures-Zărnești-Plaiul-Foii-105831758188397', 'Tur cu ATV/Quad în Teren Accidentat – Zărnești & Plaiul Foii', 'Apasă accelerația, simte anvelope spate mușcând terenul și urmează-ți ghidul pe drumuri forestiere, treceri prin pâraie și culmi înerbate sub creasta Pietrei Craiului. Vei învăța tehnica pentru urcări și coborâri, cum să citești terenul și să alegi liniile, și cât de bine se simte să traversezi un râu mic în convoi. Ritmul se adaptează grupului - beginnerii primesc instructaj; cei experimentați primesc rute mai provocatoare. Zâmbete murdare garantate.', ARRAY['categorie:motorsports','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:adventurous','stare:social','stare:explorator','teren:padure','teren:munte','teren:valley','echipament:inchiriere_echipament','context:friends','context:grup_mic','context:team','cerinta:ghid_necesar','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:wet_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:navigation','abilitati:balance','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (795, 'Drift Taxi & Hot Laps at Academia Titi Aur', 'Helmet on, harness tight, and a pro driver idling a track-tuned car beside you. The marshal signals green; tires chirp and suddenly you''re pinned to the bucket seat as lateral G-forces spike. Expect rapid weight transfers, brake-stand entries, and smoky, precise drifts that sketch arcs around clipping points. Between runs, you''ll debrief lines and braking zones, picking up real driving insights—not just thrills. It''s the safe way to feel track speed and car control without needing your own vehicle or experience. Sessions are short, intense, and unforgettable.', 'motorsports', ARRAY['all']::text[], 'outdoor', 'high', 30, 60, ARRAY['category:motorsports','energy:high','experience_level:mixed','mood:adrenaline','mood:focused','mood:social','terrain:urban','equipment:provided','context:friends','context:small-group','requirement:booking-required','risk_level:moderate','weather_fit:all_weather','time_of_day:daytime','time_of_day:evening','travel_time_band:nearby','skills:none_required','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Cocani', 'Dâmbovița', 44.635173, 25.899088, '2025-10-22T21:54:01.815Z', '2025-11-19T14:40:28.406Z', 'cocani-titi-aur-drift-hotlaps', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.635173,25.899088', 'medium', 'mixed', 'any', 'moderate', 'https://academiatitiaur.ro', 'Drift Taxi și Tururi Rapide la Academia Titi Aur', 'Cască pusă, ham strâns, și un șofer profesionist cu o mașină de circuit pornită lângă dvs. Arbitrul semnalizează verde; cauciucurile scot sunete și brusc sunteți lipit de scaun pe măsură ce forțele laterale G cresc. Veți experimenta transferuri rapide de greutate, intrări cu frână trasă și derapaje precise și fumegânde care desenează arcade în jurul punctelor de trecere. Între tururi, veți analiza linia și zonele de frânare, preluând informații reale despre conducere - nu doar senzații tari. Este modalitatea sigură de a simți viteza de circuit și controlul mașinii fără a avea propria mașină sau experiență. Sesiunile sunt scurte, intense și de neuitat.', ARRAY['categorie:motorsports','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:focused','stare:social','teren:urban','echipament:furnizat','context:friends','context:grup_mic','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:orice_vreme','moment_zi:zi','moment_zi:seara','timp_calatorie:aproape','abilitati:none_required','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (796, 'Outdoor Karting Sprints at Prejmer Raceway', 'Grab a race suit, slide into a low-slung kart, and learn to link apexes on one of Romania''s premier circuits. Short heat races are full-focus sprints: late braking, quick hands, and throttle discipline. Track staff brief you, time your laps, and post classifications so you can chase personal bests (or friends). It''s pure competition in 10–15 minute bursts—heart rate up, arms pumped, grin locked. No license needed; all gear provided.', 'motorsports', ARRAY['summer']::text[], 'outdoor', 'high', 30, 60, ARRAY['category:motorsports','energy:high','experience_level:beginner','mood:adrenaline','mood:social','mood:focused','terrain:urban','terrain:valley','equipment:provided','context:friends','context:group','context:solo','requirement:booking-optional','risk_level:moderate','weather_fit:heat_sensitive','time_of_day:daytime','time_of_day:evening','travel_time_band:day-trip','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Prejmer', 'Brașov', 45.746743, 25.770453, '2025-10-22T21:54:01.816Z', '2025-11-19T14:40:28.406Z', 'prejmer-raceway-outdoor-karting', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.746743,25.770453', 'medium', 'mixed', 'any', 'moderate', 'https://prejmer-raceway.com', 'Sprinturi de Karting în Aer Liber la Pista Prejmer', 'Luați un costum de cursă, intrați într-un kart jos și învățați să legați apexurile pe una dintre cele mai bune piste din România. Curse scurte sunt sprinturi de concentrare maximă: frânare târzie, manevre rapide și disciplină la accelerație. Personalul pistei vă informează, măsoară timpii și afișează clasamentele pentru a vă putea urmări recordurile personale. Este curse pure în intervale de 10-15 minute - puls ridicat, brațe pompate, zâmbet blocat. Nici o permisă nu este necesară; tot echipamentul este asigurat.', ARRAY['categorie:motorsports','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:social','stare:focused','teren:urban','teren:valley','echipament:furnizat','context:friends','context:grup','context:solo','cerinta:booking-optional','nivel_risc:moderate','vreme:heat_sensitive','moment_zi:zi','moment_zi:seara','timp_calatorie:excursie_zi','abilitati:tehnica','pret:$$']::text[]) ON CONFLICT DO NOTHING;
