INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (63, 'Bucegi Plateau Hike: Babele & Sphinx', 'Ride the cable car then cross the wind-shaped plateau to the Babele huts and iconic Sphinx rock. A guide sets an easy pace, explains geology and legends, and manages weather calls.', 'nature', ARRAY['spring','fall']::text[], 'outdoor', 'medium', 180, 300, ARRAY['category:nature','context:group','context:small-group','cost_band:$$','energy:medium','equipment:none','experience_level:beginner','indoor_outdoor:outdoor','mood:adventurous','mood:explorer','requirement:booking-required','requirement:guide-recommended','risk_level:moderate','seasonality:shoulder','skills:endurance','skills:none_required','subtype:hiking','subtype:scenic','terrain:forest','terrain:mountain','time_of_day:daytime','travel_time_band:day-trip','weather_fit:wind_sensitive']::text[], NULL, ARRAY[]::text[], 'Bușteni', 'Prahova', 45.4141, 25.5166, '2025-10-20T20:49:36.213Z', '2025-11-19T14:40:28.406Z', 'bucegi-babele-sphinx-hike', ARRAY['hiking','scenic']::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.4141,25.5166', 'small', 'mixed', 'solo-friendly', 'free', 'https://www.romanianfriend.com', 'Drumeție pe Platoul Bucegi: Babele & Sfinxul', 'Urcă cu telecabina și traversează platoul sculptat de vânt către stâncile Babele și celebrul Sfinx. Un ghid menține un ritm ușor, explică geologia și legendele și gestionează condițiile meteo.', ARRAY['categorie:natura','context:grup','context:grup_mic','pret:$$','energie:mediu','echipament:none','nivel_experienta:incepator','interior_exterior:exterior','stare:adventurous','stare:explorator','cerinta:rezervare_necesara','cerinta:guide-recomme