INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1894, 'Tech-House Marathon at Kristal Glam Club', 'Kristal is the big-room Bucharest experience: a dark, polished box built for low-end pressure and long blends. International headliners and local heroes push house/tech-house to minimal/rominimal without losing warmth—the subs are deep, the tops are silky, and the light rig does elegant sweeps rather than chaotic blasts. The vibe is dressy but not stiff: fashion-forward locals, expats, and weekenders on a proper night out. You''ll find VIP rails around a dense main floor, efficient bars, and a production team that treats every Saturday like a mini-festival. Expect a cover and bouncer-managed entry; tables book out quickly. If you want a ''capital city'' club moment—laser canopies, cheers at the drop, sunrise exits—this is your stop.', 'nightlife', NULL, NULL, NULL, 240, 420, ARRAY['category:nightlife','energy:high','mood:party','mood:energetic','mood:luxe','context:friends','context:large-group','context:small-group','time_of_day:night','time_of_day:late-night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Universitate', 44.4352, 26.0979, '2025-10-23T14:01:13.712Z', '2025-11-19T14:40:28.406Z', 'bucharest-kristal-techno-mainroom', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://clubkristal.ro', 'Maraton Tech-House la Kristal Glam Club', 'Kristal este experiența bucureșteană de club mare: o cutie întunecată și elegantă construită pentru presiune joasă și mixaje lungi. Headlineri internaționali și eroi locali duc house/tech-house spre minimal/rominimal fără a pierde căldura—subwooferele sunt profunde, înaltele sunt mătăsoase, iar sistemul de lumini face mișcări elegante în loc de explozii haotice. Atmosfera este elegantă dar nu rigidă: localnici pasionați de modă, expatriați și petrecăreți într-o seară adevărată. Vei găsi balustrade VIP în jurul unui ring central dens, baruri eficiente și o echipă de producție care tratează fiecare sâmbătă ca pe un mini-festival. Așteaptă-te la taxă de intrare și control la ușă; mesele se rezervă rapid. Dacă vrei un moment de club în stil de capitală—cupole laser, urale la schimbări de ritm, ieșiri la răsărit—aceasta este destinația ta.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:energetic','stare:luxe','context:friends','context:large-group','context:grup_mic','moment_zi:noapte','moment_zi:late-night','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1895, 'Student Night at Kulturhaus Bucharest', 'Budget-friendly, high-energy nights in the heart of Old Town. Kulturhaus is a sprawling multi-room student club where commercial, EDM, and throwback hits roll until morning. Inside the historic building, expect brick vaults, basic lighting, and a ''no frills, all fun'' attitude—the kind of place where you lose your friends and make new ones by the bar. Drinks are cheap, the dress code is relaxed, and the crowd skews young, local, and social. It''s a great starter club if you''re new to Bucharest''s nightlife: easy entry, big dance floors, and a party-first atmosphere that doesn''t take itself too seriously.', 'nightlife', NULL, NULL, NULL, 210, 360, ARRAY['category:nightlife','energy:high','mood:party','mood:energetic','mood:rebellious','context:friends','context:large-group','context:small-group','time_of_day:night','time_of_day:late-night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Old Town', 44.4319, 26.1025, '2025-10-23T14:01:13.712Z', '2025-11-19T14:40:28.406Z', 'bucharest-student-warehouse-kulturhaus', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://www.facebook.com/KulturhausBukarest', 'Noaptea Studenților la Kulturhaus București', 'Seri accesibile și pline de energie în inima Centrului Vechi. Kulturhaus este un club studențesc întins pe mai multe camere, unde muzica comercială, EDM și hiturile vechi rulează toată noaptea. În interiorul clădirii istorice, te poți aștepta la bolți de cărămidă, iluminat basic și o atitudine de ''distracție pură''. Este locul unde îți pierzi prietenii și faci alții noi lângă bar. Băuturile sunt ieftine, codul vestimentar este lejer, iar publicul este tânăr, local și sociabil. Este un club perfect pentru cei noi în viața de noapte a Bucureștiului: intrare ușoară, piste de dans mari și o atmosferă care pune distracția pe primul plan, fără pretenții.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:energetic','stare:rebellious','context:friends','context:large-group','context:grup_mic','moment_zi:noapte','moment_zi:late-night','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1897, 'House Night at Fratelli Lounge & Club', 'Fratelli blends a chic lounge aesthetic with a lively club floor set to house, vocal house, and polished party sets. The Herăstrău venue draws a glossy mix of locals and expats—groups celebrating, couples flirting at the bar, and a sea of tables buzzing with bottle service. Lighting is warm and cinematic with bursts of confetti on big hooks; the sound is punchy and pleasantly loud. Come late and plan to linger—the room really unlocks post-1am. Dress is upscale casual; reservations help. If you want a celebratory, sociable night with fewer elbows than a student club and more glamour than a pub crawl, this is it.', 'nightlife', NULL, NULL, NULL, 210, 360, ARRAY['category:nightlife','energy:high','mood:party','mood:luxe','mood:social','context:friends','context:large-group','context:date','time_of_day:night','time_of_day:late-night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Herăstrău Park area', 44.47247, 26.11297, '2025-10-23T14:01:13.713Z', '2025-11-19T14:40:28.406Z', 'bucharest-fratelli-herastrau-house-lounge', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://fratelli.ro', 'Noaptea de House la Fratelli Lounge & Club', 'Fratelli îmbină un aspect de lounge șic cu o pistă de dans animată, cu muzică house, vocal house și seturi de petrecere rafinate. Localul din Herăstrău atrage o mulțime strălucitoare de localnici și expatriați - grupuri care sărbătoresc, cupluri care flirtează la bar și o mare de mese animate de servicii VIP. Luminile sunt calde și cinematografice, cu burst-uri de confeti la momente cheie; sunetul este puternic și plăcut de tare. Vino târziu și pregătește-te să rămâi - sala prinde viață abia după ora 1. Ținuta este smart casual; rezervările sunt recomandate. Dacă vrei o seară festivă și sociabilă, cu mai puțină îmbulzeală decât un club studențesc și mai multă eleganță decât o ieșire la puburi, acesta este locul potrivit.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:luxe','stare:social','context:friends','context:large-group','context:intalnire','moment_zi:noapte','moment_zi:late-night','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1898, 'Rooftop Cocktails at NOMAD Skybar', 'Set above Lipscani''s cobbles, Nomad''s glass-ceilinged rooftop frames Old Town''s rooftops in warm light. By day it''s brunch and breezy cocktails; by night, the music climbs from feel-good house to sing-along classics, and the vibe turns social—dates sharing plates under hanging greenery, crews posted up at high-tops, tourists and locals mingling at the bar. It''s more ''party restaurant'' than club: you''ll sip mezcal sours and, before you know it, be dancing between tables. Entry is easy if you''re early; later, door staff meter capacity. It''s a great place to start (or end) an Old Town circuit.', 'nightlife', NULL, NULL, NULL, 120, 240, ARRAY['category:nightlife','energy:medium','mood:social','mood:romantic','mood:sophisticated','context:date','context:friends','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Lipscani', 44.4321, 26.1001, '2025-10-23T14:01:13.714Z', '2025-11-19T14:40:28.406Z', 'bucharest-rooftop-nomad-skybar', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://nomadskybar.ro', 'Cocktailuri pe Acoperiș la NOMAD Skybar', 'Amplasat deasupra pietrelor din Lipscani, acoperișul cu tavan de sticlă al Nomad încadrează în lumină caldă acoperișurile Centrului Vechi. Ziua este destinat brunch-ului și cocktailurilor răcoroase; noaptea, muzica urcă de la house-ul plăcut la clasicele care se cântă în cor, iar atmosfera devine sociabilă - cupluri care împart farfurii sub verdeață suspendată, grupuri adunate la mese înalte, turiști și localnici care se amestecă la bar. Este mai degrabă un ''restaurant de petrecere'' decât un club: vei sorbi cocktailuri de mezcal și, înainte să realizezi, vei dansa între mese. Intrarea este ușoară dacă ajungi devreme; mai târziu, personalul de la ușă monitorizează capacitatea. Este un loc perfect pentru a începe sau termina un circuit prin Centrul Vechi.', ARRAY['categorie:viata_noapte','energie:mediu','stare:social','stare:romantic','stare:sophisticated','context:intalnire','context:friends','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1900, 'Live Band & Karaoke at Mojo Music Club', 'Mojo is Old Town''s three-floor funhouse: a cavernous karaoke stage where strangers become duet partners, a live-band floor smashing rock/pop covers, and a chill pub level for regrouping between sets. Expect colored spotlights, thumping but friendly sound, and a buoyant, mixed crowd—students, office crews, visiting stag parties, and locals who know the staff on a first-name basis. Drinks are straightforward, prices fair, and the energy contagious. It''s the kind of place you wander into for ''one song'' and leave at 3am with a hoarse voice and ten new videos on your phone.', 'nightlife', NULL, NULL, NULL, 180, 300, ARRAY['category:nightlife','energy:high','mood:party','mood:social','mood:energetic','context:friends','context:large-group','context:small-group','time_of_day:night','time_of_day:late-night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Old Town', 44.4323, 26.102, '2025-10-23T14:02:11.825Z', '2025-11-19T14:40:28.406Z', 'bucharest-mojo-live-karaoke', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://mojomusic.ro', 'Trupă Live & Karaoke la Mojo Music Club', 'Mojo este casa distracției cu trei etaje din Centrul Vechi: o scenă de karaoke imensă unde necunoscuți devin parteneri de duet, un etaj cu trupă live care cântă preluări rock/pop și un nivel de pub pentru relaxare între momente. Te poți aștepta la lumini colorate, sunet puternic dar prietenos și o atmosferă plină de energie - studenți, echipe de birou, grupuri de petrecerea burlacilor și localnici care îi cunosc pe angajați pe numele mic. Băuturile sunt simple, prețurile corecte și energia molipsitoare. Este locul în care intri pentru ''o singură piesă'' și pleci la ora 3 dimineața cu vocea răgușită și zece videoclipuri noi pe telefon.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:social','stare:energetic','context:friends','context:large-group','context:grup_mic','moment_zi:noapte','moment_zi:late-night','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1902, 'Stand-Up Night at The Fool Comedy Club', 'The city''s dedicated stand-up hub sits just off University Square. Inside: a low stage, tight seating, and a room designed for punchlines—spotlit faces, crisp vocal clarity, and a bar primed for drink refills between sets. Bills mix Romanian comics (often in Romanian) with occasional English-friendly shows; the crowd is students, office crews, and couples on a low-pressure date night. Energy ramps with late shows and weekend specials. If you want a laugh-forward night that still leaves time for post-show drinks nearby, this is your anchor.', 'nightlife', NULL, NULL, NULL, 90, 150, ARRAY['category:nightlife','energy:medium','mood:social','mood:chill','mood:romantic','context:date','context:friends','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Universitate', 44.4359, 26.1, '2025-10-23T14:02:11.827Z', '2025-11-19T14:40:28.406Z', 'bucharest-the-fool-standup', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://thefool.ro', 'Seară de Stand-Up la The Fool Comedy Club', 'Centrul dedicat stand-up-ului din oraș, situat lângă Piața Universității. La interior: o scenă joasă, locuri strânse și o cameră proiectată pentru replici - fețe luminatespot, claritate vocală precisă și un bar pregătit pentru completarea paharelor între momente. Programul include comedy-uri românești (adesea în română) și ocazional spectacole în engleză; publicul este format din studenți, echipe de birou și cupluri într-o seară de ieșit lejeră. Energia crește odată cu spectacolele târzii și evenimentele de weekend. Dacă vrei o seară plină de râs care să lase și timp pentru băuturi după spectacol, acesta este locul potrivit.', ARRAY['categorie:viata_noapte','energie:mediu','stare:social','stare:relaxat','stare:romantic','context:intalnire','context:friends','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1908, 'Craft Beers at Beer O''Clock', 'Tucked on Băcani near Lipscani''s bustle, Beer O''Clock is a trusty craft stop with rotating taps and a back-bar of Romanian and international bottles. Inside is wood, chalkboards, and easy conversations; music stays background, letting hop nerds and casual drinkers compare notes. Prices are fair and the staff know their styles, steering you from crisp pilsners to punchy IPAs. It''s a refreshing pause between louder stops—or a whole evening if you''re in the mood for pints and talk.', 'nightlife', NULL, NULL, NULL, 120, 210, ARRAY['category:nightlife','energy:low','mood:chill','mood:social','mood:sophisticated','context:friends','context:small-group','context:solo','time_of_day:evening','time_of_day:night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Lipscani', 44.432, 26.1026, '2025-10-23T14:03:00.638Z', '2025-11-19T14:40:28.406Z', 'bucharest-craft-beer-oclock', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://www.facebook.com/beeroclockbucharest', 'Beri Artizanale la Beer O''Clock', 'Ascuns pe strada Băcani, lângă agitația din Lipscani, Beer O''Clock este un punct sigur pentru beri artizanale, cu robinete rotative și un bar cu sticle românești și internaționale. Interiorul este din lemn, cu table de scris și conversații ușoare; muzica rămâne pe fundal, lăsând pasionații de hamei și consumatorii ocazionali să facă schimb de impresii. Prețurile sunt corecte, iar personalul cunoaște stilurile, ghidându-te de la pilsner proaspăt la IPA puternic. E o pauză răcoritoare între opriri mai zgomotoase - sau o seară întreagă dacă ești în mood de discuții și băut.', ARRAY['categorie:viata_noapte','energie:scazut','stare:relaxat','stare:social','stare:sophisticated','context:friends','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1912, 'Live Anthems at Hard Rock Cafe Bucharest', 'Classic rock memorabilia, big stage, bigger sound—Hard Rock''s Herăstrău location doubles as a dependable live venue. Expect tribute nights, touring acts, and local covers that turn into full-room singalongs. The crowd spans office crews to visiting fans; staff keep service fast even when the floor packs. Plenty of seating, standing room by the stage, and a patio when weather''s kind. Drinks land mid-range; burgers and wings keep the energy up. It''s polished, loud, and friendly—ideal when you want concert vibes without hunting down a one-off gig.', 'nightlife', NULL, NULL, NULL, 150, 240, ARRAY['category:nightlife','energy:medium','mood:party','mood:social','mood:energetic','context:friends','context:large-group','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Herăstrău Park area', 44.4764, 26.0802, '2025-10-23T14:05:26.504Z', '2025-11-19T14:40:28.406Z', 'bucharest-hard-rock-cafe-live-sets', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://hardrockcafe.ro', 'Imnuri Live la Hard Rock Cafe București', 'Memorabilia rock clasică, scenă mare, sunet și mai mare - locația Hard Rock de la Herăstrău servește și drept spațiu de concerte de încredere. Te poți aștepta la seri de tribut, formații în turneu și preluări locale care transformă sala într-un cor spontan. Publicul variază de la echipe de birou la fani veniți din alte părți; personalul menține serviciul rapid chiar și atunci când spațiul este plin. Locuri suficiente, spațiu lângă scenă și o terasă pentru vreme bună. Băuturile sunt la preț mediu, burgeri și aripi țin energia sus. E un loc lustruit, zgomotos și prietenos - ideal atunci când vrei atmosferă de concert fără să mai cauți un spectacol izolat.', ARRAY['categorie:viata_noapte','energie:mediu','stare:party','stare:social','stare:energetic','context:friends','context:large-group','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1913, 'Peak Weekend at FACE Club', 'Mirroring, LEDs, and a precision sound system make FACE a magnet for late-night crews who want glossy production and chart-to-house crossovers. Expect dancers, confetti pops, and a VIP-friendly layout in the Herăstrău club belt. Dress codes skew sharp—heels, shirts, and a bit of sparkle get you waved in quicker. It''s crowded and kinetic past 1am, with high-energy sets that bounce between mainstream house, hits, and Romanian favorites. If you want ''big night out'' photos, this is where they''re taken.', 'nightlife', NULL, NULL, NULL, 210, 360, ARRAY['category:nightlife','energy:high','mood:party','mood:luxe','mood:energetic','context:friends','context:large-group','context:small-group','time_of_day:night','time_of_day:late-night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Herăstrău Park area', 44.4765, 26.081, '2025-10-23T14:05:26.505Z', '2025-11-19T14:40:28.406Z', 'bucharest-face-club-herastrau-party', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://faceclubbucharest.com', 'Vârful Weekendului la Clubul FACE', 'Oglinzi, LED-uri și un sistem de sunet de precizie fac din FACE un magnet pentru echipele de noapte târzie care vor producție strălucitoare și treceri între hituri și house. Te poți aștepta la dansatori, confetti și un aranjament prietenos VIP în zona de cluburi de la Herăstrău. Codul de îmbrăcăminte e elegant - tocuri, cămăși și puțin sclipici te vor trece mai ușor de intrare. E aglomerat și kinetic după ora 1, cu setări de energie înaltă care trec între house mainstream, hituri și favorite românești. Dacă vrei poze pentru ''marea seară ieșire'', aici se fac.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:luxe','stare:energetic','context:friends','context:large-group','context:grup_mic','moment_zi:noapte','moment_zi:late-night','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1915, 'Origo Cocktail Evenings (Old Town)', 'By day, Origo is Bucharest''s coffee benchmark; by night, its cocktail side turns the same precision to spirits. Expect a tight list executed perfectly—sours that balance, highballs that actually sparkle, and signatures that steer clear of gimmicks. The space is minimalist and warm: concrete, wood, and bar seating that lets you chat with the team while they work. Crowd is mixed and international, with plenty of locals who''ve been coming for years. If you want high quality without the velvet rope, start here.', 'nightlife', NULL, NULL, NULL, 90, 150, ARRAY['category:nightlife','energy:medium','mood:sophisticated','mood:social','mood:chill','context:date','context:friends','context:solo','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Lipscani', 44.432, 26.102, '2025-10-23T14:05:26.507Z', '2025-11-19T14:40:28.406Z', 'bucharest-origo-cocktail-lab-nights', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://origocoffee.ro', 'Serile de Cocktail Origo (Centrul Vechi)', 'Ziua, Origo este punctul de referință pentru cafea în București; noaptea, latura sa de cocktail aplică aceeași precizie băuturilor spirtoase. Veți găsi o listă scurtă executată perfect—sour-uri echilibrate, highball-uri care chiar strălucesc și semnături care evită gimmick-urile. Spațiul este minimalist și primitor: beton, lemn și scaune de bar care vă permit să stați de vorbă cu echipa în timp ce lucrează. Publicul este mixt și internațional, cu mulți localnici care vin de ani de zile. Dacă doriți calitate înaltă fără pretenții, începeți de aici.', ARRAY['categorie:viata_noapte','energie:mediu','stare:sophisticated','stare:social','stare:relaxat','context:intalnire','context:friends','context:solo','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1916, 'High-Gloss Nights at E3 by Entourage', 'Floreasca''s E3 by Entourage mixes lounge polish with club punch: soft banquettes, theatrical lighting, and a crowd dressed for photos. Music trends commercial-house and high-energy edits with vocal hooks; expect sax or percussion cameos on busy nights. Bottle service is common; walk-ins possible earlier. It''s the upscale side of Bucharest nightlife—less underground, more champagne pops and birthday sparklers.', 'nightlife', NULL, NULL, NULL, 180, 330, ARRAY['category:nightlife','energy:high','mood:luxe','mood:party','mood:energetic','context:friends','context:large-group','context:small-group','time_of_day:night','time_of_day:late-night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Floreasca', 44.47, 26.105, '2025-10-23T14:05:26.508Z', '2025-11-19T14:40:28.406Z', 'bucharest-e3-entourage-floreasca-club', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://meniu.byentourage.ro', 'Nopți de Lux la E3 by Entourage', 'E3 by Entourage din Floreasca combină eleganța lounge-ului cu energia clubului: canapele moi, iluminat teatral și o mulțime îmbrăcată pentru fotografii. Muzica oscilează între comercial-house și editări de înaltă energie cu inserții vocale; în serile aglomerate puteți anticipa apariții de saxofon sau percuție. Serviciul la sticlă este comun; intrările fără rezervare sunt posibile la început de seară. Este latura rafinată a vieții de noapte bucureștene—mai puțin underground, mai mult pop de șampanie și artificii de ziua de naștere.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:luxe','stare:party','stare:energetic','context:friends','context:large-group','context:grup_mic','moment_zi:noapte','moment_zi:late-night','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1917, 'Uanderful: Floreasca Lounge & Late Drinks', 'Dimly lit, smooth, and photogenic—Uanderful is where Floreasca winds down in velvet and wood. Think premium spirits, balanced classics, and a DJ who keeps it groovy not blaring. The crowd is well-heeled but friendly; conversations flow at tables while couples orbit the bar. Great pre-club warm-up or late-night wind-down when you want style without the crush.', 'nightlife', NULL, NULL, NULL, 90, 180, ARRAY['category:nightlife','energy:medium','mood:sophisticated','mood:luxe','mood:romantic','context:date','context:small-group','context:friends','time_of_day:evening','time_of_day:night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Floreasca', 44.4705, 26.1048, '2025-10-23T14:05:26.508Z', '2025-11-19T14:40:28.406Z', 'bucharest-uanderful-floreasca-lounge', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://uanderful.ro', 'Uanderful: Lounge & Băuturi Târzii Floreasca', 'Întunecat, elegant și fotogenic—Uanderful este locul unde Floreasca se relaxează în catifea și lemn. Gândiți-vă la băuturi premium, cocktailuri clasice echilibrate și un DJ care menține atmosfera plăcută, nu stridentă. Publicul este rafinat dar prietenos; conversațiile curg la mese în timp ce cupluri gravitează în jurul barului. Perfect ca pregătire pentru club sau pentru relaxarea de la final de noapte, atunci când doriți stil fără aglomerație.', ARRAY['categorie:viata_noapte','energie:mediu','stare:sophisticated','stare:luxe','stare:romantic','context:intalnire','context:grup_mic','context:friends','moment_zi:seara','moment_zi:noapte','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1919, 'Sunset Rooftops at 18 Lounge (City Gate)', 'Above Piața Presei, 18 Lounge wraps a sleek dining room around postcard views of Herăstrău and the Arc''s traffic ballet. Expect smooth service, balanced cocktails, and a DJ who keeps tempo classy. It''s an elegant kickoff for upscale club nights nearby—or a full evening if you want to linger over courses. Dress smart; book for golden hour.', 'nightlife', NULL, NULL, NULL, 90, 150, ARRAY['category:nightlife','energy:medium','mood:sophisticated','mood:romantic','mood:luxe','context:date','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Herăstrău Park area', 44.4795, 26.078, '2025-10-23T14:05:26.510Z', '2025-11-19T14:40:28.406Z', 'bucharest-18-lounge-sunset-rooftop', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://18lounge.ro', 'Terase la Apus la 18 Lounge (City Gate)', 'Deasupra Pieței Presei, 18 Lounge încadrează o sală de mese elegantă cu vederi precum o carte poștală spre Herăstrău și baletul traficului de la Arc. Veți găsi un serviciu smooth, cocktailuri echilibrate și un DJ care menține ritmul select. Este un început elegant pentru serile de club din apropiere—sau o seară completă dacă doriți să zăboviți asupra curselor. Îmbrăcați-vă elegant; rezervați pentru ora de aur.', ARRAY['categorie:viata_noapte','energie:mediu','stare:sophisticated','stare:romantic','stare:luxe','context:intalnire','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1921, 'Mojo: Karaoke & Live Band Nights', 'Old Town''s karaoke institution stacks a sports bar, live stage, and a late-night karaoke room into one buzzing vertical space. Early doors, it''s pints and matches on TV; later, expect a warm-hearted karaoke crowd where support is loud and the queue moves. Weekends bring full-band covers and theme parties. It''s unpretentious, friendly, and reliably open late—perfect for mixed groups.', 'nightlife', NULL, NULL, NULL, 150, 240, ARRAY['category:nightlife','energy:high','mood:party','mood:social','mood:energetic','context:friends','context:large-group','context:small-group','time_of_day:night','time_of_day:late-night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Lipscani', 44.4323, 26.102, '2025-10-23T14:06:27.233Z', '2025-11-19T14:40:28.406Z', 'bucharest-mojo-karaoke-live', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://mojomusic.ro', 'Mojo: Seri de Karaoke & Trupă Live', 'Instituția de karaoke din Centrul Vechi care îmbină un bar sportiv, o scenă live și o cameră de karaoke într-un spațiu vertical plin de energie. La început, beri și meciuri la TV; mai târziu, o atmosferă de karaoke plină de susținere, unde coada merge repede. Zilele de weekend aduc trupe cover și petreceri tematice. Este nepretențios, prietenos și deschis târziu—perfect pentru grupuri mixte.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:social','stare:energetic','context:friends','context:large-group','context:grup_mic','moment_zi:noapte','moment_zi:late-night','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1922, 'SHOTERIA Old Town: Rapid-Fire Shots', 'Neon, lab coats, and trays of technicolor shots—SHOTERIA is pure party theatre. The menu riffs on classics (think B-52s, picklebacks, boozy sorbets) and the bartenders keep the tempo as high as the music. It''s a pre-game launchpad that often becomes the whole night. Expect a young, international crowd and a floor that''s rarely still.', 'nightlife', NULL, NULL, NULL, 60, 120, ARRAY['category:nightlife','energy:high','mood:party','mood:energetic','mood:rebellious','context:friends','context:small-group','time_of_day:evening','time_of_day:night','time_of_day:late-night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Old Town', 44.4318, 26.101, '2025-10-23T14:06:27.235Z', '2025-11-19T14:40:28.406Z', 'bucharest-shoteria-old-town-shots', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://shoteria.ro', 'SHOTERIA Centrul Vechi: Shots cu Foc Rapid', 'Neon, halate de laborator și tăvi cu shots în culorile curcubeului—SHOTERIA este teatru pur de petrecere. Meniul reinterpretează clasicele (gândiți-vă la B-52, picklebacks, sorbeturi alcoolice) iar barmanii mențin ritmul la fel de alert ca și muzica. Este o rampă de lansare pentru pre-party care adesea devine întreaga seară. Veți întâlni o mulțime tânără și internațională într-un spațiu care rar stă nemișcat.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:energetic','stare:rebellious','context:friends','context:grup_mic','moment_zi:seara','moment_zi:noapte','moment_zi:late-night','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1924, 'Fire Club: Rock & Alternative', 'One of Lipscani''s old-guard rock bars—brick arches, posters, and a stage that sweats. Expect punk, metal, indie, and tribute nights, plus karaoke mid-week. Beer''s cheap, shots fly, and the pit wakes up when guitars get crunchy. It''s the honest, noisy side of Old Town.', 'nightlife', NULL, NULL, NULL, 150, 240, ARRAY['category:nightlife','energy:high','mood:underground','mood:rebellious','mood:party','context:friends','context:small-group','context:solo','time_of_day:night','time_of_day:late-night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Lipscani', 44.4325, 26.1022, '2025-10-23T14:06:27.237Z', '2025-11-19T14:40:28.406Z', 'bucharest-fire-club-rock-alt', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://fire.ro', 'Fire Club: Rock & Alternative', 'Unul dintre barurile de rock clasice din Lipscani—arcuri de cărămidă, postere și o scenă care transpiră. Veți găsi seri de punk, metal, indie și concerte tribut, plus karaoke la mijloc de săptămână. Berea este ieftină, shots-urile zboară, iar zona din fața scenei prinde viață când chitările devin zgomotoase. Este latura sinceră și zgomotoasă a Centrului Vechi.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:underground','stare:rebellious','stare:party','context:friends','context:grup_mic','context:solo','moment_zi:noapte','moment_zi:late-night','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1925, 'St. Patrick Irish Pub: Sports & Pints', 'Split-level Irish pub with plenty of screens, Guinness poured right, and hearty pub grub. Match nights are loud and friendly; off-nights run quizzes and live tunes. It''s a crowd-pleaser for mixed groups wanting pints and a seat without the club crush.', 'nightlife', NULL, NULL, NULL, 120, 210, ARRAY['category:nightlife','energy:medium','mood:social','mood:chill','mood:party','context:friends','context:large-group','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Lipscani', 44.432, 26.1008, '2025-10-23T14:06:27.237Z', '2025-11-19T14:40:28.406Z', 'bucharest-st-patrick-irish-pub-sports', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://www.stpatricks.ro', 'St. Patrick Irish Pub: Sport și Beri', 'Pub irlandez pe două nivele cu multe ecrane, Guinness turnat perfect și mâncare consistentă de pub. Serile de meci sunt pline de viață și prietenoase; în serile libere au quiz-uri și muzică live. Este un loc care place tuturor grupurilor care vor beri și un loc unde să stea fără aglomerația de club.', ARRAY['categorie:viata_noapte','energie:mediu','stare:social','stare:relaxat','stare:party','context:friends','context:large-group','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1929, 'Quantic: Rock & Metal Live', 'Grozăvești''s concert workhorse hosts everything from folk-rock to extreme metal, with a big beer garden for pre-show hangs. Indoors, the stage, pits, and sound are dialed for guitars; outdoors, summer nights stretch with food stalls and chill tables. Expect passionate crowds and fair ticket prices.', 'nightlife', NULL, NULL, NULL, 180, 300, ARRAY['category:nightlife','energy:high','mood:underground','mood:energetic','mood:social','context:friends','context:small-group','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'Cotroceni', 44.438, 26.045, '2025-10-23T14:06:27.240Z', '2025-11-19T14:40:28.406Z', 'bucharest-quantic-rock-metal', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://quantic.pub', 'Quantic: Rock și Metal Live', 'Locul de concerte din Grozăvești găzduiește totul de la folk-rock la metal extrem, cu o grădină mare de bere pentru întâlniri înainte de spectacol. La interior, scena, zona de dans și sunetul sunt pregătite pentru chitări; în exterior, serile de vară se prelungesc cu tarabe cu mâncare și mese relaxante. Așteptați-vă la public pasionat și prețuri corecte la bilete.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:underground','stare:energetic','stare:social','context:friends','context:grup_mic','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1949, 'Flying Circus Alt Nights', 'Cluj''s cult alt venue lives above Union Square''s cobbles, with big sash windows that leak neon onto the street and a sound system that swings from jangly indie to drum & bass and live Balkan brass. Inside it''s brick arches, flyers from a thousand gigs, and a stage that''s close enough to touch. Weeknights skew artsy—open mics, niche cinema, vinyl sessions—while weekends turn sweaty with DJs pushing breakbeat, house, and pop-punk singalongs until the lights come up. The crowd blends students, expats, and locals who know every chorus; cheap beers fuel the chorus of clinks at the bar while strobes ripple across the low ceiling. Expect spontaneous dance circles, a friendly door, and a safe, social vibe that welcomes solo wanderers and big groups alike. It''s the kind of place you arrive for ''one drink'' and leave at sunrise, hoarse and happy.', 'nightlife', NULL, NULL, NULL, 180, 360, ARRAY['category:nightlife','energy:high','mood:party','mood:social','mood:underground','mood:energetic','context:friends','context:small-group','context:solo','context:large-group','time_of_day:evening','time_of_day:night','time_of_day:late-night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Cluj-Napoca', 'Centru', 46.769539, 23.589831, '2025-10-23T14:13:34.670Z', '2025-11-19T14:40:28.406Z', 'cluj-flying-circus-alt-nights', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://flyingcircus.ro', 'Circul Zburător - Seri Alternative', 'Locul cult alternativ din Cluj, situat deasupra pietrelor de la Union Square, cu ferestre mari care lasă neonul să se reverse pe stradă și un sistem de sunet care oscilează între indie și drum & bass și alamă balcanică live. În interior sunt arcade de cărămidă, afișe de la o mie de concerte și o scenă la care aproape poți atinge. Serile de săptămână sunt creative - micro-spectacole, cinema de nișă, sesiuni de vinil - iar weekendurile devin fierbinți cu DJ-i care mixează breakbeat, house și pop-punk până când luminile se aprind. Publicul este un amestec de studenți, expatriați și localnici care știu fiecare refren; beri ieftine alimentează corul de ciocniri la bar în timp ce stroboscoapele pulsează pe tavanul scund. Te poți aștepta la cercuri spontane de dans, un control la intrare prietenos și o atmosferă sigură și socială care îi primește pe cei care vin singuri sau în grupuri mari. Este genul de loc unde ajungi pentru ''o singură băutură'' și pleci la răsărit, răgușit și fericit.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:social','stare:underground','stare:energetic','context:friends','context:grup_mic','context:solo','context:large-group','moment_zi:seara','moment_zi:noapte','moment_zi:late-night','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1950, 'Steampunk Cocktails at Joben', 'All brass fittings, copper coils, and glowing gauges, Joben is a whimsical, steampunk-themed hideout where bartenders in suspenders shake crystal-clear highballs and smoky, aromatic signatures built on Romanian spirits. The room hums at a conversational volume—vinyl soul and electro-swing under low amber lighting—while couples tuck into velvet booths and small groups orbit the long wood bar for bartender''s choice riffs. Expect a mixed, well-dressed crowd: creatives, date-nighters, visiting foodies. The menu runs from refined sours and clarified classics to zero-ABV spritzes; glassware is theatrical, but service stays warm and unpretentious. It''s a classy staging post before a big night, or a destination if you prefer sophistication over decibels. Reservations help on weekends; dress smart-casual and let the bar team narrate the build of your drink.', 'nightlife', NULL, NULL, NULL, 120, 210, ARRAY['category:nightlife','energy:medium','mood:sophisticated','mood:romantic','mood:luxe','mood:social','context:date','context:small-group','context:friends','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Cluj-Napoca', 'Centru', 46.76708, 23.59375, '2025-10-23T14:13:34.673Z', '2025-11-19T14:40:28.406Z', 'cluj-joben-steampunk-cocktails', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://jobenbistro.ro', 'Cocktailuri Steampunk la Joben', 'Cu toate fitingurile din alamă, spirale de cupru și manometre strălucitoare, Joben este un loc ascuns și fantastic, unde barmanii în bretele prepară cocktailuri înalte cristaline și semnături aromate și afumate bazate pe spirtoase românești. Încăperea vibrează la un volum conversațional - suflet vinil și electro-swing sub lumină ambră scăzută - în timp ce cupluri se ascund în cabine de catifea, iar grupuri mici gravitează în jurul barului lung pentru variațiuni alese de barman. Te poți aștepta la un public mixt și elegant: creativi, cupluri în seară de întâlnire, gurmanzi în trecere. Meniul cuprinde sour-uri rafinate, cocktailuri clasice clare și aperitive fără alcool; vesela este teatrală, dar serviciul rămâne cald și nepretențios. Este o etapă elegantă înainte de o noapte mare sau o destinație pentru cei care preferă rafinamentul în locul decibilor. Rezervările sunt recomandate în weekend; îmbracă-te smart-casual și lasă echipa de bar să povestească about construcția băuturii tale.', ARRAY['categorie:viata_noapte','energie:mediu','stare:sophisticated','stare:romantic','stare:luxe','stare:social','context:intalnire','context:grup_mic','context:friends','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1951, 'D''Arc Unirii Sessions', 'One of Timișoara''s longest-running nightlife anchors sits right on baroque Piața Unirii. Early evening, the terrace is the city''s living room; late, a compact, black-box interior kicks into house, disco edits, hip-hop and throwback rock. The mood is friendly and unpolished in the best way—workshop lights over brick, a tight bar pouring easy beer and classic mixed drinks, and a floor that fills fast when the DJ teases a 90s banger. Students mingle with professionals, pre-gaming expats, and weekenders city-tripping from Serbia and Hungary. Security is chill, staff are quick, and prices are gentle for a prime-square address. Dress code is whatever feels good; energy builds from 22:00 and often peaks around 01:30. If you want the full experience, start with golden-hour on the square and drift inside when the bassline thickens.', 'nightlife', NULL, NULL, NULL, 180, 330, ARRAY['category:nightlife','energy:high','mood:party','mood:social','mood:energetic','context:friends','context:small-group','context:large-group','context:solo','time_of_day:evening','time_of_day:night','time_of_day:late-night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Timișoara', 'Cetate', 45.757527, 21.22933, '2025-10-23T14:13:34.673Z', '2025-11-19T14:40:28.406Z', 'timisoara-darc-unirii-sessions', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://www.facebook.com/darc.timisoara', 'Sesiunile D''Arc Unirii', 'Unul dintre punctele fixe de viață nocturnă din Timișoara, situat chiar pe Piața Unirii barocă. Seara timpurie, terasa devine camera de zi a orașului; târziu, un interior compact de culoare neagră intră în ritm de house, disco edit-uri, hip-hop și rock clasic. Atmosfera este prietenească și nefinisată în cel mai bun sens - lumini de atelier pe ziduri de cărămidă, un bar mic care servește bere ușoară și cocktailuri clasice, iar pista de dans se umple repede când DJ-ul pregătește o piesă anilor ''90. Studenți se amestecă cu profesioniști, expatriați care fac pre-gaming și turiști de weekend care vin din Serbia și Ungaria. Controlul de securitate este relaxat, personalul este rapid, iar prețurile sunt blânde pentru o locație din centrul pieței. Codul vestimentar este ce te face să te simți bine; energia crește de la ora 22:00 și atinge punctul culminant în jurul orei 01:30. Pentru experiența completă, începe cu ora de aur pe piață și treci înăuntru când linia bas devine mai densă.', ARRAY['categorie:viata_noapte','energie:ridicat','stare:party','stare:social','stare:energetic','context:friends','context:grup_mic','context:large-group','context:solo','moment_zi:seara','moment_zi:noapte','moment_zi:late-night','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1958, 'Jazz & Blues Club Târgu Mureș', 'Tables dressed in warm lamplight, brick walls close enough to bounce a trumpet''s glow—this intimate club is Transylvania''s living room for swing, bebop, and slow-burn blues. On band nights, the room hushes to a murmur between solos; during jam sessions, it sparks into call-and-response whoops. The cocktail list leans classic—Manhattans, highballs, smoky old fashioneds—and the staff know when to slide in and when to vanish. The crowd is mixed ages: music students tuned to every note, couples on anniversaries, and travelers who wandered in and stayed for two sets. Dress smart-casual, snag a table near the pillar for best sightlines, and let the drummer''s brushes slow your breathing. After midnight, expect a looser second set where standards stretch and chatter rises with the bass.', 'nightlife', NULL, NULL, NULL, 120, 210, ARRAY['category:nightlife','energy:medium','mood:sophisticated','mood:romantic','mood:chill','context:date','context:small-group','context:friends','time_of_day:evening','time_of_day:night','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Târgu Mureș', 'Centru', 46.542964, 24.557858, '2025-10-23T14:13:34.677Z', '2025-11-19T14:40:28.406Z', 'targu-mures-jazz-and-blues-club', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://jazzandblues.ro', 'Jazz & Blues Club Târgu Mureș', 'Mese luminate de lămpi calde, pereți de cărămidă atât de aproape încât să reflecte strălucirea unei trompete - acest club intim este camera de zi a Transilvaniei pentru swing, bebop și blues cu ardere lentă. În serile cu formații, sala amuțește între solo-uri; în timpul jam session-urilor, izbucnește în strigăte de răspuns. Lista de cocktailuri este clasică - Manhattan, highball, vechi fashioned afumați - iar personalul știe când să apară și când să dispară. Publicul este amestecat ca vârstă: studenți de muzică atenți la fiecare notă, cupluri la aniversări și călători care au intrat întâmplător și au rămas pentru două reprize. Îmbrăcați-vă smart-casual, ocupați un loc lângă stâlp pentru cea mai bună vedere și lăsați-vă purtați de pensulele toboșarului. După miezul nopții, așteptați-vă la o a doua repriză mai lejeră în care standarde se întind și conversațiile cresc odată cu basul.', ARRAY['categorie:viata_noapte','energie:mediu','stare:sophisticated','stare:romantic','stare:relaxat','context:intalnire','context:grup_mic','context:friends','moment_zi:seara','moment_zi:noapte','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1968, 'Concert Night at Doors Club', 'Constanța''s most reliable stage for rock, alt-pop and singer-songwriter tours, Doors feels like a seaside home base: warm wood, good sightlines, and a sound tech who likes it punchy but clear. The calendar swings from indie darlings to cult Romanian legends; between sets the room softens into a friendly pub—locals swapping stories, students comparing playlists, travelers chasing one more song before the Black Sea breeze. It''s social more than scene: affordable drinks, fair tickets, and staff who''ll tip you to the next show.', 'nightlife', NULL, NULL, NULL, 150, 240, ARRAY['category:nightlife','energy:medium','mood:social','mood:chill','mood:party','context:friends','context:small-group','context:solo','time_of_day:evening','time_of_day:night','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Constanța', 'Str. Traian / Centru', 44.168333, 28.638611, '2025-10-23T14:16:35.296Z', '2025-11-19T14:40:28.406Z', 'romania-constanta-doors-club', ARRAY[]::text[], ARRAY[]::text[], NULL, 'large', 'mixed', 'any', 'moderate', 'https://doorsclub.ro', 'Seară de Concert la Doors Club', 'Cea mai sigură scenă din Constanța pentru rock, pop alternativ și turnee de cântăreți-compozitori, Doors pare o bază de acasă lângă mare: lemn cald, vizibilitate bună și un tehnician de sunet care preferă un sunet puternic dar clar. Programul variază de la idoli indie la legende românești de cult; între concerte, sala devine un pub prietenos—localnici schimbând povești, studenți comparând playliste, călători urmărind încă o melodie înainte de briza Mării Negre. Este mai social decât scenă: băuturi accesibile, bilete corecte și personal care te sfătuiește pentru următorul spectacol.', ARRAY['categorie:viata_noapte','energie:mediu','stare:social','stare:relaxat','stare:party','context:friends','context:grup_mic','context:solo','moment_zi:seara','moment_zi:noapte','pret:$']::text[]) ON CONFLICT DO NOTHING;
