INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (200, 'Boxing Gym Session (Pads & Bag)', 'Wrap your hands, learn stance, guard and footwork. You’ll work jab-cross-hook on pads and heavy bag rounds that burn lungs and sharpen focus. It’s a mental and physical reset: you leave sweaty, proud, and a little calmer because you hit something—on purpose and with form.', 'sports', ARRAY['all']::text[], 'indoor', 'high', 60, 90, ARRAY['category:sports','energy:high','experience_level:beginner','mood:adrenaline','mood:focused','terrain:urban','equipment:provided','context:solo','context:small-group','context:friends','requirement:booking-required','risk_level:moderate','weather_fit:all_weather','time_of_day:daytime','time_of_day:evening','travel_time_band:in-city','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'București', 44.44, 26.161, '2025-10-22T14:01:13.917Z', '2025-11-19T14:40:28.406Z', 'bucharest-boxing-gym-session', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.44,26.161', 'medium', 'locals', 'any', 'budget', 'https://absoluto.ro', 'Sesiune de Box (Labe și Sac)', 'Bandajează-ți mâinile, învață poziție, gardă și deplasare. Vei lucra lovituri directe pe labe și sac, care îți vor tăia respirația și vor ascuți concentrarea. Este o resetare mentală și fizică: pleci transpirат, mândru și puțin mai calm pentru că ai lovit ceva - intenționat și cu tehnică.', ARRAY['categorie:sport','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:focused','teren:urban','echipament:furnizat','context:solo','context:grup_mic','context:friends','cerinta:rezervare_necesara','nivel_risc:moderate','vreme: