INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (210, 'Muay Thai/Kickboxing Class', 'Shin guards on, stance set. You’ll drill teeps, round kicks, and knee strikes, then combine with hands into crisp combos. Pad rounds and light partner drills build timing and confidence. Expect a respectful vibe, sweat-soaked shirt, and that post-class endorphin glow.', 'sports', ARRAY['all']::text[], 'indoor', 'high', 60, 90, ARRAY['category:sports','energy:high','experience_level:beginner','mood:adrenaline','mood:focused','terrain:urban','equipment:rental-gear','context:solo','context:small-group','context:friends','requirement:booking-required','risk_level:moderate','weather_fit:all_weather','time_of_day:evening','time_of_day:daytime','travel_time_band:in-city','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'București', 44.425, 26.07, '2025-10-22T14:05:02.273Z', '2025-11-19T14:40:28.406Z', 'bucharest-kickboxing-class', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.425,26.070', 'medium', 'locals', 'any', 'budget', 'https://absoluto.ro', 'Clasă de Muay Thai/Kickboxing', 'Apărători de tibie fixate, poziție stabilă. Veți exersa teep-uri, lovituri circulare și lovituri cu genunchiul, apoi le veți combina cu loviturile de mână în serii precise. Runde cu pernițe și exerciții ușoare cu partener pentru a construi sincronizarea și încrederea. Expectă o atmosferă respectuoasă, tricou ud de transpirație și acea strălucire de endorfine după clasă.', ARRAY['categorie:sport','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:focused','teren:urban','echipament:inchiriere_echipament','context:solo','context:grup_mic','context:friends','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:orice_vreme','moment_zi:seara','moment_zi:zi','timp_calatorie:in_oras','abilitati:tehnica','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (212, 'Track Session (Iolanda Balaș-Soter)', 'Hit measured intervals on a real athletics track—400s, 800s, strides. The rubberized surface rewards good form; a watch and simple plan turn it into honest speed work. You finish gassed but satisfied, with a clear benchmark to beat next time.', 'sports', ARRAY['all']::text[], 'outdoor', 'high', 45, 75, ARRAY['category:sports','energy:high','experience_level:mixed','mood:focused','mood:adrenaline','terrain:urban','equipment:none','context:solo','context:small-group','requirement:booking-optional','risk_level:low','weather_fit:wind_sensitive','time_of_day:daytime','time_of_day:evening','travel_time_band:in-city','skills:endurance','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Bucharest', 'București', 44.471, 26.079, '2025-10-22T14:05:02.274Z', '2025-11-19T14:40:28.406Z', 'bucharest-track-running-iolanda', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.471,26.079', 'medium', 'locals', 'any', 'budget', 'https://fra.ro', 'Sesiune de Pistă (Iolanda Balaș-Soter)', 'Parcurgeți intervale măsurate pe o pistă adevărată de atletism - 400m, 800m, accelerări. Suprafața de cauciuc răsplătește tehnica bună; un ceas și un plan simplu îl transformă în lucru de viteză onest. Veți termina epuizați dar mulțumiți, cu un reper clar de bătut data viitoare.', ARRAY['categorie:sport','energie:ridicat','nivel_experienta:mixt','stare:focused','stare:adrenalina','teren:urban','echipament:none','context:solo','context:grup_mic','cerinta:booking-optional','nivel_risc:scazut','vreme:wind_sensitive','moment_zi:zi','moment_zi:seara','timp_calatorie:in_oras','abilitati