INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (746, 'Downhill MTB – Bike Resort Sinaia', 'Load the gondola, drop the saddle, and roll into berms carved under spruce canopy. Trails stack up from flowy blues to rooty, rock-littered reds where braking points and body position really matter. You learn to pump compressions, trust the bike through off-camber turns, and look ahead as speed rises. Chairlift laps turn practice into progress quickly. Rental shops sort full-face helmets and long-travel bikes so you can focus on the delicious combo of fear, finesse, and that post-run forearm burn.', 'sports', ARRAY['summer']::text[], 'outdoor', 'high', 120, 240, ARRAY['category:sports','energy:high','experience_level:intermediate','mood:adrenaline','mood:adventurous','mood:focused','terrain:mountain','terrain:forest','equipment:rental-gear','context:friends','context:small-group','requirement:booking-optional','requirement:lesson-recommended','risk_level:high','weather_fit:wet_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:balance','skills:technique','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Sinaia', 'Prahova', 45.347, 25.546, '2025-10-22T21:46:52.753Z', '2025-11-19T14:40:28.406Z', 'sinaia-bike-resort-downhill', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.347,25.546', 'medium', 'locals', 'any', 'budget', 'https://bikeresortsinaia.ro', 'Coborâre cu Mountain Bike-ul – Bike Resort Sinaia', 'Urcă cu telegondola, coboară șaua și intră în viraje sculptate sub o boltă de molizi. Traseele se stratifică de la albastru lin la roșu cu rădăcini și pietre unde punctele de frânare și poziția corpului contează cu adevărat. Înveți să lansezi în compresii, să ai încredere în bicicletă prin viraje dezaxate și să privești înainte pe măsură ce viteza crește. Turele cu scaunul rulant transformă rapid practica în progres. Magazinele de închirieri asigură căști integrale și biciclete cu cursă lungă pentru ca tu să te poți concentra pe combinația delicioasă de teamă, finețe și arsura plăcută de după coborâre.', ARRAY['categorie:sport','energie:ridicat','nivel_experienta:intermediar','stare:adrenalina','stare:adventurous','stare:focused','teren:munte','teren:padure','echipament:inchiriere_echipament','context:friends','context:grup_mic','cerinta:booking-optional','cerinta:lectie_recomandata','nivel_risc:ridicat','vreme:wet_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:balance','abilitati:tehnica','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1985, 'Premium Tennis at Stejarii Country Club', 'Hidden in the greenery north of Herăstrău, Stejarii delivers Bucharest''s most refined tennis experience. Immaculate clay and hard courts sit within a luxury wellness complex, where members enjoy professional coaching, pristine court maintenance, and attentive service. The atmosphere is executive and polished—expect well-groomed courts, quality lighting for evening play, and a clubhouse that blends sport with lifestyle. Pro shop, locker rooms, and post-match spa access complete the package.', 'sports', ARRAY['year-round']::text[], 'both', 'high', 60, 120, ARRAY['category:sports','energy:high','mood:focused','mood:sophisticated','context:date','context:friends','time_of_day:evening','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'București', 'Băneasa', 44.4929, 26.0752, '2025-11-13T09:38:30.248Z', '2025-11-19T14:40:28.406Z', 'bucharest-stejarii-tennis', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://stejariicountryclub.ro', 'Tenis Premium la Stejarii Country Club', 'Ascuns în verdeața din nordul Herăstrăului, Stejarii oferă cea mai rafinată experiență de tenis din București. Terenuri impecabile de zgură și hard, situate într-un complex de wellness de lux, unde membrii beneficiază de antrenament profesionist, întreținere desăvârșită a terenurilor și servicii attente. Atmosfera este executivă și elegantă - cu terenuri bine îngrijite, iluminat de calitate pentru jocuri de seară și un clubhouse care îmbină sportul cu stilul de viață. Magazin de echipamente, vestiare și acces la spa completează oferta.', ARRAY['categorie:sport','energie:ridicat','stare:focused','stare:sophisticated','context:intalnire','context:friends','moment_zi:seara','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1989, 'Parkside Tennis at North Tennis Herăstrău', 'Set beside Herăstrău Park, North Tennis offers quality courts in one of Bucharest''s most pleasant locations. Multiple outdoor surfaces with professional maintenance serve a mix of recreational and competitive players. The parkside setting brings fresh air and green views, while the facility maintains serious tennis standards—good lighting, net quality, and surface care. Coaching is available, and the club hosts regular social events and leagues.', 'sports', ARRAY['spring','summer','fall']::text[], 'outdoor', 'high', 60, 120, ARRAY['category:sports','energy:high','mood:social','mood:focused','context:friends','context:solo','time_of_day:evening','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'București', 'Herăstrău', 44.4758, 26.0856, '2025-11-13T09:38:30.253Z', '2025-11-19T14:40:28.406Z', 'bucharest-north-tennis-herastrau', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://northtennis.ro', 'Tenis Parkside la North Tennis Herăstrău', 'Situat lângă Parcul Herăstrău, North Tennis oferă terenuri de calitate într-una dintre cele mai plăcute locații din București. Suprafețe exterioare multiple cu întreținere profesională servesc o mixtură de jucători recreativi și competitivi. Amplasarea în parc aduce aer proaspăt și priveliști verzi, menținând în același timp standarde serioase de tenis - iluminat bun, calitatea fileului și îngrijirea suprafeței. Antrenamentul este disponibil, iar clubul găzduiește periodic evenimente sociale și ligi.', ARRAY['categorie:sport','energie:ridicat','stare:social','stare:focused','context:friends','co