INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1990, 'Community Tennis at BDK Tennis Herăstrău', 'Another excellent option near Herăstrău Park, BDK Tennis maintains quality courts with a welcoming, community-focused atmosphere. The facility serves regular players with well-maintained surfaces, reliable booking systems, and a friendly staff who know the regulars. Expect a mix of skill levels, from beginners taking lessons to experienced players in competitive matches. The club organizes social events, round-robins, and coaching clinics.', 'sports', ARRAY['spring','summer','fall']::text[], 'outdoor', 'high', 60, 120, ARRAY['category:sports','energy:high','mood:social','mood:focused','context:friends','context:solo','time_of_day:evening','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'București', 'Herăstrău', 44.4769, 26.0842, '2025-11-13T09:38:30.254Z', '2025-11-19T14:40:28.406Z', 'bucharest-bdk-tennis-herastrau', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://bdktennis.ro', 'Tenis Comunitar la BDK Tennis Herăstrău', 'O altă opțiune excelentă lângă Parcul Herăstrău, BDK Tennis menține terenuri de calitate cu o atmosferă primitoare, concentrată pe comunitate. Facilitatea deservește jucătorii obișnuiți cu suprafețe bine întreținute, sisteme de programare fiabile și un personal prietenos care îi cunoaște pe regulari. Veți întâlni un mix de nivele de abilitate, de la începători care iau lecții la jucători experimentați în meciuri competitive. Clubul organizează evenimente sociale, turnee și clinic de antrenament.', ARRAY['categorie:sport','energie:ridicat','stare:social','stare:focused','context:friends','context:solo','moment