INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1991, 'National Training at Centrul Național de Tenis', 'Romania''s national tennis center opens to the public for court bookings, offering professional-grade facilities where national team players train. Multiple indoor and outdoor courts feature excellent surfaces, professional lighting, and tournament-standard maintenance. The atmosphere is serious and focused—expect quality play and well-organized operations. Coaching from experienced professionals is available, and the facility hosts regular tournaments and events.', 'sports', ARRAY['year-round']::text[], 'both', 'high', 60, 120, ARRAY['category:sports','energy:high','mood:focused','mood:energetic','context:friends','context:solo','time_of_day:evening','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'București', 'Progresul', 44.4156, 26.1389, '2025-11-13T09:38:30.255Z', '2025-11-19T14:40:28.406Z', 'bucharest-centrul-national-tenis', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://frt.ro', 'Antrenament Național la Centrul Național de Tenis', 'Centrul național de tenis al României este deschis publicului pentru programări la terenuri, oferind facilități de nivel profesionist unde antrenează echipa națională. Mai multe terenuri în interior și exterior cu suprafețe excelente, iluminat profesionist și întreținere la standarde de turneu. Atmosfera este serioasă și concentrată - veți găsi joc de calitate și operațiuni bine organizate. Antrenamentul de la profesioniști experimentați este disponibil, iar facilitatea găzduiește turnee și evenimente regulate.', ARRAY['categorie:sport','energie:ridicat','stare:focused','stare:energe