INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1992, 'Western Tennis at Tenis Timișoara', 'Just outside Timișoara in Moșnița Nouă, this tennis complex offers quality courts in a quieter setting. Multiple outdoor surfaces with good maintenance serve both casual players and competitive members. The facility provides coaching, equipment rental, and organized leagues. The atmosphere is relaxed yet focused—expect friendly matches and regular players who appreciate the less urban setting.', 'sports', ARRAY['spring','summer','fall']::text[], 'outdoor', 'high', 60, 120, ARRAY['category:sports','energy:high','mood:social','mood:focused','context:friends','context:solo','time_of_day:evening','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Moșnița Nouă', 'Timiș', 45.7892, 21.1547, '2025-11-13T09:38:30.256Z', '2025-11-19T14:40:28.406Z', 'timisoara-tenis-mosnita', ARRAY[]::text[], ARRAY[]::text[], NULL, NULL, NULL, NULL, NULL, 'https://tenistimisoara.ro', 'Tenis Vestic la Tenis Timișoara', 'Chiar în afara Timișoarei, în Moșnița Nouă, acest complex de tenis oferă terenuri de calitate într-o zonă mai liniștită. Suprafețe exterioare multiple cu întreținere bună servesc atât jucători casual, cât și membri competitivi. Facilitatea oferă antrenament, închiriere de echipamente și ligi organizate. Atmosfera este relaxată dar concentrată - veți găsi meciuri prietenești și jucători obișnuiți care apreciază mediul mai puțin urban.', ARRAY['categorie:sport','energie:ridicat','stare:social','stare:focused','context:friends','context:solo','moment_zi:seara','pret:$$']::text[]) ON CONFLICT DO NOTHING;
