INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (236, 'Windsurfing Intro (Mamaia)', 'Learn up-hauling, basic stance, and how to sheet in/out to catch wind. You’ll wobble, laugh, then suddenly cruise—wind in sail, eyes wide. In light breezes it’s forgiving; in gusts it’s thrilling. Progress comes fast and feels amazing.', 'water', ARRAY['summer']::text[], 'outdoor', 'high', 90, 120, ARRAY['category:water','energy:high','experience_level:beginner','mood:adventurous','mood:adrenaline','terrain:coast','terrain:resort','equipment:rental-gear','context:solo','context:small-group','requirement:booking-required','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:in-city','skills:balance','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Mamaia', 'Constanța', 44.264, 28.62, '2025-10-22T14:05:02.286Z', '2025-11-19T14:40:28.406Z', 'mamaia-windsurf-intro', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.264,28.620', 'medium', 'mixed', 'any', 'moderate', 'https://windsurfing1.ro', 'Inițiere în Windsurf (Mamaia)', 'Veți învăța ridicarea velei, poziția de bază și cum să prindeți vântul. Veți ezita, veți râde, apoi deodată veți aluneca—vântul în velă, ochii larg deschiși. În brize ușoare este iertător; în rafale este incitant. Progresul vine rapid și simți o bucurie uriașă.', ARRAY['categorie:apa','energie:ridicat','nivel_experienta:incepator','stare:adventurous','stare:adrenalina','teren:coasta','teren:resort','echipament:inchiriere_echipament','context:solo','context:grup_mic','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:in_oras','abilitati:balance','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (276, 'Traditional Rowing (Canotcă) Basics', 'Two oars, long wooden boat, mirrored strokes with a partner. You''ll learn blade entry and smooth recovery so the hull glides straight. It’s rhythmic and social—work and glide, work and glide—leaving you pleasantly tired and oddly serene.', 'water', ARRAY['summer']::text[], 'outdoor', 'high', 90, 120, ARRAY['category:water','energy:high','experience_level:beginner','mood:explorer','mood:social','mood:focused','terrain:delta','terrain:river','equipment:provided','context:small-group','context:friends','requirement:booking-required','risk_level:low','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:technique','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Tulcea', 'Tulcea', 45.175, 28.804, '2025-10-22T20:00:20.901Z', '2025-11-19T14:40:28.406Z', 'tulcea-rowmania-canotca', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.175,28.804', 'medium', 'mixed', 'any', 'moderate', 'https://rowmania.ro', 'Canotcă Tradițională — Noțiuni de Bază', 'Două vâsle, barcă lungă din lemn, lovituri oglindă cu un partener. Veți învăța tehnica intrării și recuperării astfel încât barca să alunece lin. Este ritmic și social—lucrezi și aluneci, lucrezi și aluneci—terminând plăcut obosit și ciudat de calm.', ARRAY['categorie:apa','energie:ridicat','nivel_experienta:incepator','stare:explorator','stare:social','stare:focused','teren:delta','teren:river','echipament:furnizat','context:grup_mic','context:friends','cerinta:rezervare_necesara','nivel_risc:scazut','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:tehnica','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (319, 'Jet Ski Bursts on Lake Siutghiol (JT Watersports Mamaia)', 'Thumb the throttle and feel the bow lift as you carve broad, fast arcs across calm lagoon water. Staff run through kill-switch, safety zones, and hand signals first; then it''s you reading chop, setting lean angles, and trusting your balance while spray hits your cheeks. Short, intense sets deliver that hit of speed and control—enough adrenaline to reset your brain without eating your whole day at the beach. Pairs can swap pilot/passenger to share the fun.', 'water', ARRAY['summer']::text[], 'outdoor', 'high', 15, 30, ARRAY['category:water','energy:high','experience_level:beginner','mood:adrenaline','mood:social','mood:adventurous','terrain:coast','terrain:lake','terrain:urban','equipment:rental-gear','context:date','context:friends','context:small-group','requirement:booking-required','requirement:lesson-recommended','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','time_of_day:sunset','travel_time_band:in-city','skills:balance','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Constanța', 'Constanța', 44.26, 28.63, '2025-10-22T21:28:05.316Z', '2025-11-19T14:40:28.406Z', 'mamaia-jt-watersports-jet-ski', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.2600,28.6300', 'medium', 'mixed', 'any', 'moderate', 'https://jtwatersports.ro', 'Curse cu Jet Ski pe Lacul Siutghiol (JT Watersports Mamaia)', 'Apasă accelerația și simte prova ridicându-se în timp ce desenezi arcade largi și rapide peste apa liniștită a lagunei. Personalul explică mai întâi sistemul de oprire de urgență, zonele de siguranță și semnalele cu mâna; apoi rămâi tu, citind suprafața apei, stabilind unghiuri de înclinare și încrezător în echilibrul tău în timp ce spray-ul îți lovește obrajii. Seturi scurte și intense oferă acel impuls de viteză și control - suficient de mult adrenalină pentru a-ți reseta creierul fără să-ți consume toată ziua de plajă. Perechile pot schimba rolurile de pilot/pasager pentru a împ[ărți distracția.', ARRAY['categorie:apa','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:social','stare:adventurous','teren:coasta','teren:lac','teren:urban','echipament:inchiriere_echipament','context:intalnire','context:friends','context:grup_mic','cerinta:rezervare_necesara','cerinta:lectie_recomandata','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','moment_zi:sunset','timp_calatorie:in_oras','abilitati:balance','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (738, 'Buzău River Whitewater Rafting (Class III–IV) with Green Adventure', 'Gear up, practice paddle commands on calm water, then punch through standing waves and funneling chutes on the Buzău. You''ll lean forward into spray, listen for your guide''s forward hard! and feel the raft surge as your crew times strokes together. Between rapids you float through forested banks, catching your breath and adrenaline in equal measure. It''s hands-on, social, and satisfying—everyone contributes, everyone cheers the clean line. Expect a safety briefing, helmets and wetsuits, swim practice, and progressive rapids that build confidence before the big drops.', 'water', ARRAY['summer']::text[], 'outdoor', 'high', 180, 300, ARRAY['category:water','energy:high','experience_level:mixed','mood:adrenaline','mood:adventurous','mood:explorer','mood:social','terrain:river','terrain:mountain','equipment:provided','context:small-group','context:friends','context:team','requirement:guide-required','requirement:booking-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:technique','skills:endurance','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Nehoiu', 'Buzău', 45.42, 26.305, '2025-10-22T21:46:52.747Z', '2025-11-19T14:40:28.406Z', 'nehoiu-buzau-rafting-green-adventure', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.420,26.305', 'medium', 'mixed', 'any', 'moderate', 'https://greenadventure.ro', 'Rafting pe Râul Buzău (Clasa III–IV) cu Green Adventure', 'Echipează-te, exersează comenzile de vâslit pe apă calmă, apoi lovește valurile statice și canalele înguste de pe Buzău. Vei apleca corpul în spray, vei asculta comenzile ghidului ''înainte tare!'' și vei simți pluta accelerând în momentul în care echipa sincronizează loviturile. Între rapids plutești prin maluri împădurite, recuperând adrenalina și respirația în egală măsură. Este un proces hands-on, social și satisfăcător - toată lumea contribuie, toată lumea aplaudă linia curată. Vei primi un briefing de siguranță, căști, costume de neopren, pregătire pentru înot și rapids progresive care construiesc încrederea înaintea coborârilor mari.', ARRAY['categorie:apa','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:adventurous','stare:explorator','stare:social','teren:river','teren:munte','echipament:furnizat','context:grup_mic','context:friends','context:team','cerinta:ghid_necesar','cerinta:rezervare_necesara','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:tehnica','abilitati:endurance','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (745, 'Kitesurfing Lessons – H2O Kiteboarding Mamaia', 'On the sand you learn kite control with a trainer, then wade into waist-deep water to feel power build through the bar. Your coach shouts cues—edge, sheet in, stand—and suddenly you''re skimming over warm Black Sea chop. Crashes are part of the fun; fast resets and focused drills turn them into progress. It''s technical, athletic, and addictive: the pull of the kite against your harness, the board releasing onto plane, the first down-the-line glide that feels like flying at water level.', 'water', ARRAY['summer']::text[], 'both', 'high', 120, 180, ARRAY['category:water','energy:high','experience_level:beginner','mood:adrenaline','mood:focused','mood:adventurous','mood:social','terrain:coast','terrain:urban','equipment:rental-gear','context:solo','context:small-group','context:friends','requirement:booking-required','requirement:lesson-recommended','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','time_of_day:sunset','travel_time_band:day-trip','skills:balance','skills:technique','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Mamaia', 'Constanța', 44.279, 28.622, '2025-10-22T21:46:52.752Z', '2025-11-19T14:40:28.406Z', 'mamaia-h2o-kite-kitesurf-lessons', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.279,28.622', 'medium', 'mixed', 'any', 'moderate', 'https://h2oteam.ro', 'Lecții de Kitesurfing – H2O Kiteboarding Mamaia', 'Pe nisip, înveți controlul zmeului cu un antrenor, apoi intri în apă până la brâu pentru a simte cum crește puterea prin bară. Antrenorul strigă comenzi - marginea, trage, ridică-te - și brusc aluneci peste valurile calde ale Mării Negre. Căderile sunt parte din distracție; resetări rapide și exerciții concentrate le transformă în progres. Este tehnic, atletic și adictiv: tragerea zmeului în ham, placa eliberată în plan, primul alunecat lin care seamănă cu zborul la nivelul apei.', ARRAY['categorie:apa','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:focused','stare:adventurous','stare:social','teren:coasta','teren:urban','echipament:inchiriere_echipament','context:solo','context:grup_mic','context:friends','cerinta:rezervare_necesara','cerinta:lectie_recomandata','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','moment_zi:sunset','timp_calatorie:excursie_zi','abilitati:balance','abilitati:tehnica','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (801, 'Full-Size Cable Wakeboarding at A2Wake (km 49 A2)', 'Grab a board, get a quick dockside coaching tip, and launch as the cable picks you up. First laps focus on stable stance and smooth corners; then the fun escalates—flatwater carves, surface 180s, and eventually features if you want airtime. Sessions are short, punchy, and addictive, with staff resetting you for rapid tries. It''s a stellar high-energy escape just outside Bucharest, with rentals and beginner coaching on tap.', 'water', ARRAY['summer']::text[], 'outdoor', 'high', 60, 120, ARRAY['category:water','energy:high','experience_level:beginner','mood:adrenaline','mood:social','mood:focused','terrain:lake','terrain:resort','terrain:urban','equipment:rental-gear','context:solo','context:friends','context:group','requirement:booking-optional','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','time_of_day:evening','travel_time_band:nearby','skills:balance','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Sărulești-Gară', 'Călărași', 44.44444, 26.6578, '2025-10-22T21:54:01.819Z', '2025-11-19T14:40:28.406Z', 'sarulesti-a2wake-cable-wakeboard', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.44444,26.65780', 'medium', 'mixed', 'any', 'moderate', 'https://a2wake.ro', 'Wakeboarding pe Cablu la Dimensiune Completă la A2Wake (km 49 A2)', 'Luați o placă, primiți un sfat scurt de coaching la doc și lansați-vă când cablul vă preia. Primele tururi se concentrează pe poziție stabilă și colțuri line; apoi distracția crește - tăieri pe apă, 180 de grade la suprafață și eventual elemente dacă doriți salt. Sesiunile sunt scurte, intense și care creează dependență, cu personalul care vă repoziționa pentru încercări rapide. Este o modalitate strălucitoare de evadare cu energie ridicată chiar și în afara Bucureștiului, cu echipament de închiriat și instruire pentru începători la îndemână.', ARRAY['categorie:apa','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:social','stare:focused','teren:lac','teren:resort','teren:urban','echipament:inchiriere_echipament','context:solo','context:friends','context:grup','cerinta:booking-optional','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','moment_zi:seara','timp_calatorie:aproape','abilitati:balance','pret:$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (803, 'Black Sea Intro Dive with Marine Explorers', 'Suit up, weight belt snug, and practice breathing from a regulator in shallow water before following your instructor along the seabed. You''ll focus on slow fin kicks and buoyancy while spotting shells and fish near Constanța. The sensations are singular: sound muted, bubbles rising, body feather-light. It''s a controlled, high-attention experience that leaves you mentally buzzing and physically calm. All equipment and briefings included—just bring swimwear and curiosity.', 'water', ARRAY['summer']::text[], 'outdoor', 'high', 90, 120, ARRAY['category:water','energy:high','experience_level:beginner','mood:adrenaline','mood:explorer','mood:focused','terrain:coast','terrain:urban','equipment:provided','context:solo','context:small-group','context:friends','requirement:booking-required','risk_level:moderate','weather_fit:wind_sensitive','time_of_day:daytime','time_of_day:morning','travel_time_band:in-city','skills:none_required','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Constanța', 'Constanța', 44.179249, 28.64994, '2025-10-22T21:54:01.821Z', '2025-11-19T14:40:28.406Z', 'constanta-marine-explorers-scuba-intro', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.179249,28.649940', 'medium', 'mixed', 'any', 'moderate', 'https://marineexplorers.ro', 'Scufundare introductivă la Marea Neagră cu Exploratori Marini', 'Îmbracă echipamentul, centura de greutăți strânsă, și exersează respirația cu regulatorul în apă puțin adâncă, înainte de a urma instructorul pe fundul mării. Vei pune accent pe mișcări lente de înotat și menținerea echilibrului, observând scoici și pești lângă Constanța. Senzațiile sunt unice: sunet estompat, bule care se ridică, corp ușor ca o pană. Este o experiență controlată, cu atenție maximă, care te lasă mental energizat și fizic calm. Tot echipamentul și briefingurile sunt incluse - adu doar costum de baie și curiozitate.', ARRAY['categorie:apa','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:explorator','stare:focused','teren:coasta','teren:urban','echipament:furnizat','context:solo','context:grup_mic','context:friends','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:wind_sensitive','moment_zi:zi','moment_zi:dimineata','timp_calatorie:in_oras','abilitati:none_required','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
