INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1018, 'Therme București Full-Day Thermal Spa', 'Float in warm mineral pools under a glass dome, wander steam rooms and themed saunas, then cool off in outdoor lagoons fringed with palms. You move between heat and cold, pools and loungers, until your body feels unknotted and your mind quiet. Pick your zone (Galaxy, The Palm, Elysium) and make it a slow, sensory reset—solo or with friends.', 'wellness', ARRAY['all']::text[], 'both', 'low', 180, 540, ARRAY['category:wellness','energy:low','experience_level:beginner','mood:relaxed','mood:cozy','mood:mindful','mood:social','terrain:resort','terrain:urban','equipment:provided','context:solo','context:date','context:friends','context:small-group','requirement:booking-required','risk_level:low','weather_fit:all_weather','time_of_day:daytime','time_of_day:evening','travel_time_band:nearby','skills:none_required','cost_band:$$']::text[], NULL, ARRAY[]::text[], 'Balotești', 'Ilfov', 44.605431, 26.084768, '2025-10-22T22:10:09.127Z', '2025-11-19T14:40:28.406Z', 'balotesti-therme-bucharest-full-day', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=44.605431,26.084768', 'intimate', 'locals', 'solo-friendly', 'premium', 'https://therme.ro', 'Spa-ul Termal Therme București - Zi Întreagă', 'Plutești în bazine minerale calde sub o cupolă de sticlă, rătăcești prin camere de aburi și saune tematice, apoi te răcorești în lagune exterioare marginite de palmieri. Te deplasezi între căldură și frig, bazine și șezlonguri, până când corpul tău se destinde și mintea se liniștește. Alege-ți zona (Galaxy, The Palm, Elysium) și transformă-o într-o resetare lentă, senzorială - singur sau cu prietenii.', ARRAY['categorie:wellness','energie:scazut','nivel_experienta:incepator','stare:relaxat','stare:confortabil','stare:contemplativ','stare:social','teren:resort','teren:urban','echipament:furnizat','context:solo','context:intalnire','context:friends','context:grup_mic','cerinta:rezervare_necesara','nivel_risc:scazut','vreme:orice_vreme','moment_zi:zi','moment_zi:seara','timp_calatorie:aproape','abilitati:none_required','pret:$$']::text[]) ON CONFLICT DO NOTHING;
