INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (1020, 'Ensana Sovata Spa & Saltwater Pools', 'Bathe in saltwater pools inspired by nearby Bear Lake, cycle through Kneipp paths and saunas, then finish with a massage. It''s slow, ritual care: warm, floaty water supports your body while the spa team works out the knots. You''ll walk out lighter, sleep deeper.', 'wellness', ARRAY['all']::text[], 'indoor', 'low', 120, 240, ARRAY['category:wellness','energy:low','experience_level:beginner','mood:relaxed','mood:mindful','mood:cozy','terrain:lake','terrain:resort','terrain:forest','equipment:provided','context:solo','context:date','context:small-group','requirement:booking-required','risk_level:low','weather_fit:all_weather','time_of_day:daytime','time_of_day:evening','travel_time_band:day-trip','skills:none_required','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Sovata', 'Mureș', 46.5989, 25.0746, '2025-10-22T22:10:09.130Z', '2025-11-19T14:40:28.406Z', 'sovata-ensana-bear-lake-spa-day', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.598900,25.074600', 'intimate', 'locals', 'solo-friendly', 'premium', 'https://ensanahotels.com/sovata', 'Spa și Bazine cu Apă Sărată Ensana Sovata', 'Înoată în bazine cu apă sărată inspirate de Lacul Urșilor din apropiere, treci prin circuite Kneipp și saune, apoi încheie cu un masaj. Este o îngrijire lentă, rituală: apa caldă, plutitoare, îți susține corpul în timp ce echipa spa-ului dezleagă nodurile. Vei ieși mai ușor și vei dormi mai adânc.', ARRAY['categorie:wellness','energie:scazut','nivel_experienta:incepator','stare:relaxat','stare:contemplativ','stare:confortabil','teren:lac','teren:resort','teren:padure','echipament:furnizat','context:solo','context:intalnire','context:grup_mic','cerinta:rezervare_necesara','nivel_risc:scazut','vreme:orice_vreme','moment_zi:zi','moment_zi:seara','timp_calatorie:excursie_zi','abilitati:none_required','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (748, 'Backcountry Snowmobile Tour – Harghita-Mădăraș', 'After a quick throttle and safety lesson, you ease onto groomed snow then squeeze the trigger and feel the sled surge. Forest tracks open into white meadows where you weave gentle S-turns, then climb toward viewpoints with a guide setting safe spacing and reading snowpack. It''s speed, scenery, and skill progression—how to weight the sled, handle sidehills, and stop with control. Breaks mean hot tea and frosty breath photos; the ride back is confident, faster, and ridiculously fun.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 90, 150, ARRAY['category:winter','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:explorer','mood:social','terrain:mountain','terrain:forest','equipment:rental-gear','context:friends','context:small-group','requirement:guide-required','requirement:booking-required','risk_level:moderate','weather_fit:cold_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:balance','skills:technique','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Mădăraș', 'Harghita', 46.436, 25.592, '2025-10-22T21:46:52.754Z', '2025-11-19T14:40:28.406Z', 'madaras-harghita-snowmobile', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.436,25.592', 'medium', 'mixed', 'any', 'moderate', 'https://madaras-snowmobile.ro', 'Tur cu Snowmobilul în Teren Neprelucrat – Harghita-Mădăraș', 'După o scurtă lecție de accelerație și siguranță, ieși pe zăpada netezită, apoi apesi accelerația și simți snowmobilul zvâcnind. Traseele prin pădure se deschid în poieni albe unde desenezi S-uri line, apoi urci către puncte de vedere cu un ghid care stabilește spații sigure și citește stratul de zăpadă. Este vorba de viteză, peisaj și progresie în abilități - cum să orientezi snowmobilul, cum să treci pe pantele înclinate și cum să te oprești controlat. Pauzele înseamnă ceai fierbinte și fotografii cu răsuflarea înghețată; întoarcerea este plină de încredere, mai rapidă și incredibil de distractivă.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:explorator','stare:social','teren:munte','teren:padure','echipament:inchiriere_echipament','context:friends','context:grup_mic','cerinta:ghid_necesar','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:cold_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:balance','abilitati:tehnica','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (860, 'Făgăraș Ski Touring Week (High Alpine)', 'Five heavy days above treeline: early skins on refrozen crust, kick turns through wind lips, then long powder descents linking cirques. Your guide handles route-finding and hazard checks; you handle steady pacing and clean transitions. Nights are spent at mountain chalets; days are a rhythm of climb, assess, descend. It''s physical, exposed, and wildly rewarding—summit cols near 2,500m, legs humming, lungs sharp, and the rush of pulling into safe islands after committing pitches. You finish with tuned technique, smarter terrain eyes, and a deep Făgăraș respect.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 4320, 7200, ARRAY['category:winter','energy:high','experience_level:intermediate','mood:adrenaline','mood:adventurous','mood:explorer','mood:focused','terrain:mountain','terrain:cliff','equipment:bring-own','context:small-group','context:friends','requirement:guide-required','risk_level:exposed','weather_fit:wind_sensitive','time_of_day:daytime','time_of_day:sunrise','travel_time_band:weekend-trip','skills:endurance','skills:technique','skills:navigation','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Cârțișoara', 'Sibiu', 45.604, 24.616, '2025-10-22T21:59:34.460Z', '2025-11-19T14:40:28.406Z', 'cartisoara-fagaras-ski-touring-week-exploreshare', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.604,24.616', 'medium', 'mixed', 'any', 'moderate', 'https://mountainguide.ro', 'Săptămână de Schi cu Schi Montan în Făgăraș (Alpin Înalt)', 'Cinci zile intense deasupra limitei pădurii: urcare cu schiurile dimineața pe crustă înghețată, schimbări de direcție prin buzele de vânt, apoi lungi coborâri în praf de zăpadă care leagă circurile. Ghidul tău se ocupă de găsirea rutei și verificarea pericolelor; tu te ocupi de un ritm constant și tranziții curate. Nopțile sunt petrecute în cabane montane; zilele au un ritm de urcare, evaluare, coborâre. Este fizic, expus și incredibil de răsplătitor - culmi de summit aproape de 2.500m, picioare care vibrează, plămâni ascuțiți și senzația de siguranță atunci când ajungi în zone sigure după porțiuni dificile. Vei termina cu tehnică perfecționată, ochi mai deștepți pentru teren și un respect profund pentru Făgăraș.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:intermediar','stare:adrenalina','stare:adventurous','stare:explorator','stare:focused','teren:munte','teren:cliff','echipament:bring-own','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:exposed','vreme:wind_sensitive','moment_zi:zi','moment_zi:sunrise','timp_calatorie:weekend-trip','abilitati:endurance','abilitati:tehnica','abilitati:navigation','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (861, 'Retezat Backcountry Ski (6-Day)', 'Load skins at Râușor and climb into granite bowls of Retezat National Park. You''ll alternate 700–1000 m vertical days: long, methodical ascents, kick turns through sparse spruce, and satisfying fall-line descents to silent valleys. Guides choose lines to match the group, drilling safe spacing, hand signals, and quick-probe decisions. Expect lactic legs, cold air on steep bootpacks, and that shot of adrenaline when the slope opens and your tips plane on soft snow. You''ll leave with stronger legs, tighter transitions, and confidence on real mountain terrain.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 3600, 5400, ARRAY['category:winter','energy:high','experience_level:mixed','mood:adrenaline','mood:adventurous','mood:explorer','mood:focused','terrain:mountain','terrain:forest','equipment:bring-own','context:small-group','context:friends','requirement:guide-required','risk_level:high','weather_fit:cold_sensitive','time_of_day:daytime','travel_time_band:weekend-trip','skills:endurance','skills:navigation','skills:technique','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Râușor', 'Hunedoara', 45.381, 22.849, '2025-10-22T21:59:34.461Z', '2025-11-19T14:40:28.406Z', 'rausor-retezat-backcountry-6day-exploreshare', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.381,22.849', 'medium', 'mixed', 'any', 'moderate', 'https://mountainguide.ro', 'Schi în Zona Sălbatică din Retezat (6 Zile)', 'Încarcă schiurile la Râușor și urcă în bazinele de granit ale Parcului Național Retezat. Vei alterna zile de 700-1000 m verticali: urcări lungi și metodice, schimbări de direcție printre molizii rari și coborâri satisfăcătoare pe linia de cădere spre văi tăcute. Ghizii aleg traseele potrivite grupului, exersând distanțare sigură, semnale și decizii rapide. Pregătește-te pentru picioare pline de acid, aer rece pe urcări abrupte și acea doză de adrenalină când panta se deschide și vârfurile schiurilor alunecă pe zăpadă moale. Vei pleca cu picioare mai puternice, tranziții mai precise și încredere pe teren muntos real.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:mixt','stare:adrenalina','stare:adventurous','stare:explorator','stare:focused','teren:munte','teren:padure','echipament:bring-own','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:ridicat','vreme:cold_sensitive','moment_zi:zi','timp_calatorie:weekend-trip','abilitati:endurance','abilitati:navigation','abilitati:tehnica','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (862, 'Harghita Husky Dog-Sledding Ride', 'Clip into a sled, feel the jerk as the huskies surge, then settle into a hypnotic glide over snowy meadows and forest tracks. You learn voice commands and foot-brake pressure, working with the team to handle corners and climbs. It''s physically engaging—balancing, bracing, and sometimes jogging to help on rises—and viscerally thrilling as speed builds and breath plumes in the cold. You finish buzzing from teamwork, with frosty eyelashes, warm dogs to pat, and a new respect for winter travel by muscle and instinct.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 60, 120, ARRAY['category:winter','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:social','mood:explorer','terrain:forest','terrain:valley','equipment:provided','context:small-group','context:friends','context:date','requirement:booking-required','risk_level:moderate','weather_fit:cold_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:balance','skills:technique','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Ciumani', 'Harghita', 46.7, 25.45, '2025-10-22T21:59:34.462Z', '2025-11-19T14:40:28.406Z', 'ciumani-harghita-dog-sledding-husky-tours', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.700,25.450', 'medium', 'mixed', 'any', 'moderate', 'https://transylvanianhusky.com', 'Plimbare cu Sania trasă de Câini Husky în Harghita', 'Prinde-te de sanie, simte smucirea când huskii pornesc, apoi intrând într-o alunecare hipnotică peste pajiști și trasee înzăpezite. Vei învăța comenzi vocale și presiunea frânei, lucrând cu echipa pentru a gestiona colțurile și urcările. Este fizic antrenant - echilibrându-te, sprijinindu-te și uneori alergând pentru a ajuta la urcări - și visceral emoționant pe măsură ce viteza crește și respirația se transformă în aburi în frig. Vei termina energizat de lucrul în echipă, cu gene înghețate, câini calzi de mângâiat și un nou respect pentru călătoria de iarnă prin mușchi și instinct.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:social','stare:explorator','teren:padure','teren:valley','echipament:furnizat','context:grup_mic','context:friends','context:intalnire','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:cold_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:balance','abilitati:tehnica','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (863, 'Azuga Guided Snowmobile Ride', 'Straddle a powerful sled, thumb the throttle, and feel the track bite as you climb snowy forest roads to panoramic ridgelines. An instructor briefs throttle control, safe spacing, and body-lean for turns before you set off in a convoy. Expect arm pump on steep sections, icy rooster tails, and photo breaks above the Prahova Valley. It''s pure motorized adrenaline with mountain air in your lungs and a grin you can''t shake.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 60, 120, ARRAY['category:winter','energy:high','experience_level:beginner','mood:adrenaline','mood:adventurous','mood:social','mood:focused','terrain:mountain','terrain:forest','equipment:rental-gear','context:small-group','context:friends','requirement:booking-required','risk_level:moderate','weather_fit:cold_sensitive','time_of_day:daytime','time_of_day:evening','travel_time_band:day-trip','skills:balance','skills:technique','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Azuga', 'Prahova', 45.445, 25.556, '2025-10-22T21:59:34.462Z', '2025-11-19T14:40:28.406Z', 'azuga-snowmobile-mountain-ride-quadromania', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.445,25.556', 'medium', 'mixed', 'any', 'moderate', 'https://snowmobile-romania.ro', 'Plimbare ghidată cu snowmobilul în Azuga', 'Urcă pe un snowmobil puternic, apasă accelerația și simte șenila mordând zăpada pe drumurile forestiere până la creștetele cu panoramă. Un instructor te inițiază în controlul accelerației, distanțarea sigură și poziționarea corpului pentru viraje, înainte de a porni în convoi. Pregătește-te pentru efort la urcări abrupte, valuri de gheață și pauze pentru fotografii deasupra Văii Prahovei. Este curată adrenalină motorizată, cu aer de munte în plămâni și un zâmbet care nu dispare.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:adventurous','stare:social','stare:focused','teren:munte','teren:padure','echipament:inchiriere_echipament','context:grup_mic','context:friends','cerinta:rezervare_necesara','nivel_risc:moderate','vreme:cold_sensitive','moment_zi:zi','moment_zi:seara','timp_calatorie:excursie_zi','abilitati:balance','abilitati:tehnica','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (864, 'Bâlea Ice Climbing Intro', 'On frozen waterfalls near Bâlea you''ll swing tools, kick front points, and trust steel on hard ice. Guides demo efficient placements and how to read blue vs. aerated ice; you practice clipping screws, resting on straight arms, and controlled lowers. The sensation is primal—thunk, crunch, breathe—while calves burn and focus narrows to your next placement. By day''s end you''ll top-rope steeper lines with confidence and understand anchor etiquette and cold-weather systems.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 300, 420, ARRAY['category:winter','energy:high','experience_level:beginner','mood:adrenaline','mood:focused','mood:explorer','mood:adventurous','terrain:mountain','terrain:cliff','equipment:provided','context:small-group','context:friends','requirement:guide-required','risk_level:high','weather_fit:cold_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:technique','skills:strength','skills:balance','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Cârțișoara', 'Sibiu', 45.604, 24.617, '2025-10-22T21:59:34.463Z', '2025-11-19T14:40:28.406Z', 'cartisoara-balea-ice-climbing-alpine-tours', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.604,24.617', 'medium', 'mixed', 'any', 'moderate', 'https://zaikoadventures.com', 'Inițiere în Escalada pe Gheață la Bâlea', 'Pe cascade înghețate lângă Bâlea, vei folosi uneltele, lovi cu vârfurile și te vei încrede în oțel pe gheață tare. Ghizii demonstrează plasări eficiente și cum să citești gheața albastră versus poroasă; tu exersezi prinderea șuruburilor, odihna pe brațe drepte și coborâri controlate. Senzația este primară - lovitură, sfărâmare, respirație - în timp ce gambele ard și focalizarea se îngustează la următoarea poziție. Până la finalul zilei, vei urca pe linii din ce în ce mai abrupte cu încredere și vei înțelege etica punctelor de ancorare și sistemele pentru vreme rece.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:focused','stare:explorator','stare:adventurous','teren:munte','teren:cliff','echipament:furnizat','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:ridicat','vreme:cold_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:tehnica','abilitati:strength','abilitati:balance','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (865, 'Făgăraș Winter Mountaineering Weekend', 'Crampon across bullet-hard neve, front-point short steps, and move roped along airy ridges under a watchful guide. You''ll practice ice-axe arrest, cornice awareness, and safe belays on mixed ground. Progress is steady and absorbing—breath fogging in cold wind, fingers learning fine motor skills in gloves, mind locked on footwork. Expect exposure and exhilaration as you step over cols with 2,000-m valleys yawning below. You finish tired, sharper, and proud of real alpine competence.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 900, 1200, ARRAY['category:winter','energy:high','experience_level:intermediate','mood:adrenaline','mood:focused','mood:adventurous','mood:explorer','terrain:mountain','terrain:cliff','equipment:rental-gear','context:small-group','context:friends','requirement:guide-required','risk_level:exposed','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:weekend-trip','skills:technique','skills:navigation','skills:strength','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Bâlea Lac', 'Sibiu', 45.608, 24.615, '2025-10-22T21:59:34.463Z', '2025-11-19T14:40:28.406Z', 'fagaras-winter-mountaineering-zaiko', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.608,24.615', 'medium', 'mixed', 'any', 'moderate', 'https://mountainguide.ro', 'Weekend de Alpinism Iarna în Făgăraș', 'Traversează cu colțarii un gheață tare ca glonțul, urcă scurte porțiuni pe vârfuri, și deplasează-te legat cu frânghia pe culmi spectaculoase sub supravegherea unui ghid. Vei exersa oprirea cu gheața, conștiența cornișelor și asigurări sigure pe teren mixt. Progresul este constant și absorbant - respirație care se transformă în aburi în vântul rece, degete care învață abilități motorii fine cu mănuși, minte concentrată pe mișcarea picioarelor. Pregătește-te pentru expunere și exaltare când treci peste culmi cu văi de 2.000 m căscate dedesubt. Vei termina obosit, mai ascuțit și mândru de adevărata competență alpină.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:intermediar','stare:adrenalina','stare:focused','stare:adventurous','stare:explorator','teren:munte','teren:cliff','echipament:inchiriere_echipament','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:exposed','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:weekend-trip','abilitati:tehnica','abilitati:navigation','abilitati:strength','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (866, 'Bucegi Ski Touring (Busteni/Sinaia)', 'Ride cable cars to the plateau, then step into touring mode for long, leg-testing traverses and clean descents into secluded valleys (Cerbului, Morarului, Gaura—conditions permitting). Your guide sets safe uptracks and spacing; you manage transitions and tempo. Feel the burn on sustained skins, the snap of cold wind at cols, and the surge of speed on north-facing powder. It''s classic Carpathian touring—committing, rewarding, and full of line choice.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 360, 480, ARRAY['category:winter','energy:high','experience_level:intermediate','mood:adrenaline','mood:adventurous','mood:focused','mood:explorer','terrain:mountain','terrain:ridge','equipment:bring-own','context:small-group','context:friends','requirement:guide-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:endurance','skills:technique','skills:navigation','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Sinaia', 'PH/Brașov', 45.35, 25.545, '2025-10-22T21:59:34.464Z', '2025-11-19T14:40:28.406Z', 'sinaia-bucegi-ski-touring-mountainguide', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.350,25.545', 'medium', 'mixed', 'any', 'moderate', 'https://mountainguide.ro', 'Schi Montan în Bucegi (Bușteni/Sinaia)', 'Urcă cu telecabina pe platou, apoi intră în modul de schi montan pentru traversări lungi care îți testează picioarele și coborâri curate în văi izolate (Cerbului, Morarului, Gaura - în funcție de condiții). Ghidul tău stabilește urme și distanțe sigure; tu gestionezi tranzițiile și ritmul. Simte arsura în urcările susținute, lovitura vântului rece la trecători și accelerarea pe zăpadă pudră de pe versanții nordici. Este schi montan clasic carpatin - angajant, răsplătitor și plin de opțiuni de traseu.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:intermediar','stare:adrenalina','stare:adventurous','stare:focused','stare:explorator','teren:munte','teren:ridge','echipament:bring-own','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:endurance','abilitati:tehnica','abilitati:navigation','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (867, 'Poiana Brașov Steep Snowshoe Traverse', 'Power up short, steep forest pitches on snowshoes, learning kick turns, pole plants, and steady breathing for sustained effort. Guides thread quieter lines off the resort to sheltered clearings and ridges with wide views over Brașov. The cardio is real—calves and lungs working—then quick downhill sections test balance on packed snow. You finish flushed and happy, with basic winter travel skills and a renewed respect for cold-weather pacing.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 120, 180, ARRAY['category:winter','energy:high','experience_level:beginner','mood:adrenaline','mood:explorer','mood:focused','mood:social','terrain:forest','terrain:hill','equipment:rental-gear','context:small-group','context:friends','requirement:lesson-recommended','risk_level:moderate','weather_fit:cold_sensitive','time_of_day:daytime','travel_time_band:in-city','skills:endurance','skills:balance','cost_band:$']::text[], NULL, ARRAY[]::text[], 'Brașov', 'Brașov', 45.574, 25.557, '2025-10-22T21:59:34.465Z', '2025-11-19T14:40:28.406Z', 'poiana-brasov-snowshoe-steeps-detour', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.574,25.557', 'medium', 'mixed', 'any', 'moderate', 'https://mountainguide.ro', 'Traversarea cu Rachetele de Zăpadă pe Pârtia Abruptă din Poiana Brașov', 'Urcă scurte și abrupte porțiuni de pădure cu rachetele de zăpadă, învățând tehnici de întoarcere, folosirea bețelor de schi și respirație constantă pentru un efort susținut. Ghizii urmează trasee mai puțin circulate în afara pârtiei, spre poieni adăpostite și culmi cu priveliști largi asupra Brașovului. Antrenamentul cardiac este intens - mușchii gambelor și plămânii lucrează - iar secțiunile rapide de coborâre testează echilibrul pe zăpada bătătorită. Te vei termina înroșit și fericit, cu deprinderi de bază pentru deplasarea în condiții de iarnă și un respect reînnoit pentru ritm în mediul rece.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:incepator','stare:adrenalina','stare:explorator','stare:focused','stare:social','teren:padure','teren:hill','echipament:inchiriere_echipament','context:grup_mic','context:friends','cerinta:lectie_recomandata','nivel_risc:moderate','vreme:cold_sensitive','moment_zi:zi','timp_calatorie:in_oras','abilitati:endurance','abilitati:balance','pret:$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (868, 'Avalanche Skills & Freetouring – Bâlea', 'Train with a pro on beacon drills, probing patterns, and strategic shoveling before moving into real terrain to read wind slabs, crusts, and safe islands. You''ll practice group spacing, terrain traps recognition, and quick decision checklists, then earn a short descent to cement the lessons. It''s mentally intense and physically demanding—bursts of digging, hiking, switching modes—leaving you more confident and conservative in complex snow.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 360, 420, ARRAY['category:winter','energy:high','experience_level:beginner','mood:focused','mood:adrenaline','mood:explorer','mood:adventurous','terrain:mountain','terrain:ridge','equipment:rental-gear','context:small-group','context:friends','requirement:guide-required','risk_level:high','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:technique','skills:navigation','skills:strength','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Bâlea Lac', 'Sibiu', 45.608, 24.615, '2025-10-22T21:59:34.466Z', '2025-11-19T14:40:28.406Z', 'balea-avalanche-skills-day-detour', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.608,24.615', 'medium', 'mixed', 'any', 'moderate', 'https://mountainguide.ro', 'Abilități de Avalanșă și Freetouring – Bâlea', 'Antrenament profesionist cu exerciții de utilizare a dispozitivului de căutare, tehnici de sondare și săpat strategic, înainte de a trece în teren real pentru a analiza plăcile de vânt, crustele și zonele sigure. Veți exersa distanțarea în grup, recunoașterea capcanelor de teren și liste de verificare rapide, urmând să efectuați o scurtă coborâre pentru a fixa lecțiile. Este intens mental și fizic - cu perioade de săpat, mers și schimbări de mod - lăsându-vă mai siguri și mai precauți în condiții complexe de zăpadă.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:incepator','stare:focused','stare:adrenalina','stare:explorator','stare:adventurous','teren:munte','teren:ridge','echipament:inchiriere_echipament','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:ridicat','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:tehnica','abilitati:navigation','abilitati:strength','pret:$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (869, 'Héliski Făgăraș (Freeride Vertical)', 'Lift off from the valley and drop onto untouched faces for 500–900 m descent runs, repeating as conditions allow. Guides brief helicopter safety, group spacing, and line choice from the air; you manage legs and focus for high-speed powder. It''s maximum adrenaline: rotor wash, sharp cold at 2,400 m, and the bliss of surfing broad alpine bowls. Expect strong groups, terrain options tuned to avalanche risk, and lifelong memories.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 300, 420, ARRAY['category:winter','energy:high','experience_level:advanced','mood:adrenaline','mood:adventurous','mood:focused','mood:social','terrain:mountain','terrain:ridge','equipment:rental-gear','context:small-group','context:friends','requirement:guide-required','risk_level:exposed','weather_fit:wind_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:technique','skills:endurance','cost_band:$$$$']::text[], NULL, ARRAY[]::text[], 'Sibiu', 'Sibiu', 45.788, 24.151, '2025-10-22T21:59:34.466Z', '2025-11-19T14:40:28.406Z', 'sibiu-heliski-fagaras-heliski-romania', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=45.788,24.151', 'medium', 'mixed', 'any', 'moderate', 'https://heliskiromania.ro', 'Heli-Schi în Făgăraș (Vertical Freeride)', 'Decolează din vale și coboară pe pante neatinse pentru curse de 500–900 m, repetând în funcție de condiții. Ghizii prezintă siguranța elicopterului, distanțarea în grup și alegerea traseului din aer; tu gestionezi picioarele și concentrarea pentru zăpada pudrată de mare viteză. Adrenalină maximă: spulberarea rotorului, cold ascuțit la 2.400 m și beatitudinea de a ''naviga'' pe văile alpine largi. Grupuri puternice, opțiuni de teren adaptate riscului de avalanșă și amintiri care durează o viață.', ARRAY['categorie:iarna','energie:ridicat','nivel_experienta:avansat','stare:adrenalina','stare:adventurous','stare:focused','stare:social','teren:munte','teren:ridge','echipament:inchiriere_echipament','context:grup_mic','context:friends','cerinta:ghid_necesar','nivel_risc:exposed','vreme:wind_sensitive','moment_zi:zi','timp_calatorie:excursie_zi','abilitati:tehnica','abilitati:endurance','pret:$$$$']::text[]) ON CONFLICT DO NOTHING;
INSERT INTO activities (id, name, description, category, seasonality, indoor_outdoor, energy_level, duration_min, duration_max, tags, hero_image_url, youtube_video_ids, city, region, latitude, longitude, created_at, updated_at, slug, subtypes, image_urls, maps_url, crowd_size, crowd_type, group_suitability, price_tier, website, name_ro, description_ro, tags_ro) VALUES (870, 'Bicaz Gorges Ice Climbing', 'Swing tools on frozen curtains in Bicaz''s shadowy gullies. After a ground school on movement and screw placement, you''ll top-rope classic lines and, conditions allowing, second a short lead to feel rope systems in action. It''s physical and precise—kick, stand tall, relax grip—while the gorge walls tower above. Expect pumped forearms, focused breathing, and that clean jolt when your picks bite perfectly. By the end you''ll trust your feet and love the winter granite-and-ice theatre.', 'winter', ARRAY['winter']::text[], 'outdoor', 'high', 300, 420, ARRAY['category:winter','energy:high','experience_level:beginner','mood:adrenaline','mood:focused','mood:explorer','mood:adventurous','terrain:cliff','terrain:canyon','equipment:provided','context:small-group','context:friends','requirement:guide-required','risk_level:high','weather_fit:cold_sensitive','time_of_day:daytime','travel_time_band:day-trip','skills:technique','skills:strength','skills:balance','cost_band:$$$']::text[], NULL, ARRAY[]::text[], 'Lacu Roșu', 'Harghita/Neamț', 46.792, 25.792, '2025-10-22T21:59:34.467Z', '2025-11-19T14:40:28.406Z', 'lacu-rosu-bicaz-ice-climbing-outdoor-world', ARRAY[]::text[], ARRAY[]::text[], 'https://www.google.com/maps?q=46.792,25.792', 'medium', 'mixed', 'any', 'moderate', 'https://mountainguide.ro', 'Escaladă pe Gheață în Cheile Bicazului', 'Lovește cu uneltele în cortinele înghețate din văile umbrite ale Bicazului. După o instruire la sol privind mișcarea și amplasarea șuruburilor, vei urca pe trasee clasice cu asigurare de sus și, dacă permit condițiile, vei încerca să asigure